INSERT INTO `evoxscripts_version` VALUES ('evoXScripts (for evo-X Emu 060) ');
INSERT INTO `script_texts` VALUES ('-1000000', '<ScriptDev2 Text Entry Missing!>', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'DEFAULT_TEXT');
INSERT INTO `script_texts` VALUES ('-1000001', '%s goes into a killing frenzy!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_GENERIC_FRENZY_KILL');
INSERT INTO `script_texts` VALUES ('-1000002', '%s goes into a frenzy!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_GENERIC_FRENZY');
INSERT INTO `script_texts` VALUES ('-1000003', '%s becomes enraged!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_GENERIC_ENRAGED');
INSERT INTO `script_texts` VALUES ('-1000004', '%s goes into a berserker rage!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_GENERIC_BERSERK');
INSERT INTO `script_texts` VALUES ('-1000005', '%s goes into a frenzy!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'EMOTE_BOSS_GENERIC_FRENZY');
INSERT INTO `script_texts` VALUES ('-1000100', 'Come, little ones. Face me!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'azuregos SAY_TELEPORT');
INSERT INTO `script_texts` VALUES ('-1000101', 'Follow me, $N. I\'ll take you to the Defias hideout. But you better protect me or I am as good as dead', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_START');
INSERT INTO `script_texts` VALUES ('-1000102', 'The entrance is hidden here in Moonbrook. Keep your eyes peeled for thieves. They want me dead.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000103', 'You can go tell Stoutmantle this is where the Defias Gang is holed up, $N.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_END');
INSERT INTO `script_texts` VALUES ('-1000104', '%s coming in fast! Prepare to fight!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000105', 'Help!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000106', 'Everyone ready?', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_READY');
INSERT INTO `script_texts` VALUES ('-1000107', 'Ok, Lets move out!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_MOVE');
INSERT INTO `script_texts` VALUES ('-1000108', 'Prepare yourselves. Silverwing is just around the bend.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_PREPARE');
INSERT INTO `script_texts` VALUES ('-1000109', 'Silverwing is ours!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_WIN');
INSERT INTO `script_texts` VALUES ('-1000110', 'Go report that the outpost is taken. We will remain here.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_END');
INSERT INTO `script_texts` VALUES ('-1000111', 'Our house is this way, through the thicket.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_START');
INSERT INTO `script_texts` VALUES ('-1000112', 'Help me!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000113', 'My poor family. Everything has been destroyed.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000114', 'Father! Father! You\'re alive!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_END1');
INSERT INTO `script_texts` VALUES ('-1000115', 'You can thank $N for getting me back here safely, father.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_END2');
INSERT INTO `script_texts` VALUES ('-1000116', '%s hugs her father.', null, null, null, null, null, null, null, null, '0', '2', '7', '0', 'magwin EMOTE_HUG');
INSERT INTO `script_texts` VALUES ('-1000117', 'Thank you for agreeing to help. Now, let\'s get out of here $N.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_START');
INSERT INTO `script_texts` VALUES ('-1000118', 'Over there! They\'re following us!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1000119', 'Allow me a moment to rest. The journey taxes what little strength I have.', null, null, null, null, null, null, null, null, '0', '0', '1', '16', 'wounded elf SAY_ELF_RESTING');
INSERT INTO `script_texts` VALUES ('-1000120', 'Did you hear something?', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1000121', 'Falcon Watch, at last! Now, where\'s my... Oh no! My pack, it\'s missing! Where has -', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000122', 'You won\'t keep me from getting to Falcon Watch!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000123', 'Ready when you are, $c.', null, null, null, null, null, null, null, null, '0', '0', '0', '15', 'big will SAY_BIG_WILL_READY');
INSERT INTO `script_texts` VALUES ('-1000124', 'The Affray has begun. $n, get ready to fight!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'twiggy SAY_TWIGGY_BEGIN');
INSERT INTO `script_texts` VALUES ('-1000125', 'You! Enter the fray!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'twiggy SAY_TWIGGY_FRAY');
INSERT INTO `script_texts` VALUES ('-1000126', 'Challenger is down!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'twiggy SAY_TWIGGY_DOWN');
INSERT INTO `script_texts` VALUES ('-1000127', 'The Affray is over.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'twiggy SAY_TWIGGY_OVER');
INSERT INTO `script_texts` VALUES ('-1000128', 'We need you to send reinforcements to Manaforge Duro, Ardonis. This is not a request, it\'s an order.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_1');
INSERT INTO `script_texts` VALUES ('-1000129', 'You cannot be serious! We are severely understaffed and can barely keep this manaforge functional!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_ARCANIST_ARDONIS_1');
INSERT INTO `script_texts` VALUES ('-1000130', 'You will do as ordered. Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control. Failure to comply will not be tolerated!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_2');
INSERT INTO `script_texts` VALUES ('-1000131', 'Indeed, it is not a request.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_1');
INSERT INTO `script_texts` VALUES ('-1000132', 'My lord!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_3');
INSERT INTO `script_texts` VALUES ('-1000133', 'Duro will be reinforced! Ultris was a complete disaster. I will NOT have that mistake repeated!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2');
INSERT INTO `script_texts` VALUES ('-1000134', 'We\'ve had too many setbacks along the way: Hellfire Citadel, Fallen Sky Ridge, Firewing Point... Prince Kael\'thas will tolerate no further delays. I will tolerate nothing other than complete success!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2_1');
INSERT INTO `script_texts` VALUES ('-1000135', 'I am returning to Tempest Keep. See to it that I do not have reason to return!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2_2');
INSERT INTO `script_texts` VALUES ('-1000136', 'Yes, my lord.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge COMMANDER_DAWNFORGE_4 SAY_ARCANIST_ARDONIS_2');
INSERT INTO `script_texts` VALUES ('-1000137', 'See to it, Ardonis!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'dawnforge COMMANDER_DAWNFORGE_5');
INSERT INTO `script_texts` VALUES ('-1000138', 'Avruu\'s magic... it still controls me. You must fight me, mortal. It\'s the only way to break the spell!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'aeranas SAY_SUMMON');
INSERT INTO `script_texts` VALUES ('-1000139', 'Avruu\'s magic is broken! I\'m free once again!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'aeranas SAY_FREE');
INSERT INTO `script_texts` VALUES ('-1000140', 'Let\'s go.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_START');
INSERT INTO `script_texts` VALUES ('-1000141', '$N, let\'s use the antechamber to the right.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_PROGRESS1');
INSERT INTO `script_texts` VALUES ('-1000142', 'I can see the light at the end of the tunnel!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_PROGRESS2');
INSERT INTO `script_texts` VALUES ('-1000143', 'There\'s Farstrider Enclave now, $C. Not far to go... Look out! Troll ambush!!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_PROGRESS3');
INSERT INTO `script_texts` VALUES ('-1000144', 'Thank you for saving my life and bringing me back to safety, $N', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_END1');
INSERT INTO `script_texts` VALUES ('-1000145', 'Captain Helios, I\'ve been rescued from the Amani Catacombs. Reporting for duty, sir!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_END2');
INSERT INTO `script_texts` VALUES ('-1000146', 'Liatha, get someone to look at those injuries. Thank you for bringing her back safely.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lilatha CAPTAIN_ANSWER');
INSERT INTO `script_texts` VALUES ('-1000147', 'I remember well the sting of defeat at the conclusion of the Third War. I have waited far too long for my revenge. Now the shadow of the Legion falls over this world. It is only a matter of time until all of your failed creation... is undone.', null, null, null, null, null, null, null, null, '11332', '1', '0', '0', 'kazzak SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1000148', 'The Legion will conquer all!', null, null, null, null, null, null, null, null, '11333', '1', '0', '0', 'kazzak SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000149', 'All mortals will perish!', null, null, null, null, null, null, null, null, '11334', '1', '0', '0', 'kazzak SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000150', 'All life must be eradicated!', null, null, null, null, null, null, null, null, '11335', '1', '0', '0', 'kazzak SAY_SURPREME1');
INSERT INTO `script_texts` VALUES ('-1000151', 'I\'ll rip the flesh from your bones!', null, null, null, null, null, null, null, null, '11336', '1', '0', '0', 'kazzak SAY_SURPREME2');
INSERT INTO `script_texts` VALUES ('-1000152', 'Kirel Narak!', null, null, null, null, null, null, null, null, '11337', '1', '0', '0', 'kazzak SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1000153', 'Contemptible wretch!', null, null, null, null, null, null, null, null, '11338', '1', '0', '0', 'kazzak SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1000154', 'The universe will be remade.', null, null, null, null, null, null, null, null, '11339', '1', '0', '0', 'kazzak SAY_KILL3');
INSERT INTO `script_texts` VALUES ('-1000155', 'The Legion... will never... fall.', null, null, null, null, null, null, null, null, '11340', '1', '0', '0', 'kazzak SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1000156', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1000157', 'Invaders, you dangle upon the precipice of oblivion! The Burning Legion comes and with it comes your end.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_RAND1');
INSERT INTO `script_texts` VALUES ('-1000158', 'Impudent whelps, you only delay the inevitable. Where one has fallen, ten shall rise. Such is the will of Kazzak...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_RAND2');
INSERT INTO `script_texts` VALUES ('-1000159', 'Do not proceed. You will be eliminated!', null, null, null, null, null, null, null, null, '11344', '1', '0', '0', 'doomwalker SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000160', 'Tectonic disruption commencing.', null, null, null, null, null, null, null, null, '11345', '1', '0', '0', 'doomwalker SAY_EARTHQUAKE_1');
INSERT INTO `script_texts` VALUES ('-1000161', 'Magnitude set. Release.', null, null, null, null, null, null, null, null, '11346', '1', '0', '0', 'doomwalker SAY_EARTHQUAKE_2');
INSERT INTO `script_texts` VALUES ('-1000162', 'Trajectory locked.', null, null, null, null, null, null, null, null, '11347', '1', '0', '0', 'doomwalker SAY_OVERRUN_1');
INSERT INTO `script_texts` VALUES ('-1000163', 'Engage maximum speed.', null, null, null, null, null, null, null, null, '11348', '1', '0', '0', 'doomwalker SAY_OVERRUN_2');
INSERT INTO `script_texts` VALUES ('-1000164', 'Threat level zero.', null, null, null, null, null, null, null, null, '11349', '1', '0', '0', 'doomwalker SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1000165', 'Directive accomplished.', null, null, null, null, null, null, null, null, '11350', '1', '0', '0', 'doomwalker SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1000166', 'Target exterminated.', null, null, null, null, null, null, null, null, '11351', '1', '0', '0', 'doomwalker SAY_SLAY_3');
INSERT INTO `script_texts` VALUES ('-1000167', 'System failure in five, f-o-u-r...', null, null, null, null, null, null, null, null, '11352', '1', '0', '0', 'doomwalker SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1000168', 'Who dares awaken Aquementas?', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'aquementas AGGRO_YELL_AQUE');
INSERT INTO `script_texts` VALUES ('-1000169', 'Muahahahaha! You fool! You\'ve released me from my banishment in the interstices between space and time!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_1');
INSERT INTO `script_texts` VALUES ('-1000170', 'All of Draenor shall quick beneath my feet! I will destroy this world and reshape it in my image!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_2');
INSERT INTO `script_texts` VALUES ('-1000171', 'Where shall I begin? I cannot bother myself with a worm such as yourself. There is a world to be conquered!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_3');
INSERT INTO `script_texts` VALUES ('-1000172', 'No doubt the fools that banished me are long dead. I shall take wing survey my demense. Pray to whatever gods you hold dear that we do not meet again.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_4');
INSERT INTO `script_texts` VALUES ('-1000173', 'NOOOOooooooo!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_INTERRUPT');
INSERT INTO `script_texts` VALUES ('-1000174', 'Good $N, you are under the spell\'s influence. I must analyze it quickly, then we can talk.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'daranelle SAY_SPELL_INFLUENCE');
INSERT INTO `script_texts` VALUES ('-1000175', 'Thank you, mortal.', null, null, null, null, null, null, null, null, '0', '0', '11', '0', ' SAY_JUST_EATEN');
INSERT INTO `script_texts` VALUES ('-1000176', 'The last thing I remember is the ship falling and us getting into the pods. I\'ll go see how I can help. Thank you!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HEAL1');
INSERT INTO `script_texts` VALUES ('-1000177', '$C, Where am I? Who are you? Oh no! What happened to the ship?', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HEAL2');
INSERT INTO `script_texts` VALUES ('-1000178', '$C You saved me! I owe you a debt that I can never repay. I\'ll go see if I can help the others.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HEAL3');
INSERT INTO `script_texts` VALUES ('-1000179', 'Ugh... what is this place? Is that all that\'s left of the ship over there?', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HEAL4');
INSERT INTO `script_texts` VALUES ('-1000180', 'Oh, the pain...', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HELP1');
INSERT INTO `script_texts` VALUES ('-1000181', 'Everything hurts, Please make it stop...', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HELP2');
INSERT INTO `script_texts` VALUES ('-1000182', 'Ughhh... I hurt. Can you help me?', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HELP3');
INSERT INTO `script_texts` VALUES ('-1000183', 'I don\'t know if I can make it, please help me...', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HELP4');
INSERT INTO `script_texts` VALUES ('-1000184', 'Yes Master, all goes along as planned.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'engineer_spark SAY_TEXT');
INSERT INTO `script_texts` VALUES ('-1000185', '%s puts the shell to his ear.', null, null, null, null, null, null, null, null, '0', '2', '7', '0', 'engineer_spark EMOTE_SHELL');
INSERT INTO `script_texts` VALUES ('-1000186', 'Now I cut you!', null, null, null, null, null, null, null, null, '0', '1', '7', '0', 'engineer_spark SAY_ATTACK');
INSERT INTO `script_texts` VALUES ('-1000187', 'Thank you, dear $C, you just saved my life.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'faulk SAY_HEAL');
INSERT INTO `script_texts` VALUES ('-1000188', 'Deployment sucessful. Trespassers will be neutralized.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'converted_sentry SAY_CONVERTED_1');
INSERT INTO `script_texts` VALUES ('-1000189', 'Objective acquired. Initiating security routines.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'converted_sentry SAY_CONVERTED_2');
INSERT INTO `script_texts` VALUES ('-1000190', 'In Nagrand, food hunt ogre!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', ' SAY_LUMP_0');
INSERT INTO `script_texts` VALUES ('-1000191', 'You taste good with maybe a little salt and pepper.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', ' SAY_LUMP_1');
INSERT INTO `script_texts` VALUES ('-1000192', 'OK, OK! Lump give up!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', ' SAY_LUMP_DEFEAT');
INSERT INTO `script_texts` VALUES ('-1000193', 'Thank you, dear $C, you just saved my life.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'stillblade SAY_HEAL');
INSERT INTO `script_texts` VALUES ('-1000194', 'I give up! Please don\'t kill me!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'unkor SAY_SUBMIT');
INSERT INTO `script_texts` VALUES ('-1000195', 'I choose the third option: KILLING YOU!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'floon SAY_FLOON_ATTACK');
INSERT INTO `script_texts` VALUES ('-1000196', 'Belore...', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lady_sylvanas SAY_LAMENT_END');
INSERT INTO `script_texts` VALUES ('-1000197', '%s kneels down and pick up the amulet.', null, null, null, null, null, null, null, null, '0', '2', '1', '0', 'lady_sylvanas EMOTE_LAMENT_END');
INSERT INTO `script_texts` VALUES ('-1000198', 'Taste blade, mongrel!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000199', 'Please tell me that you didn\'t just do what I think you just did. Please tell me that I\'m not going to have to hurt you...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000200', 'As if we don\'t have enough problems, you go and create more!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1000201', 'I\'m saved! Thank you, doctor!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'injured_patient SAY_DOC1');
INSERT INTO `script_texts` VALUES ('-1000202', 'HOORAY! I AM SAVED!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'injured_patient SAY_DOC2');
INSERT INTO `script_texts` VALUES ('-1000203', 'Sweet, sweet embrace... take me...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'injured_patient SAY_DOC3');
INSERT INTO `script_texts` VALUES ('-1000204', '%s looks up at you quizzically. Maybe you should inspect it?', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_A_HELLO');
INSERT INTO `script_texts` VALUES ('-1000205', '%s looks at you unexpectadly.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_H_HELLO');
INSERT INTO `script_texts` VALUES ('-1000206', '%s starts pecking at the feed.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_CLUCK_TEXT2');
INSERT INTO `script_texts` VALUES ('-1000207', 'You have my blessing', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ashyen_and_keleth SAY_REWARD_BLESS');
INSERT INTO `script_texts` VALUES ('-1000208', 'Frenzyheart kill you if you come back. You no welcome here no more!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'vekjik SAY_TEXTID_VEKJIK1');
INSERT INTO `script_texts` VALUES ('-1000209', 'Very well. Let\'s see what you have to show me, $N.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'anvilward SAY_ANVIL1');
INSERT INTO `script_texts` VALUES ('-1000210', 'What manner of trick is this, $R? If you seek to ambush me, I warn you I will not go down quietly!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'anvilward SAY_ANVIL2');
INSERT INTO `script_texts` VALUES ('-1000211', 'Warning! %s emergency shutdown process initiated by $N. Shutdown will complete in two minutes.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_START');
INSERT INTO `script_texts` VALUES ('-1000212', 'Emergency shutdown will complete in one minute.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_60');
INSERT INTO `script_texts` VALUES ('-1000213', 'Emergency shutdown will complete in thirty seconds.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_30');
INSERT INTO `script_texts` VALUES ('-1000214', 'Emergency shutdown will complete in ten seconds.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_10');
INSERT INTO `script_texts` VALUES ('-1000215', 'Emergency shutdown complete.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000216', 'Emergency shutdown aborted.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_ABORT');
INSERT INTO `script_texts` VALUES ('-1000217', 'Greetings, $N. I will guide you through the cavern. Please try and keep up.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_1');
INSERT INTO `script_texts` VALUES ('-1000218', 'We do not know if the Caverns of Time have always been accessible to mortals. Truly, it is impossible to tell as the Timeless One is in perpetual motion, changing our timeways as he sees fit. What you see now may very well not exist tomorrow. You may wake up and have no memory of this place.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_2');
INSERT INTO `script_texts` VALUES ('-1000219', 'It is strange, I know... Most mortals cannot actually comprehend what they see here, as often, what they see is not anchored within their own perception of reality.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_3');
INSERT INTO `script_texts` VALUES ('-1000220', 'Follow me, please.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_4');
INSERT INTO `script_texts` VALUES ('-1000221', 'There are only two truths to be found here: First, that time is chaotic, always in flux, and completely malleable and second, perception does not dictate reality.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_5');
INSERT INTO `script_texts` VALUES ('-1000222', 'As custodians of time, we watch over and care for Nozdormu\'s realm. The master is away at the moment, which means that attempts are being made to dramatically alter time. The master never meddles in the affairs of mortals but instead corrects the alterations made to time by others. He is reactionary in this regard.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_6');
INSERT INTO `script_texts` VALUES ('-1000223', 'For normal maintenance of time, the Keepers of Time are sufficient caretakers. We are able to deal with most ordinary disturbances. I speak of little things, such as rogue mages changing something in the past to elevate their status or wealth in the present.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_7');
INSERT INTO `script_texts` VALUES ('-1000224', 'These tunnels that you see are called timeways. They are infinite in number. The ones that currently exist in your reality are what the master has deemed as \'trouble spots.\' These trouble spots may differ completely in theme but they always share a cause. That is, their existence is a result of the same temporal disturbance. Remember that should you venture inside one...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_8');
INSERT INTO `script_texts` VALUES ('-1000225', 'This timeway is in great disarray! We have agents inside right now attempting to restore order. What information I have indicates that Thrall\'s freedom is in jeopardy. A malevolent organization known as the Infinite Dragonflight is trying to prevent his escape. I fear without outside assistance, all will be lost.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_9');
INSERT INTO `script_texts` VALUES ('-1000226', 'We have very little information on this timeway. Sa\'at has been dispatched and is currently inside. The data we have gathered from his correspondence is that the Infinite Dragonflight are once again attempting to alter time. Could it be that the opening of the Dark Portal is being targeted for sabotage? Let us hope not...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_10');
INSERT INTO `script_texts` VALUES ('-1000227', 'This timeway is currently collapsing. What that may hold for the past, present and future is currently unknown...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_11');
INSERT INTO `script_texts` VALUES ('-1000228', 'The timeways are currently ranked in order from least catastrophic to most catastrophic. Note that they are all classified as catastrophic, meaning that any single one of these timeways collapsing would mean that your world would end. We only classify them in such a way so that the heroes and adventurers that are sent here know which timeway best suits their abilities.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_12');
INSERT INTO `script_texts` VALUES ('-1000229', 'All we know of this timeway is that it leads to Mount Hyjal. The Infinite Dragonflight have gone to great lengths to prevent our involvement. We know next to nothing, mortal. Soridormi is currently attempting to break through the timeway\'s defenses but has thus far been unsuccessful. You might be our only hope of breaking through and resolving the conflict.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_13');
INSERT INTO `script_texts` VALUES ('-1000230', 'Our time is at an end $N. I would wish you luck, if such a thing existed.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'WHISPER_CUSTODIAN_14');
INSERT INTO `script_texts` VALUES ('-1000231', 'Ah, $GPriest:Priestess; you came along just in time. I appreciate it.', null, null, null, null, null, null, null, null, '0', '0', '0', '20', 'garments SAY_COMMON_HEALED');
INSERT INTO `script_texts` VALUES ('-1000232', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those gnolls with your power to back me!', null, null, null, null, null, null, null, null, '0', '0', '1', '4', 'garments SAY_DG_KEL_THANKS');
INSERT INTO `script_texts` VALUES ('-1000233', 'Farewell to you, and may shadow always protect you!', null, null, null, null, null, null, null, null, '0', '0', '1', '3', 'garments SAY_DG_KEL_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000234', 'Follow me, stranger. This won\'t take long.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_SERV_0');
INSERT INTO `script_texts` VALUES ('-1000235', 'Shattrath was once the draenei capital of this world. Its name means \"dwelling of light.\"', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_1');
INSERT INTO `script_texts` VALUES ('-1000236', 'When the Burning Legion turned the orcs against the draenei, the fiercest battle was fought here. The draenei fought tooth and nail, but in the end the city fell.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_2');
INSERT INTO `script_texts` VALUES ('-1000237', 'The city was left in ruins and darkness... until the Sha\'tar arrived.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_3');
INSERT INTO `script_texts` VALUES ('-1000238', 'Let us go into the Lower City. I will warn you that as one of the only safe havens in Outland, Shattrath has attracted droves of refugees from all wars, current and past.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_4');
INSERT INTO `script_texts` VALUES ('-1000239', 'The Sha\'tar, or \"born from light\" are the naaru that came to Outland to fight the demons of the Burning Legion.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_5');
INSERT INTO `script_texts` VALUES ('-1000240', 'They were drawn to the ruins of Shattrath City where a small remnant of the draenei priesthood conducted its rites inside a ruined temple on this very spot.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_6');
INSERT INTO `script_texts` VALUES ('-1000241', 'The priesthood, known as the Aldor, quickly regained its strength as word spread that the naaru had returned and reconstruction soon began. The ruined temple is now used as an infirmary for injured refugees.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_7');
INSERT INTO `script_texts` VALUES ('-1000242', 'It wouldn\'t be long, however, before the city came under attack once again. This time, the attack came from Illidan\'s armies. A large regiment of blood elves had been sent by Illidan\'s ally, Kael\'thas Sunstrider, to lay waste to the city.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_8');
INSERT INTO `script_texts` VALUES ('-1000243', 'As the regiment of blood elves crossed this very bridge, the Aldor\'s exarchs and vindicators lined up to defend the Terrace of Light. But then the unexpected happened.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_9');
INSERT INTO `script_texts` VALUES ('-1000244', 'The blood elves laid down their weapons in front of the city\'s defenders; their leader, a blood elf elder known as Voren\'thal, stormed into the Terrace of Light and demanded to speak to A\'dal.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_10');
INSERT INTO `script_texts` VALUES ('-1000245', 'As the naaru approached him, Voren\'thal kneeled before him and uttered the following words: \"I\'ve seen you in a vision, naaru. My race\'s only hope for survival lies with you. My followers and I are here to serve you.\"', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_11');
INSERT INTO `script_texts` VALUES ('-1000246', 'The defection of Voren\'thal and his followers was the largest loss ever incurred by Kael\'s forces. And these weren\'t just any blood elves. Many of the best and brightest amongst Kael\'s scholars and magisters had been swayed by Voren\'thal\'s influence.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_12');
INSERT INTO `script_texts` VALUES ('-1000247', 'The naaru accepted the defectors, who would become known as the Scryers; their dwelling lies in the platform above. Only those initiated with the Scryers are allowed there.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_13');
INSERT INTO `script_texts` VALUES ('-1000248', 'The Aldor are followers of the Light and forgiveness and redemption are values they understand. However, they found hard to forget the deeds of the blood elves while under Kael\'s command.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_14');
INSERT INTO `script_texts` VALUES ('-1000249', 'Many of the priesthood had been slain by the same magisters who now vowed to serve the naaru. They were not happy to share the city with their former enemies.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_15');
INSERT INTO `script_texts` VALUES ('-1000250', 'The Aldor\'s most holy temple and its surrounding dwellings lie on the terrace above. As a holy site, only the initiated are welcome inside.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_16');
INSERT INTO `script_texts` VALUES ('-1000251', 'The attacks against Shattrath continued, but the city did not fall, as you can see. On the contrary, the naaru known as Xi\'ri led a successful incursion into Shadowmoon Valley - Illidan\'s doorstep.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_17');
INSERT INTO `script_texts` VALUES ('-1000252', 'There he continues to wage war on Illidan with the assistance of the Aldor and the Scryers. The two factions have not given up on their old feuds, though.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_18');
INSERT INTO `script_texts` VALUES ('-1000253', 'Such is their animosity that they vie for the honor of being sent to assist the naaru there. Each day, that decision is made here by A\'dal. The armies gather here to receive A\'dal\'s blessing before heading to Shadowmoon.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_19');
INSERT INTO `script_texts` VALUES ('-1000254', 'Khadgar should be ready to see you again. Just remember that to serve the Sha\'tar you will most likely have to ally with the Aldor or the Scryers. And seeking the favor of one group will cause the others\' dislike.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_20');
INSERT INTO `script_texts` VALUES ('-1000255', 'Good luck stranger, and welcome to Shattrath City.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_21');
INSERT INTO `script_texts` VALUES ('-1000256', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those murlocs with the Light on my side!', null, null, null, null, null, null, null, null, '0', '0', '7', '4', 'garments SAY_ROBERTS_THANKS');
INSERT INTO `script_texts` VALUES ('-1000257', 'Farewell to you, and may the Light be with you always.', null, null, null, null, null, null, null, null, '0', '0', '7', '3', 'garments SAY_ROBERTS_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000258', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those humans with your power to back me!', null, null, null, null, null, null, null, null, '0', '0', '1', '4', 'garments SAY_KORJA_THANKS');
INSERT INTO `script_texts` VALUES ('-1000259', 'Farewell to you, and may our ancestors be with you always!', null, null, null, null, null, null, null, null, '0', '0', '1', '3', 'garments SAY_KORJA_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000260', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those wendigo with the Light on my side!', null, null, null, null, null, null, null, null, '0', '0', '7', '4', 'garments SAY_DOLF_THANKS');
INSERT INTO `script_texts` VALUES ('-1000261', 'Farewell to you, and may the Light be with you always.', null, null, null, null, null, null, null, null, '0', '0', '7', '3', 'garments SAY_DOLF_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000262', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those corrupt timberlings with Elune\'s power behind me!', null, null, null, null, null, null, null, null, '0', '0', '2', '4', 'garments SAY_SHAYA_THANKS');
INSERT INTO `script_texts` VALUES ('-1000263', 'Farewell to you, and may Elune be with you always.', null, null, null, null, null, null, null, null, '0', '0', '2', '3', 'garments SAY_SHAYA_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000264', 'Ok, $N. Follow me to the cave where I\'ll attempt to harness the power of the rune stone into these goggles.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_1');
INSERT INTO `script_texts` VALUES ('-1000265', 'I discovered this cave on our first day here. I believe the energy in the stone can be used to our advantage.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_2');
INSERT INTO `script_texts` VALUES ('-1000266', 'I\'ll begin drawing energy from the stone. Your job, $N, is to defend me. This place is cursed... trust me.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_3');
INSERT INTO `script_texts` VALUES ('-1000267', '%s begins tinkering with the goggles before the stone.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'phizzlethorpe EMOTE_PROGRESS_4');
INSERT INTO `script_texts` VALUES ('-1000268', 'Help!!! Get these things off me so I can get my work done!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'phizzlethorpe SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000269', 'Almost done! Just a little longer!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_5');
INSERT INTO `script_texts` VALUES ('-1000270', 'I\'ve done it! I have harnessed the power of the stone into the goggles! Let\'s get out of here!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_6');
INSERT INTO `script_texts` VALUES ('-1000271', 'Phew! Glad to be back from that creepy cave.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_7');
INSERT INTO `script_texts` VALUES ('-1000272', '%s hands one glowing goggles over to Doctor Draxlegauge.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'phizzlethorpe EMOTE_PROGRESS_8');
INSERT INTO `script_texts` VALUES ('-1000273', 'Doctor Draxlegauge will give you further instructions, $N. Many thanks for your help!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_9');
INSERT INTO `script_texts` VALUES ('-1000274', 'Time to teach you a lesson in manners, little $Gboy:girl;!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_START');
INSERT INTO `script_texts` VALUES ('-1000275', 'Now I\'m gonna give you to the count of \'3\' to get out of here before I sick the dogs on you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_COUNT');
INSERT INTO `script_texts` VALUES ('-1000276', '1...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_COUNT_1');
INSERT INTO `script_texts` VALUES ('-1000277', '2...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_COUNT_2');
INSERT INTO `script_texts` VALUES ('-1000278', 'Time to meet your maker!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_ATTACK_5');
INSERT INTO `script_texts` VALUES ('-1000279', 'Alright, we give up! Don\'t hurt us!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_GIVEUP');
INSERT INTO `script_texts` VALUES ('-1000280', 'A shadowy, sinister presence has invaded the Emerald Dream. Its power is poised to spill over into our world, $N. We must oppose it! That\'s why I cannot accompany you in person.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'clintar SAY_START');
INSERT INTO `script_texts` VALUES ('-1000281', 'The Emerald Dream will never be yours!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000282', 'Begone from this place!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000283', 'That\'s the first relic, but there are still two more. Follow me, $N.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_RELIC1');
INSERT INTO `script_texts` VALUES ('-1000284', 'I\'ve recovered the second relic. Take a moment to rest, and then we\'ll continue to the last reliquary.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_RELIC2');
INSERT INTO `script_texts` VALUES ('-1000285', 'We have all three of the relics, but my energy is rapidly fading. We must make our way back to Dreamwarden Lurosa! He will let you know what to do next.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_RELIC3');
INSERT INTO `script_texts` VALUES ('-1000286', 'Lurosa, I am entrusting the Relics of Aviana to $N, who will take them to Morthis Whisperwing. I must return completely to the Emerald Dream now. Do not let $N fail!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'clintar SAY_END');
INSERT INTO `script_texts` VALUES ('-1000287', 'Emergency power activated! Initializing ambulanory motor! CLUCK!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_START');
INSERT INTO `script_texts` VALUES ('-1000288', 'Physical threat detected! Evasive action! CLUCK!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000289', 'Thread analyzed! Activating combat plan beta! CLUCK!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000290', 'CLUCK! Sensors detect spatial anomaly - danger imminent! CLUCK!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_AMBUSH');
INSERT INTO `script_texts` VALUES ('-1000291', 'No one challanges the Wastewander nomads - not even robotic chickens! ATTACK!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX17_AMBUSH_REPLY');
INSERT INTO `script_texts` VALUES ('-1000292', 'Cloaking systems online! CLUCK! Engaging cloak for transport to Booty Bay!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_END');
INSERT INTO `script_texts` VALUES ('-1000293', 'To the house! Stay close to me, no matter what! I have my gun and ammo there!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_START');
INSERT INTO `script_texts` VALUES ('-1000294', 'We showed that one!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_1');
INSERT INTO `script_texts` VALUES ('-1000295', 'One more down!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_2');
INSERT INTO `script_texts` VALUES ('-1000296', 'We\'ve done it! We won!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_3');
INSERT INTO `script_texts` VALUES ('-1000297', 'Meet me down by the orchard--I just need to put my gun away.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_PROLOGUE');
INSERT INTO `script_texts` VALUES ('-1000298', 'Alright, alright I think I can figure out how to operate this thing...', null, null, null, null, null, null, null, null, '0', '0', '0', '393', 'wizzlecrank SAY_START');
INSERT INTO `script_texts` VALUES ('-1000299', 'Arrrgh! This isn\'t right!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wizzlecrank SAY_STARTUP1');
INSERT INTO `script_texts` VALUES ('-1000300', 'Okay, I think I\'ve got it, now. Follow me, $n!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'wizzlecrank SAY_STARTUP2');
INSERT INTO `script_texts` VALUES ('-1000301', 'There\'s the stolen shredder! Stop it or Lugwizzle will have our hides!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'wizzlecrank SAY_MERCENARY');
INSERT INTO `script_texts` VALUES ('-1000302', 'Looks like we\'re out of woods, eh? Wonder what this does...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wizzlecrank SAY_PROGRESS_1');
INSERT INTO `script_texts` VALUES ('-1000303', 'Come on, don\'t break down on me now!', null, null, null, null, null, null, null, null, '0', '0', '0', '393', 'wizzlecrank SAY_PROGRESS_2');
INSERT INTO `script_texts` VALUES ('-1000304', 'That was a close one! Well, let\'s get going, it\'s still a ways to Ratchet!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wizzlecrank SAY_PROGRESS_3');
INSERT INTO `script_texts` VALUES ('-1000305', 'Hmm... I don\'t think this blinking red light is a good thing...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wizzlecrank SAY_END');
INSERT INTO `script_texts` VALUES ('-1000306', 'Let\'s get to the others, and keep an eye open for those wolves cutside...', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_START_1');
INSERT INTO `script_texts` VALUES ('-1000307', 'Be careful, $N. Those wolves like to hide among the trees.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_START_2');
INSERT INTO `script_texts` VALUES ('-1000308', 'A $C attacks!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000309', 'Beware! I am under attack!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000310', 'Oh no! A $C is upon us!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1000311', 'We\'re almost there!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000312', 'We made it! Thanks, $N. I couldn\'t have gotten without you.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_END');
INSERT INTO `script_texts` VALUES ('-1000313', 'It\'s good to see you again, Erland. What is your report?', null, null, null, null, null, null, null, null, '0', '0', '33', '1', 'erland SAY_RANE');
INSERT INTO `script_texts` VALUES ('-1000314', 'Masses of wolves are to the east, and whoever lived at Malden\'s Orchard is gone.', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'erland SAY_RANE_REPLY');
INSERT INTO `script_texts` VALUES ('-1000315', 'If I am excused, then I\'d like to check on Quinn...', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'erland SAY_CHECK_NEXT');
INSERT INTO `script_texts` VALUES ('-1000316', 'Hello, Quinn. How are you faring?', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'erland SAY_QUINN');
INSERT INTO `script_texts` VALUES ('-1000317', 'I\'ve been better. Ivar the Foul got the better of me...', null, null, null, null, null, null, null, null, '0', '0', '33', '1', 'erland SAY_QUINN_REPLY');
INSERT INTO `script_texts` VALUES ('-1000318', 'Try to take better care of yourself, Quinn. You were lucky this time.', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'erland SAY_BYE');
INSERT INTO `script_texts` VALUES ('-1000319', 'Let the trial begin, Bloodwrath, attack!', null, null, null, null, null, null, null, null, '0', '1', '1', '0', 'kelerun SayId1');
INSERT INTO `script_texts` VALUES ('-1000320', 'Champion Lightrend, make me proud!', null, null, null, null, null, null, null, null, '0', '1', '1', '0', 'kelerun SayId2');
INSERT INTO `script_texts` VALUES ('-1000321', 'Show this upstart how a real Blood Knight fights, Swiftblade!', null, null, null, null, null, null, null, null, '0', '1', '1', '0', 'kelerun SayId3');
INSERT INTO `script_texts` VALUES ('-1000322', 'Show $n the meaning of pain, Sunstriker!', null, null, null, null, null, null, null, null, '0', '1', '1', '0', 'kelerun SayId4');
INSERT INTO `script_texts` VALUES ('-1000323', 'Mist! I feared I would never see you again! Yes, I am well, do not worry for me. You must rest and recover your health.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'mist SAY_AT_HOME');
INSERT INTO `script_texts` VALUES ('-1000324', '%s growls in acknowledgement before straightening and making her way off into the forest.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'mist EMOTE_AT_HOME');
INSERT INTO `script_texts` VALUES ('-1000325', '\"Threshwackonator First Mate unit prepared to follow\"', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'threshwackonator EMOTE_START');
INSERT INTO `script_texts` VALUES ('-1000326', 'YARRR! Swabie, what have ye done?! He\'s gone mad! Baton him down the hatches! Hoist the mast! ARRRR! Every man for hi\'self!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'threshwackonator SAY_AT_CLOSE');
INSERT INTO `script_texts` VALUES ('-1000327', 'Ok, $n, let\'s go find where I left that mysterious fossil. Follow me!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_START');
INSERT INTO `script_texts` VALUES ('-1000328', 'Now where did I put that mysterious fossil? Ah, maybe up there...', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_RAMP1_1');
INSERT INTO `script_texts` VALUES ('-1000329', 'Hrm, nothing up here.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_RAMP1_2');
INSERT INTO `script_texts` VALUES ('-1000330', 'No mysterious fossil here... Ah, but my copy of Green Hills of Stranglethorn. What a good book!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_BOOK');
INSERT INTO `script_texts` VALUES ('-1000331', 'I bet you I left it in the tent!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_TENT1_1');
INSERT INTO `script_texts` VALUES ('-1000332', 'Oh wait, that\'s Hollee\'s tent... and it\'s empty.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_TENT1_2');
INSERT INTO `script_texts` VALUES ('-1000333', 'Interesting... I hadn\'t noticed this earlier...', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_MOSS');
INSERT INTO `script_texts` VALUES ('-1000334', '%s inspects the ancient, mossy stone.', null, null, null, null, null, null, null, null, '0', '2', '7', '0', 'remtravel EMOTE_REM_MOSS');
INSERT INTO `script_texts` VALUES ('-1000335', 'Oh wait! I\'m supposed to be looking for that mysterious fossil!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_MOSS_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000336', 'Nope. didn\'t leave the fossil back here!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000337', 'Ah. I remember now! I gave the mysterious fossil to Hollee! Check with her.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_REMEMBER');
INSERT INTO `script_texts` VALUES ('-1000338', '%s goes back to work, oblivious to everything around him.', null, null, null, null, null, null, null, null, '0', '2', '7', '0', 'remtravel EMOTE_REM_END');
INSERT INTO `script_texts` VALUES ('-1000339', 'Something tells me this $r wants the mysterious fossil too. Help!', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000340', '%s howls in delight at the sight of his lunch!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kyle EMOTE_SEE_LUNCH');
INSERT INTO `script_texts` VALUES ('-1000341', '%s eats his lunch.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kyle EMOTE_EAT_LUNCH');
INSERT INTO `script_texts` VALUES ('-1000342', '%s thanks you with a special dance.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kyle EMOTE_DANCE');
INSERT INTO `script_texts` VALUES ('-1000343', 'Is the way clear? Let\'s get out while we can, $N.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_START');
INSERT INTO `script_texts` VALUES ('-1000344', 'Looks like we won\'t get away so easy. Get ready!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_AMBUSH1');
INSERT INTO `script_texts` VALUES ('-1000345', 'Let\'s keep moving. We\'re not safe here!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000346', 'Look out, $N! Enemies ahead!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_AMBUSH2');
INSERT INTO `script_texts` VALUES ('-1000347', 'We\'re almost to the refuge! Let\'s go.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_END');
INSERT INTO `script_texts` VALUES ('-1000348', 'Ah...the wondrous sound of kodos. I love the way they make the ground shake... inspect the beast for me.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_1');
INSERT INTO `script_texts` VALUES ('-1000349', 'Hey, look out with that kodo! You had better inspect that beast before i give you credit!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_2');
INSERT INTO `script_texts` VALUES ('-1000350', 'That kodo sure is a beauty. Wait a minute, where are my bifocals? Perhaps you should inspect the beast for me.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_3');
INSERT INTO `script_texts` VALUES ('-1000351', 'You, there! Hand over that moonstone and nobody gets hurt!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'sprysprocket SAY_START');
INSERT INTO `script_texts` VALUES ('-1000352', '%s takes the Southfury moonstone and escapes into the river. Follow her!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'sprysprocket EMOTE_START');
INSERT INTO `script_texts` VALUES ('-1000353', 'Just chill!', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'sprysprocket SAY_WHISPER_CHILL');
INSERT INTO `script_texts` VALUES ('-1000354', 'Stupid grenade picked a fine time to backfire! So much for high quality goblin engineering!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'sprysprocket SAY_GRENADE_FAIL');
INSERT INTO `script_texts` VALUES ('-1000355', 'All right, you win! I surrender! Just don\'t hurt me!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'sprysprocket SAY_END');
INSERT INTO `script_texts` VALUES ('-1000356', 'Okay, okay... gimme a minute to rest now. You gone and beat me up good.', null, null, null, null, null, null, null, null, '0', '0', '1', '14', 'calvin SAY_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000357', 'Let\'s go before they find out I\'m free!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'KAYA_SAY_START');
INSERT INTO `script_texts` VALUES ('-1000358', 'Look out! We\'re under attack!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'KAYA_AMBUSH');
INSERT INTO `script_texts` VALUES ('-1000359', 'Thank you for helping me. I know my way back from here.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'KAYA_END');
INSERT INTO `script_texts` VALUES ('-1000360', 'The strands of LIFE have been severed! The Dreamers must be avenged!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', ' ysondre SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000361', 'Come forth, ye Dreamers ', null, null, null, null, null, null, null, null, '0', '1', '0', '0', ' ysondre SAY_SUMMONDRUIDS');
INSERT INTO `script_texts` VALUES ('-1000362', 'Let\'s go $N. I am ready to reach Whitereach Post.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'paoka SAY_START');
INSERT INTO `script_texts` VALUES ('-1000363', 'Now this looks familiar. If we keep heading east, I think we can... Ahh, Wyvern on the attack!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'paoka SAY_WYVERN');
INSERT INTO `script_texts` VALUES ('-1000364', 'Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'paoka SAY_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000365', 'Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_START');
INSERT INTO `script_texts` VALUES ('-1000366', 'Look out, the Grimtotem are upon us!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_LOOK_OUT');
INSERT INTO `script_texts` VALUES ('-1000367', 'Here they come.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_HERE_COME');
INSERT INTO `script_texts` VALUES ('-1000368', 'More Grimtotems are coming this way!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_MORE');
INSERT INTO `script_texts` VALUES ('-1000369', 'Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_END');
INSERT INTO `script_texts` VALUES ('-1000370', 'Stay close, $n. I\'ll need all the help I can get to break out of here. Let\'s go!', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'gilthares SAY_GIL_START');
INSERT INTO `script_texts` VALUES ('-1000371', 'At last! Free from Northwatch Hold! I need a moment to catch my breath...', null, null, null, null, null, null, null, null, '0', '0', '1', '5', 'gilthares SAY_GIL_AT_LAST');
INSERT INTO `script_texts` VALUES ('-1000372', 'Now i feel better. Let\'s get back to Ratchet. Come on, $n.', null, null, null, null, null, null, null, null, '0', '0', '1', '23', 'gilthares SAY_GIL_PROCEED');
INSERT INTO `script_texts` VALUES ('-1000373', 'Looks like the Southsea Freeboters are heavily entrenched on the coast. This could get rough.', null, null, null, null, null, null, null, null, '0', '0', '1', '25', 'gilthares SAY_GIL_FREEBOOTERS');
INSERT INTO `script_texts` VALUES ('-1000374', 'Help! $C attacking!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000375', '$C heading this way fast! Time for revenge!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000376', '$C coming right at us!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1000377', 'Get this $C off of me!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_4');
INSERT INTO `script_texts` VALUES ('-1000378', 'Almost back to Ratchet! Let\'s keep up the pace...', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_ALMOST');
INSERT INTO `script_texts` VALUES ('-1000379', 'Ah, the sweet salt air of Ratchet.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_SWEET');
INSERT INTO `script_texts` VALUES ('-1000380', 'Captain Brightsun, $N here has freed me! $N, i am certain the Captain will reward your bravery.', null, null, null, null, null, null, null, null, '0', '0', '1', '66', 'gilthares SAY_GIL_FREED');
INSERT INTO `script_texts` VALUES ('-1000381', 'I sense the tortured spirits, $n. They are this way, come quickly!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'wilda SAY_WIL_START');
INSERT INTO `script_texts` VALUES ('-1000382', 'Watch out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000383', 'Naga attackers! Defend yourself!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000384', 'Grant me protection $n, i must break trough their foul magic!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS1');
INSERT INTO `script_texts` VALUES ('-1000385', 'The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS2');
INSERT INTO `script_texts` VALUES ('-1000386', 'Now we must find the exit.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_FIND_EXIT');
INSERT INTO `script_texts` VALUES ('-1000387', 'Lady Vashj must answer for these atrocities. She must be brought to justice!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS4');
INSERT INTO `script_texts` VALUES ('-1000388', 'The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS5');
INSERT INTO `script_texts` VALUES ('-1000389', 'It shouldn\'t be much further, $n. The exit is just up ahead.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_JUST_AHEAD');
INSERT INTO `script_texts` VALUES ('-1000390', 'Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_END');
INSERT INTO `script_texts` VALUES ('-1000391', 'I\'m Thirsty.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_THIRST');
INSERT INTO `script_texts` VALUES ('-1000392', 'Torta must be so worried.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_WORRIED');
INSERT INTO `script_texts` VALUES ('-1000393', 'Torta, my love! I have returned at long last.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_POST_1');
INSERT INTO `script_texts` VALUES ('-1000394', 'You have any idea how long I\'ve been waiting here? And where\'s dinner? All that time gone and nothing to show for it?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TORT_POST_2');
INSERT INTO `script_texts` VALUES ('-1000395', 'My dearest Torta. I have been gone for so long. Finally we are reunited. At long last our love can blossom again.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_POST_3');
INSERT INTO `script_texts` VALUES ('-1000396', 'Enough with the rambling. I am starving! Now, get your dusty shell into that ocean and bring momma some grub.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TORT_POST_4');
INSERT INTO `script_texts` VALUES ('-1000397', 'Yes Torta. Whatever your heart desires...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_POST_5');
INSERT INTO `script_texts` VALUES ('-1000398', 'And try not to get lost this time...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TORT_POST_6');
INSERT INTO `script_texts` VALUES ('-1000399', 'Peace is but a fleeting dream! Let the NIGHTMARE reign!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'taerar SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000400', 'Children of Madness - I release you upon this world!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'taerar SAY_SUMMONSHADE');
INSERT INTO `script_texts` VALUES ('-1000401', 'Hope is a DISEASE of the soul! This land shall wither and die!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'emeriss SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000402', 'Taste your world\'s corruption!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'emeriss SAY_CASTCORRUPTION');
INSERT INTO `script_texts` VALUES ('-1000403', 'Rin\'ji is free!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_RIN_FREE');
INSERT INTO `script_texts` VALUES ('-1000404', 'Attack my sisters! The troll must not escape!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_RIN_BY_OUTRUNNER');
INSERT INTO `script_texts` VALUES ('-1000405', 'Rin\'ji needs help!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_HELP_1');
INSERT INTO `script_texts` VALUES ('-1000406', 'Rin\'ji is being attacked!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_HELP_2');
INSERT INTO `script_texts` VALUES ('-1000407', 'Rin\'ji can see road now, $n. Rin\'ji knows the way home.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000408', 'Rin\'ji will tell you secret now... $n, should go to the Overlook Cliffs. Rin\'ji hid something on island  there', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_PROGRESS_1');
INSERT INTO `script_texts` VALUES ('-1000409', 'You find it, you keep it! Don\'t tell no one that Rin\'ji talked to you!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_PROGRESS_2');
INSERT INTO `script_texts` VALUES ('-1000410', 'Here they come! Defend yourself!', null, null, null, null, null, null, null, null, '0', '0', '1', '5', 'kanati SAY_KAN_START');
INSERT INTO `script_texts` VALUES ('-1000411', 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak for you back in the tower.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_1_TER');
INSERT INTO `script_texts` VALUES ('-1000412', 'Please... please... Miss Proudmore. I didn\'t mean to...', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_2_HEN');
INSERT INTO `script_texts` VALUES ('-1000413', 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_3_TER');
INSERT INTO `script_texts` VALUES ('-1000414', 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_4_TER');
INSERT INTO `script_texts` VALUES ('-1000415', '%s, too injured, gives up the chase.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'hendel EMOTE_SURRENDER');
INSERT INTO `script_texts` VALUES ('-1000416', 'Well, I\'m not sure how far I\'ll make it in this state... I\'m feeling kind of faint...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_START_1');
INSERT INTO `script_texts` VALUES ('-1000417', 'Remember, if I faint again, the water that Spraggle gave you will revive me.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_START_2');
INSERT INTO `script_texts` VALUES ('-1000418', 'The heat... I can\'t take it...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_1');
INSERT INTO `script_texts` VALUES ('-1000419', 'Maybe... you could carry me?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_2');
INSERT INTO `script_texts` VALUES ('-1000420', 'Uuuuuuggggghhhhh....', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_3');
INSERT INTO `script_texts` VALUES ('-1000421', 'I\'m not feeling so well...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_4');
INSERT INTO `script_texts` VALUES ('-1000422', 'Where... Where am I?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_1');
INSERT INTO `script_texts` VALUES ('-1000423', 'I am feeling a little better now, thank you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_2');
INSERT INTO `script_texts` VALUES ('-1000424', 'Yes, I must go on.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_3');
INSERT INTO `script_texts` VALUES ('-1000425', 'How am I feeling? Quite soaked, thank you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_4');
INSERT INTO `script_texts` VALUES ('-1000426', 'Spraggle! I didn\'t think I\'d make it back!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_1');
INSERT INTO `script_texts` VALUES ('-1000427', 'Ringo! You\'re okay!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_SPR_END_2');
INSERT INTO `script_texts` VALUES ('-1000428', 'Oh... I\'m feeling faint...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_3');
INSERT INTO `script_texts` VALUES ('-1000429', '%s collapses onto the ground.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_4');
INSERT INTO `script_texts` VALUES ('-1000430', '%s stands up after a short pause.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_5');
INSERT INTO `script_texts` VALUES ('-1000431', 'Ugh.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_6');
INSERT INTO `script_texts` VALUES ('-1000432', 'Ringo? Wake up! Don\'t worry, I\'ll take care of you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_SPR_END_7');
INSERT INTO `script_texts` VALUES ('-1000433', '%s fades away after a long pause.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_8');
INSERT INTO `script_texts` VALUES ('-1000434', 'Liladris has been waiting for me at Maestra\'s Post, so we should make haste, $N.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_START');
INSERT INTO `script_texts` VALUES ('-1000435', '%s looks very sleepy...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_1');
INSERT INTO `script_texts` VALUES ('-1000436', '%s suddenly falls asleep', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_2');
INSERT INTO `script_texts` VALUES ('-1000437', '%s begins to drift off...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_3');
INSERT INTO `script_texts` VALUES ('-1000438', 'This looks like the perfect place for a nap...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_1');
INSERT INTO `script_texts` VALUES ('-1000439', 'Yaaaaawwwwwnnnn...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_2');
INSERT INTO `script_texts` VALUES ('-1000440', 'Oh, I am so tired...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_3');
INSERT INTO `script_texts` VALUES ('-1000441', 'You don\'t mind if I stop here for a moment, do you?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_4');
INSERT INTO `script_texts` VALUES ('-1000442', 'Be on the alert! The Blackwood furbolgs are numerous in the area...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_ALERT_1');
INSERT INTO `script_texts` VALUES ('-1000443', 'It\'s quiet... Too quiet...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_ALERT_2');
INSERT INTO `script_texts` VALUES ('-1000444', 'Oh, I can see Liladris from here... Tell her I\'m here, won\'t you?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_END');
INSERT INTO `script_texts` VALUES ('-1000445', '%s wakes up!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_AWAKEN');
INSERT INTO `script_texts` VALUES ('-1000446', 'A-Me good. Good, A-Me. Follow... follow A-Me. Home. A-Me go home.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_START');
INSERT INTO `script_texts` VALUES ('-1000447', 'Good... good, A-Me. A-Me good. Home. Find home.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000448', 'A-Me home! A-Me good! Good A-Me. Home. Home. Home.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_END');
INSERT INTO `script_texts` VALUES ('-1000449', '$c, no hurt A-Me. A-Me good.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000450', 'Danger. Danger! $c try hurt A-Me.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000451', 'Bad, $c. $c, bad!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1000452', 'I noticed some fire on that island over there. A human, too. Let\'s go check it out, $n.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_START');
INSERT INTO `script_texts` VALUES ('-1000453', 'That\'s Reethe alright. Let\'s go see what he has to say, yeah?', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'ogron SAY_OGR_SPOT');
INSERT INTO `script_texts` VALUES ('-1000454', 'W-what do you want? Just leave me alone...', null, null, null, null, null, null, null, null, '0', '0', '0', '6', 'ogron SAY_OGR_RET_WHAT');
INSERT INTO `script_texts` VALUES ('-1000455', 'I swear. I didn\'t steal anything from you! Here, take some of my supplies, just go away!', null, null, null, null, null, null, null, null, '0', '0', '0', '27', 'ogron SAY_OGR_RET_SWEAR');
INSERT INTO `script_texts` VALUES ('-1000456', 'Just tell us what you know about the Shady Rest Inn, and I won\'t bash your skull in.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_REPLY_RET');
INSERT INTO `script_texts` VALUES ('-1000457', 'I... Well, I may of taken a little thing or two from the inn... but what would an ogre care about that?', null, null, null, null, null, null, null, null, '0', '0', '0', '6', 'ogron SAY_OGR_RET_TAKEN');
INSERT INTO `script_texts` VALUES ('-1000458', 'Look here, if you don\'t tell me about the fire--', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_TELL_FIRE');
INSERT INTO `script_texts` VALUES ('-1000459', 'Not one step closer, ogre!', null, null, null, null, null, null, null, null, '0', '0', '0', '27', 'ogron SAY_OGR_RET_NOCLOSER');
INSERT INTO `script_texts` VALUES ('-1000460', 'And I don\'t know anything about this fire of yours...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_RET_NOFIRE');
INSERT INTO `script_texts` VALUES ('-1000461', 'What was that? Did you hear something?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_RET_HEAR');
INSERT INTO `script_texts` VALUES ('-1000462', 'Paval Reethe! Found you at last. And consorting with ogres now? No fear, even deserters and traitors are afforded some mercy.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_CAL_FOUND');
INSERT INTO `script_texts` VALUES ('-1000463', 'Private, show Lieutenant Reethe some mercy.', null, null, null, null, null, null, null, null, '0', '0', '0', '29', 'ogron SAY_OGR_CAL_MERCY');
INSERT INTO `script_texts` VALUES ('-1000464', 'Gladly, sir.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_HALL_GLAD');
INSERT INTO `script_texts` VALUES ('-1000465', '%s staggers backwards as the arrow lodges itself deeply in his chest.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ogron EMOTE_OGR_RET_ARROW');
INSERT INTO `script_texts` VALUES ('-1000466', 'Ugh... Hallan, didn\'t think you had it in you...', null, null, null, null, null, null, null, null, '0', '0', '0', '34', 'ogron SAY_OGR_RET_ARROW');
INSERT INTO `script_texts` VALUES ('-1000467', 'Now, let\'s clean up the rest of the trash, men!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_CAL_CLEANUP');
INSERT INTO `script_texts` VALUES ('-1000468', 'Damn it! You\'d better not die on me, human!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_NODIE');
INSERT INTO `script_texts` VALUES ('-1000469', 'Still with us, Reethe?', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_SURVIVE');
INSERT INTO `script_texts` VALUES ('-1000470', 'Must be your lucky day. Alright, I\'ll talk. Just leave me alone. Look, you\'re not going to believe me, but it wa... oh, Light, looks like the girl could shoot...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_RET_LUCKY');
INSERT INTO `script_texts` VALUES ('-1000471', 'By the way, thanks for watching my back.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_THANKS');
INSERT INTO `script_texts` VALUES ('-1000472', '1...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_1');
INSERT INTO `script_texts` VALUES ('-1000473', '2...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_2');
INSERT INTO `script_texts` VALUES ('-1000474', '3...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_3');
INSERT INTO `script_texts` VALUES ('-1000475', '4...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_4');
INSERT INTO `script_texts` VALUES ('-1000476', '5...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_5');
INSERT INTO `script_texts` VALUES ('-1000477', 'Let us leave this place. I\'ve had enough of these madmen!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_START');
INSERT INTO `script_texts` VALUES ('-1000478', 'You\'ll go nowhere, fool!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_AMBUSH_A');
INSERT INTO `script_texts` VALUES ('-1000479', 'Beware! More cultists come!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_AMBUSH_B');
INSERT INTO `script_texts` VALUES ('-1000480', 'You will not escape us so easily!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_AMBUSH_B_REPLY');
INSERT INTO `script_texts` VALUES ('-1000481', 'I can find my way from here. My friend Mekeda will reward you for your kind actions.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000482', 'Look out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_START');
INSERT INTO `script_texts` VALUES ('-1000483', 'Don\'t let them escape! Kill the strong one first!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_NO_ESCAPE');
INSERT INTO `script_texts` VALUES ('-1000484', 'More of them coming! Watch out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_MORE');
INSERT INTO `script_texts` VALUES ('-1000485', 'Where do you think you\'re going? Kill them all!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_MORE_REPLY');
INSERT INTO `script_texts` VALUES ('-1000486', 'Ride the lightning, filth!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_LIGHTNING');
INSERT INTO `script_texts` VALUES ('-1000487', 'FROST SHOCK!!!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_SHOCK');
INSERT INTO `script_texts` VALUES ('-1000488', 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell stranger.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000489', 'Show our guest around Shattrath, will you? Keep an eye out for pickpockets in the lower city.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_START');
INSERT INTO `script_texts` VALUES ('-1000490', 'A newcomer to Shattrath! Make sure to drop by later. We can always use a hand with the injured.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_INJURED');
INSERT INTO `script_texts` VALUES ('-1000491', 'Be mindful of what you say, this one\'s being shown around by Khadgar\'s pet.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_MIND_YOU');
INSERT INTO `script_texts` VALUES ('-1000492', 'Are you joking? I\'m a Scryer, I always watch what i say. More enemies than allies in this city, it seems.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_MIND_ALWAYS');
INSERT INTO `script_texts` VALUES ('-1000493', 'Light be with you, $n. And welcome to Shattrath.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_ALDOR_GREET');
INSERT INTO `script_texts` VALUES ('-1000494', 'We\'re rather selective of who we befriend, $n. You think you have what it takes?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_SCRYER_GREET');
INSERT INTO `script_texts` VALUES ('-1000495', 'Khadgar himself is showing you around? You must have made a good impression, $n.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_HAGGARD');
INSERT INTO `script_texts` VALUES ('-1000496', '%s lifts its head into the air, as if listening for something.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ancestral wolf EMOTE_WOLF_LIFT_HEAD');
INSERT INTO `script_texts` VALUES ('-1000497', '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ancestral wolf EMOTE_WOLF_HOWL');
INSERT INTO `script_texts` VALUES ('-1000498', 'Welcome, kind spirit. What has brought you to us?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ancestral wolf SAY_WOLF_WELCOME');
INSERT INTO `script_texts` VALUES ('-1000499', 'Puny $r wanna fight %s? Me beat you! Me boss here!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'morokk SAY_MOR_CHALLENGE');
INSERT INTO `script_texts` VALUES ('-1000500', 'Me scared! Me run now!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'morokk SAY_MOR_SCARED');
INSERT INTO `script_texts` VALUES ('-1000501', 'Are you sure that you are ready? If we do not have a group of your allies to aid us, we will surely fail.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_START1');
INSERT INTO `script_texts` VALUES ('-1000502', 'This will be a though fight, $n. Follow me closely.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_START2');
INSERT INTO `script_texts` VALUES ('-1000503', 'This is the brazier, $n. Put it out. Vorsha is a beast, worthy of praise from no one!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_BRAZIER');
INSERT INTO `script_texts` VALUES ('-1000504', 'Now we must wait. It won\'t be long before the naga realize what we have done.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_BRAZIER_WAIT');
INSERT INTO `script_texts` VALUES ('-1000505', 'Be on your guard, $n!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_ON_GUARD');
INSERT INTO `script_texts` VALUES ('-1000506', 'Perhaps we will get a moment to rest. But i will not give up until we have faced off against Vorsha!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_REST');
INSERT INTO `script_texts` VALUES ('-1000507', 'We have done it!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_DONE');
INSERT INTO `script_texts` VALUES ('-1000508', 'You have my deepest gratitude. I thank you.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_GRATITUDE');
INSERT INTO `script_texts` VALUES ('-1000509', 'I am going to patrol the area for a while longer and ensure that things are truly safe.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_PATROL');
INSERT INTO `script_texts` VALUES ('-1000510', 'Please return to Zoram\'gar and report our success to the Warsong runner.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_RETURN');
INSERT INTO `script_texts` VALUES ('-1000511', 'Aright, listen up! Form a circle around me and move out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_START');
INSERT INTO `script_texts` VALUES ('-1000512', 'Aright, $r, just keep us safe from harm while we work. We\'ll pay you when we return.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_KEEP_SAFE');
INSERT INTO `script_texts` VALUES ('-1000513', 'The dig site is just north of here.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NORTH');
INSERT INTO `script_texts` VALUES ('-1000514', 'We\'re here! Start diggin\'!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_ARRIVE');
INSERT INTO `script_texts` VALUES ('-1000515', 'I think there\'s somethin\' buried here, beneath the sand!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_BURIED');
INSERT INTO `script_texts` VALUES ('-1000516', 'Almost got it!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_ALMOST');
INSERT INTO `script_texts` VALUES ('-1000517', 'By brann\'s brittle bananas! What is it!? It... It looks like a drum.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_DRUM');
INSERT INTO `script_texts` VALUES ('-1000518', 'Wow... a drum.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_DRUM_REPLY');
INSERT INTO `script_texts` VALUES ('-1000519', 'This discovery will surely rock the foundation of modern archaeology.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_DISCOVERY');
INSERT INTO `script_texts` VALUES ('-1000520', 'Yea, great. Can we leave now? This desert is giving me hives.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_DISCOVERY_REPLY');
INSERT INTO `script_texts` VALUES ('-1000521', 'Have ye gone mad? You expect me to leave behind a drum without first beatin\' on it? Not this son of Ironforge! No sir!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE');
INSERT INTO `script_texts` VALUES ('-1000522', 'This reminds me of that one time where you made us search Silithus for evidence of sand gnomes.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE_REPLY1');
INSERT INTO `script_texts` VALUES ('-1000523', 'Or that time when you told us that you\'d discovered the cure for the plague of the 20th century. What is that even? 20th century?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE_REPLY2');
INSERT INTO `script_texts` VALUES ('-1000524', 'I don\'t think it can top the one time where he told us that he\'d heard that Artha\'s \"cousin\'s\" skeleton was frozen beneath a glacier in Winterspring. I\'ll never forgive you for that one, Letoll. I mean honestly... Artha\'s cousin?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE_REPLY3');
INSERT INTO `script_texts` VALUES ('-1000525', 'I dunno. It can\'t possibly beat the time he tried to convince us that we\'re all actually a figment of some being\'s imagination and that they only use us for their own personal amusement. That went over well during dinner with the family.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE_REPLY4');
INSERT INTO `script_texts` VALUES ('-1000526', 'Shut yer yaps! I\'m gonna bang on this drum and that\'s that!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_SHUT');
INSERT INTO `script_texts` VALUES ('-1000527', 'Say, do you guys hear that?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_REPLY_HEAR');
INSERT INTO `script_texts` VALUES ('-1000528', 'IN YOUR FACE! I told you there was somethin\' here!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_IN_YOUR_FACE');
INSERT INTO `script_texts` VALUES ('-1000529', 'Don\'t just stand there! Help him out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_HELP_HIM');
INSERT INTO `script_texts` VALUES ('-1000530', '%s picks up the drum.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'letoll EMOTE_LE_PICK_UP');
INSERT INTO `script_texts` VALUES ('-1000531', 'You\'ve been a tremendous help, $r! Let\'s get out of here before more of those things show up! I\'ll let Dwarfowitz know you did the job asked of ya\' admirably.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_THANKS');
INSERT INTO `script_texts` VALUES ('-1000532', 'At your command, my liege...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'torloth TORLOTH_DIALOGUE1');
INSERT INTO `script_texts` VALUES ('-1000533', 'As you desire, Lord Illidan.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'torloth TORLOTH_DIALOGUE2');
INSERT INTO `script_texts` VALUES ('-1000534', 'Yes, Lord Illidan, I would sacrifice to you this magnificent physique. On this day you will fall - another victim of Torloth...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'torloth TORLOTH_DIALOGUE3');
INSERT INTO `script_texts` VALUES ('-1000535', 'Destroy them, Torloth. Let lose their blood like a river upon this hallowed ground.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'lordillidan ILLIDAN_DIALOGUE');
INSERT INTO `script_texts` VALUES ('-1000536', 'What manner of fool dares stand before Illidan Stormrage? Soldiers, destroy these insects!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'lordillidan ILLIDAN_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1000537', 'You are no challenge for the Crimson Sigil. Mind breakers, end this nonsense.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'lordillidan ILLIDAN_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1000538', 'Run while you still can. The highlords come soon...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'lordillidan ILLIDAN_SUMMON3');
INSERT INTO `script_texts` VALUES ('-1000539', 'Torloth your master calls!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'lordillidan ILLIDAN_SUMMON4');
INSERT INTO `script_texts` VALUES ('-1000540', 'So you have defeated the Crimson Sigil. You now seek to challenge my rule? Not even Arthas could defeat me, yet you dare to even harbor such thoughts? Then I say to you, come! Come $N! The Black Temple awaits...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'lordillidan EVENT_COMPLETED');
INSERT INTO `script_texts` VALUES ('-1000541', '%s jumps into the moonwell and goes underwater...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kitten EMOTE_SAB_JUMP');
INSERT INTO `script_texts` VALUES ('-1000542', '%s follows $n obediertly.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kitten EMOTE_SAB_FOLLOW');
INSERT INTO `script_texts` VALUES ('-1000543', 'Why have you come here, outsider? You will only find pain! Our fate will be yours...', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_1');
INSERT INTO `script_texts` VALUES ('-1000544', 'It was ... terrible... the demon...', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_2');
INSERT INTO `script_texts` VALUES ('-1000545', 'The darkness... the corruption... they came too quickly for anyone to know...', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_3');
INSERT INTO `script_texts` VALUES ('-1000546', 'The darkness will consume all... all the living...', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_4');
INSERT INTO `script_texts` VALUES ('-1000547', 'It is too late for us, living one. Take yourself and your friend away from here before you both are... claimed...', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_5');
INSERT INTO `script_texts` VALUES ('-1000548', 'It is too late for Jarl... its hold is too strong...', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_6');
INSERT INTO `script_texts` VALUES ('-1000549', 'Go away, whoever you are! Witch Hill is mine... mine!', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_7');
INSERT INTO `script_texts` VALUES ('-1000550', 'The manor... someone else... will soon be consumed...', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_8');
INSERT INTO `script_texts` VALUES ('-1033000', 'Follow me and I\'ll open the courtyard door for you.', null, null, null, null, null, null, null, null, '0', '0', '7', '1', 'prisoner ashcrombe SAY_FREE_AS');
INSERT INTO `script_texts` VALUES ('-1033001', 'I have just the spell to get this door open. Too bad the cell doors weren\'t locked so haphazardly.', null, null, null, null, null, null, null, null, '0', '0', '7', '1', 'prisoner ashcrombe SAY_OPEN_DOOR_AS');
INSERT INTO `script_texts` VALUES ('-1033002', 'There it is! Wide open. Good luck to you conquering what lies beyond. I must report back to the Kirin Tor at once!', null, null, null, null, null, null, null, null, '0', '0', '7', '1', 'prisoner ashcrombe SAY_POST_DOOR_AS');
INSERT INTO `script_texts` VALUES ('-1033003', 'Free from this wretched cell at last! Let me show you to the courtyard....', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_FREE_AD');
INSERT INTO `script_texts` VALUES ('-1033004', 'You are indeed courageous for wanting to brave the horrors that lie beyond this door.', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_OPEN_DOOR_AD');
INSERT INTO `script_texts` VALUES ('-1033005', 'There we go!', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_POST1_DOOR_AD');
INSERT INTO `script_texts` VALUES ('-1033006', 'Good luck with Arugal. I must hurry back to Hadrec now.', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_POST2_DOOR_AD');
INSERT INTO `script_texts` VALUES ('-1033007', 'About time someone killed the wretch.', null, null, null, null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_BOSS_DIE_AD');
INSERT INTO `script_texts` VALUES ('-1033008', 'For once I agree with you... scum.', null, null, null, null, null, null, null, null, '0', '0', '7', '1', 'prisoner ashcrombe SAY_BOSS_DIE_AS');
INSERT INTO `script_texts` VALUES ('-1036000', 'You there, check out that noise!', null, null, null, null, null, null, null, null, '5775', '1', '7', '0', 'smite INST_SAY_ALARM1');
INSERT INTO `script_texts` VALUES ('-1036001', 'We\'re under attack! A vast, ye swabs! Repel the invaders!', null, null, null, null, null, null, null, null, '5777', '1', '7', '0', 'smite INST_SAY_ALARM2');
INSERT INTO `script_texts` VALUES ('-1070000', 'None may steal the secrets of the makers!', null, null, null, null, null, null, null, null, '5851', '1', '0', '0', 'ironaya SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1109000', 'The walls of the chamber tremble. Something is happening...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'malfurion stormrage EMOTE_MALFURION');
INSERT INTO `script_texts` VALUES ('-1109001', 'Be steadfast, champion. I know why it is that you are here and I know what it is that you seek. Eranikus will not give up the shard freely. He has been twisted... twisted by the same force that you seek to destroy.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'malfurion stormrge SAY_MALFURION1');
INSERT INTO `script_texts` VALUES ('-1109002', 'Are you really surprised? Is it hard to believe that the power of an Old God could reach even inside the Dream? It is true - Eranikus, Tyrant of the Dream, wages a battle against us all. The Nightmare follows in his wake of destruction.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'malfurion stormrge SAY_MALFURION2');
INSERT INTO `script_texts` VALUES ('-1109003', 'Understand this, Eranikus wants nothing more than to be brought to Azeroth from the Dream. Once he is out, he will stop at nothing to destroy my physical manifestation. This, however, is the only way in which you could recover the scepter shard.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'malfurion stormrge SAY_MAFLURION3');
INSERT INTO `script_texts` VALUES ('-1109004', 'You will bring him back into this world, champion.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'malfurion Stormrge SAY_MALFURION4');
INSERT INTO `script_texts` VALUES ('-1129000', 'You\'ll never leave this place... alive.', null, null, null, null, null, null, null, null, '5825', '1', '0', '0', 'amnennar SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1129001', 'To me, my servants!', null, null, null, null, null, null, null, null, '5828', '1', '0', '0', 'amnennar SAY_SUMMON60');
INSERT INTO `script_texts` VALUES ('-1129002', 'Come, spirits, attend your master!', null, null, null, null, null, null, null, null, '5829', '1', '0', '0', 'amnennar SAY_SUMMON30');
INSERT INTO `script_texts` VALUES ('-1129003', 'I am the hand of the Lich King!', null, null, null, null, null, null, null, null, '5827', '1', '0', '0', 'amnennar SAY_HP');
INSERT INTO `script_texts` VALUES ('-1129004', 'Too...easy!', null, null, null, null, null, null, null, null, '5826', '1', '0', '0', 'amnennar SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1189000', 'Ah, I have been waiting for a real challenge!', null, null, null, null, null, null, null, null, '5830', '1', '0', '0', 'herod SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189001', 'Blades of Light!', null, null, null, null, null, null, null, null, '5832', '1', '0', '0', 'herod SAY_WHIRLWIND');
INSERT INTO `script_texts` VALUES ('-1189002', 'Light, give me strength!', null, null, null, null, null, null, null, null, '5833', '1', '0', '0', 'herod SAY_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1189003', 'Hah, is that all?', null, null, null, null, null, null, null, null, '5831', '1', '0', '0', 'herod SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1189004', '%s becomes enraged!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'herod EMOTE_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1189005', 'Infidels! They must be purified!', null, null, null, null, null, null, null, null, '5835', '1', '0', '0', 'mograine SAY_MO_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189006', 'Unworthy!', null, null, null, null, null, null, null, null, '5836', '1', '0', '0', 'mograine SAY_MO_KILL');
INSERT INTO `script_texts` VALUES ('-1189007', 'At your side, milady!', null, null, null, null, null, null, null, null, '5837', '1', '0', '0', 'mograine SAY_MO_RESSURECTED');
INSERT INTO `script_texts` VALUES ('-1189008', 'What, Mograine has fallen? You shall pay for this treachery!', null, null, null, null, null, null, null, null, '5838', '1', '0', '0', 'whitemane SAY_WH_INTRO');
INSERT INTO `script_texts` VALUES ('-1189009', 'The Light has spoken!', null, null, null, null, null, null, null, null, '5839', '1', '0', '0', 'whitemane SAY_WH_KILL');
INSERT INTO `script_texts` VALUES ('-1189010', 'Arise, my champion!', null, null, null, null, null, null, null, null, '5840', '1', '0', '0', 'whitemane SAY_WH_RESSURECT');
INSERT INTO `script_texts` VALUES ('-1189011', 'Tell me... tell me everything!', null, null, null, null, null, null, null, null, '5847', '1', '0', '0', 'vishas SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189012', 'Naughty secrets!', null, null, null, null, null, null, null, null, '5849', '1', '0', '0', 'vishas SAY_HEALTH1');
INSERT INTO `script_texts` VALUES ('-1189013', 'I\'ll rip the secrets from your flesh!', null, null, null, null, null, null, null, null, '5850', '1', '0', '0', 'vishas SAY_HEALTH2');
INSERT INTO `script_texts` VALUES ('-1189014', 'Purged by pain!', null, null, null, null, null, null, null, null, '5848', '1', '0', '0', 'vishas SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1189015', 'The monster got what he deserved.', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'vishas SAY_TRIGGER_VORREL');
INSERT INTO `script_texts` VALUES ('-1189016', 'We hunger for vengeance.', null, null, null, null, null, null, null, null, '5844', '1', '0', '0', 'thalnos SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189017', 'No rest, for the angry dead.', null, null, null, null, null, null, null, null, '5846', '1', '0', '0', 'thalnos SAY_HEALTH');
INSERT INTO `script_texts` VALUES ('-1189018', 'More... More souls.', null, null, null, null, null, null, null, null, '5845', '1', '0', '0', 'thalnos SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1189019', 'You will not defile these mysteries!', null, null, null, null, null, null, null, null, '5842', '1', '0', '0', 'doan SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189020', 'Burn in righteous fire!', null, null, null, null, null, null, null, null, '5843', '1', '0', '0', 'doan SAY_SPECIALAE');
INSERT INTO `script_texts` VALUES ('-1189021', 'Release the hounds!', null, null, null, null, null, null, null, null, '5841', '1', '0', '0', 'loksey SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189022', 'It is over, your search is done! Let fate choose now, the righteous one.', null, null, null, null, null, null, null, null, '11961', '1', '0', '0', 'horseman SAY_ENTRANCE');
INSERT INTO `script_texts` VALUES ('-1189023', 'Here\'s my body, fit and pure! Now, your blackened souls I\'ll cure!', null, null, null, null, null, null, null, null, '12567', '1', '0', '0', 'horseman SAY_REJOINED');
INSERT INTO `script_texts` VALUES ('-1189024', 'So eager you are for my blood to spill, yet to vanquish me this my head you must kill!', null, null, null, null, null, null, null, null, '11969', '1', '0', '0', 'horseman SAY_BODY_DEFEAT');
INSERT INTO `script_texts` VALUES ('-1189025', 'Over here, you idiot!', null, null, null, null, null, null, null, null, '12569', '1', '0', '0', 'horseman SAY_LOST_HEAD');
INSERT INTO `script_texts` VALUES ('-1189026', 'Harken, cur! Tis you I spurn! Now, $N, feel the burn!', null, null, null, null, null, null, null, null, '12573', '1', '0', '0', 'horseman SAY_CONFLAGRATION');
INSERT INTO `script_texts` VALUES ('-1189027', 'Soldiers arise, stand and fight! Bring victory at last to this fallen knight!', null, null, null, null, null, null, null, null, '11963', '1', '0', '0', 'horseman SAY_SPROUTING_PUMPKINS');
INSERT INTO `script_texts` VALUES ('-1189028', 'Your body lies beaten, battered and broken. Let my curse be your own, fate has spoken.', null, null, null, null, null, null, null, null, '11962', '1', '0', '0', 'horseman SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1189029', 'This end have I reached before. What new adventure lies in store?', null, null, null, null, null, null, null, null, '11964', '1', '0', '0', 'horseman SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1189030', '%s laughs.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'horseman EMOTE_LAUGH');
INSERT INTO `script_texts` VALUES ('-1189031', 'Horseman rise...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'horseman SAY_PLAYER1');
INSERT INTO `script_texts` VALUES ('-1189032', 'Your time is night...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'horseman SAY_PLAYER2');
INSERT INTO `script_texts` VALUES ('-1189033', 'You felt death once...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'horseman SAY_PLAYER3');
INSERT INTO `script_texts` VALUES ('-1189034', 'Now, know demise!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'horseman SAY_PLAYER4');
INSERT INTO `script_texts` VALUES ('-1230000', 'Ah, hits the spot!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'rocknot SAY_GOT_BEER');
INSERT INTO `script_texts` VALUES ('-1230001', 'Come to aid the Throne!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'dagran SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1230002', 'Hail to the king, baby!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'dagran SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1230003', 'You have challenged the Seven, and now you will die!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'doomrel SAY_DOOMREL_START_EVENT');
INSERT INTO `script_texts` VALUES ('-1249000', 'How fortuitous. Usually, I must leave my lair to feed.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'onyxia SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1249001', 'Learn your place mortal!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'onyxia SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1249002', 'This meaningless exertion bores me. I\'ll incinerate you all from above!', null, null, null, null, null, null, null, null, '0', '1', '0', '254', 'onyxia SAY_PHASE_2_TRANS');
INSERT INTO `script_texts` VALUES ('-1249003', 'It seems you\'ll need another lesson, mortals!', null, null, null, null, null, null, null, null, '0', '1', '0', '293', 'onyxia SAY_PHASE_3_TRANS');
INSERT INTO `script_texts` VALUES ('-1249004', '%s takes in a deep breath...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'onyxia EMOTE_BREATH');
INSERT INTO `script_texts` VALUES ('-1269000', 'Why do you persist? Surely you can see the futility of it all. It is not too late! You may still leave with your lives ...', null, null, null, null, null, null, null, null, '10442', '1', '0', '0', 'temporus SAY_ENTER');
INSERT INTO `script_texts` VALUES ('-1269001', 'So be it ... you have been warned.', null, null, null, null, null, null, null, null, '10444', '1', '0', '0', 'temporus SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1269002', 'Time... sands of time is run out for you.', null, null, null, null, null, null, null, null, '10443', '1', '0', '0', 'temporus SAY_BANISH');
INSERT INTO `script_texts` VALUES ('-1269003', 'You should have left when you had the chance.', null, null, null, null, null, null, null, null, '10445', '1', '0', '0', 'temporus SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1269004', 'Your days are done.', null, null, null, null, null, null, null, null, '10446', '1', '0', '0', 'temporus SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1269005', 'My death means ... little.', null, null, null, null, null, null, null, null, '10447', '1', '0', '0', 'temporus SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1269006', 'Why do you aid the Magus? Just think of how many lives could be saved if the portal is never opened, if the resulting wars could be erased ...', null, null, null, null, null, null, null, null, '10412', '1', '0', '0', 'chrono_lord_deja SAY_ENTER');
INSERT INTO `script_texts` VALUES ('-1269007', 'If you will not cease this foolish quest, then you will die!', null, null, null, null, null, null, null, null, '10414', '1', '0', '0', 'chrono_lord_deja SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1269008', 'You have outstayed your welcome, Timekeeper. Begone!', null, null, null, null, null, null, null, null, '10413', '1', '0', '0', 'chrono_lord_deja SAY_BANISH');
INSERT INTO `script_texts` VALUES ('-1269009', 'I told you it was a fool\'s quest!', null, null, null, null, null, null, null, null, '10415', '1', '0', '0', 'chrono_lord_deja SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1269010', 'Leaving so soon?', null, null, null, null, null, null, null, null, '10416', '1', '0', '0', 'chrono_lord_deja SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1269011', 'Time ... is on our side.', null, null, null, null, null, null, null, null, '10417', '1', '0', '0', 'chrono_lord_deja SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1269012', 'The time has come to shatter this clockwork universe forever! Let us no longer be slaves of the hourglass! I warn you: those who do not embrace the greater path shall become victims of its passing!', null, null, null, null, null, null, null, null, '10400', '1', '0', '0', 'aeonus SAY_ENTER');
INSERT INTO `script_texts` VALUES ('-1269013', 'Let us see what fate lays in store...', null, null, null, null, null, null, null, null, '10402', '1', '0', '0', 'aeonus SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1269014', 'Your time is up, slave of the past!', null, null, null, null, null, null, null, null, '10401', '1', '0', '0', 'aeonus SAY_BANISH');
INSERT INTO `script_texts` VALUES ('-1269015', 'One less obstacle in our way!', null, null, null, null, null, null, null, null, '10403', '1', '0', '0', 'aeonus SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1269016', 'No one can stop us! No one!', null, null, null, null, null, null, null, null, '10404', '1', '0', '0', 'aeonus SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1269017', 'It is only a matter...of time.', null, null, null, null, null, null, null, null, '10405', '1', '0', '0', 'aeonus SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1269018', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1269019', 'Stop! Do not go further, mortals. You are ill-prepared to face the forces of the Infinite Dragonflight. Come, let me help you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'saat SAY_SAAT_WELCOME');
INSERT INTO `script_texts` VALUES ('-1269020', 'The time has come! Gul\'dan, order your warlocks to double their efforts! Moments from now the gateway will open, and your Horde will be released upon this ripe, unsuspecting world!', null, null, null, null, null, null, null, null, '10435', '1', '0', '0', 'medivh SAY_ENTER');
INSERT INTO `script_texts` VALUES ('-1269021', 'What is this? Champions, coming to my aid? I sense the hand of the dark one in this. Truly this sacred event bears his blessing?', null, null, null, null, null, null, null, null, '10436', '1', '0', '0', 'medivh SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1269022', 'Champions, my shield grows weak!', null, null, null, null, null, null, null, null, '10437', '1', '0', '0', 'medivh SAY_WEAK75');
INSERT INTO `script_texts` VALUES ('-1269023', 'My powers must be concentrated on the portal! I do not have time to hold the shield!', null, null, null, null, null, null, null, null, '10438', '1', '0', '0', 'medivh SAY_WEAK50');
INSERT INTO `script_texts` VALUES ('-1269024', 'The shield is nearly gone! All that I have worked for is in danger!', null, null, null, null, null, null, null, null, '10439', '1', '0', '0', 'medivh SAY_WEAK25');
INSERT INTO `script_texts` VALUES ('-1269025', 'No... damn this feeble mortal coil...', null, null, null, null, null, null, null, null, '10441', '1', '0', '0', 'medivh SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1269026', 'I am grateful for your aid, champions. Now, Gul\'dan\'s Horde will sweep across this world, like a locust swarm, and all my designs, all my carefully laid plans will at last fall into place.', null, null, null, null, null, null, null, null, '10440', '1', '0', '0', 'medivh SAY_WIN');
INSERT INTO `script_texts` VALUES ('-1269027', 'Orcs of the Horde! This portalis the gateway to your new destiny! Azeroth lies before you, ripe for the taking!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'medivh SAY_ORCS_ENTER');
INSERT INTO `script_texts` VALUES ('-1269028', 'Gul\'dan speaks the truth! We should return at once to tell our brothers of the news! Retreat back trought the portal!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'medivh SAY_ORCS_ANSWER');
INSERT INTO `script_texts` VALUES ('-1309000', 'Let the coils of hate unfurl!', null, null, null, null, null, null, null, null, '8421', '1', '0', '0', 'venoxis SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1309001', 'Ssserenity..at lassst!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'venoxis SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309002', 'Lord Hir\'eek, grant me wings of vengance!', null, null, null, null, null, null, null, null, '8417', '1', '0', '0', 'jeklik SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309003', 'I command you to rain fire down upon these invaders!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'jeklik SAY_RAIN_FIRE');
INSERT INTO `script_texts` VALUES ('-1309004', 'Finally ...death. Curse you Hakkar! Curse you!', null, null, null, null, null, null, null, null, '8422', '1', '0', '0', 'jeklik SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309005', 'Draw me to your web mistress Shadra. Unleash your venom!', null, null, null, null, null, null, null, null, '8418', '1', '0', '0', 'marli SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309006', 'Shadra, make of me your avatar!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'marli SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1309007', 'Aid me my brood!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'marli SAY_SPIDER_SPAWN');
INSERT INTO `script_texts` VALUES ('-1309008', 'Bless you mortal for this release. Hakkar controls me no longer...', null, null, null, null, null, null, null, null, '8423', '1', '0', '0', 'marli SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309009', 'Shirvallah, fill me with your RAGE!', null, null, null, null, null, null, null, null, '8419', '1', '0', '0', 'thekal SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309010', 'Hakkar binds me no more! Peace at last!', null, null, null, null, null, null, null, null, '8424', '1', '0', '0', 'thekal SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309011', 'Bethekk, your priestess calls upon your might!', null, null, null, null, null, null, null, null, '8416', '1', '0', '0', 'arlokk SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309012', 'Feast on $n, my pretties!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'arlokk SAY_FEAST_PANTHER');
INSERT INTO `script_texts` VALUES ('-1309013', 'At last, I am free of the Soulflayer!', null, null, null, null, null, null, null, null, '8412', '1', '0', '0', 'arlokk SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309014', 'Welcome to da great show friends! Step right up to die!', null, null, null, null, null, null, null, null, '8425', '1', '0', '0', 'jindo SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309015', 'I\'ll feed your souls to Hakkar himself!', null, null, null, null, null, null, null, null, '8413', '1', '0', '0', 'mandokir SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309016', 'DING!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'mandokir SAY_DING_KILL');
INSERT INTO `script_texts` VALUES ('-1309017', 'GRATS!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'mandokir SAY_GRATS_JINDO');
INSERT INTO `script_texts` VALUES ('-1309018', '$N! I\'m watching you!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'mandokir SAY_WATCH');
INSERT INTO `script_texts` VALUES ('-1309019', 'Don\'t make me angry. You won\'t like it when I\'m angry.', null, null, null, null, null, null, null, null, '0', '4', '0', '0', 'mandokir SAY_WATCH_WHISPER');
INSERT INTO `script_texts` VALUES ('-1309020', 'PRIDE HERALDS THE END OF YOUR WORLD. COME, MORTALS! FACE THE WRATH OF THE SOULFLAYER!', null, null, null, null, null, null, null, null, '8414', '1', '0', '0', 'hakkar SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309021', 'Fleeing will do you no good, mortals!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'hakkar SAY_FLEEING');
INSERT INTO `script_texts` VALUES ('-1309022', 'You dare set foot upon Hakkari holy ground? Minions of Hakkar, destroy the infidels!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'hakkar SAY_MINION_DESTROY');
INSERT INTO `script_texts` VALUES ('-1309023', 'Minions of Hakkar, hear your God. The sanctity of this temple has been compromised. Invaders encroach upon holy ground! The Altar of Blood must be protected. Kill them all!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'hakkar SAY_PROTECT_ALTAR');
INSERT INTO `script_texts` VALUES ('-1309024', '%s goes into a rage after seeing his raptor fall in battle!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'mandokir EMOTE_RAGE');
INSERT INTO `script_texts` VALUES ('-1329000', 'Thanks to Egan', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'freed_soul SAY_ZAPPED0');
INSERT INTO `script_texts` VALUES ('-1329001', 'Rivendare must die', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'freed_soul SAY_ZAPPED1');
INSERT INTO `script_texts` VALUES ('-1329002', 'Who you gonna call?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'freed_soul SAY_ZAPPED2');
INSERT INTO `script_texts` VALUES ('-1329003', 'Don\'t cross those beams!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'freed_soul SAY_ZAPPED3');
INSERT INTO `script_texts` VALUES ('-1409000', '%s performs one last service for Ragnaros.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'geddon EMOTE_SERVICE');
INSERT INTO `script_texts` VALUES ('-1409001', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1409002', '%s refuses to die while its master is in trouble.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'core rager EMOTE_LOWHP');
INSERT INTO `script_texts` VALUES ('-1409003', 'Reckless mortals, none may challenge the sons of the living flame!', null, null, null, null, null, null, null, null, '8035', '1', '0', '0', 'majordomo SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1409004', 'The runes of warding have been destroyed! Hunt down the infedels my bretheren.', null, null, null, null, null, null, null, null, '8039', '1', '0', '0', 'majordomo SAY_SPAWN');
INSERT INTO `script_texts` VALUES ('-1409005', 'Ashes to Ashes!', null, null, null, null, null, null, null, null, '8037', '1', '0', '0', 'majordomo SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1409006', 'Burn mortals! Burn for this transgression!', null, null, null, null, null, null, null, null, '8036', '1', '0', '0', 'majordomo SAY_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1409007', 'Impossible! Stay your attack mortals! I submitt! I submitt! Brashly you have come to rest the secrets of the living flame. You will soon regret the recklessness of your quest. I go now to summon the lord whos house this is. Should you seek an audiance with him your paltry lives will surly be forfit. Nevertheless seek out his lair if you dare!', null, null, null, null, null, null, null, null, '8038', '1', '0', '0', 'majordomo SAY_DEFEAT');
INSERT INTO `script_texts` VALUES ('-1409008', 'Behold Ragnaros, the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', null, null, null, null, null, null, null, null, '8040', '1', '0', '0', 'ragnaros SAY_SUMMON_MAJ');
INSERT INTO `script_texts` VALUES ('-1409009', 'TOO SOON! YOU HAVE AWAKENED ME TOO SOON, EXECUTUS! WHAT IS THE MEANING OF THIS INTRUSION?', null, null, null, null, null, null, null, null, '8043', '1', '0', '0', 'ragnaros SAY_ARRIVAL1_RAG');
INSERT INTO `script_texts` VALUES ('-1409010', 'These mortal infidels, my lord! They have invaded your sanctum, and seek to steal your secrets!', null, null, null, null, null, null, null, null, '8041', '1', '0', '0', 'ragnaros SAY_ARRIVAL2_MAJ');
INSERT INTO `script_texts` VALUES ('-1409011', 'FOOL! YOU ALLOWED THESE INSECTS TO RUN RAMPANT THROUGH THE HALLOWED CORE, AND NOW YOU LEAD THEM TO MY VERY LAIR? YOU HAVE FAILED ME, EXECUTUS! JUSTICE SHALL BE MET, INDEED!', null, null, null, null, null, null, null, null, '8044', '1', '0', '0', 'ragnaros SAY_ARRIVAL3_RAG');
INSERT INTO `script_texts` VALUES ('-1409012', 'NOW FOR YOU, INSECTS. BOLDLY YOU SAUGHT THE POWER OF RAGNAROS NOW YOU SHALL SEE IT FIRST HAND.', null, null, null, null, null, null, null, null, '8045', '1', '0', '0', 'ragnaros SAY_ARRIVAL5_RAG');
INSERT INTO `script_texts` VALUES ('-1409013', 'COME FORTH, MY SERVANTS! DEFEND YOUR MASTER!', null, null, null, null, null, null, null, null, '8049', '1', '0', '0', 'ragnaros SAY_REINFORCEMENTS1');
INSERT INTO `script_texts` VALUES ('-1409014', 'YOU CANNOT DEFEAT THE LIVING FLAME! COME YOU MINIONS OF FIRE! COME FORTH YOU CREATURES OF HATE! YOUR MASTER CALLS!', null, null, null, null, null, null, null, null, '8050', '1', '0', '0', 'ragnaros SAY_REINFORCEMENTS2');
INSERT INTO `script_texts` VALUES ('-1409015', 'BY FIRE BE PURGED!', null, null, null, null, null, null, null, null, '8046', '1', '0', '0', 'ragnaros SAY_HAND');
INSERT INTO `script_texts` VALUES ('-1409016', 'TASTE THE FLAMES OF SULFURON!', null, null, null, null, null, null, null, null, '8047', '1', '0', '0', 'ragnaros SAY_WRATH');
INSERT INTO `script_texts` VALUES ('-1409017', 'DIE INSECT!', null, null, null, null, null, null, null, null, '8051', '1', '0', '0', 'ragnaros SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1409018', 'MY PATIENCE IS DWINDILING! COME NATS TO YOUR DEATH!', null, null, null, null, null, null, null, null, '8048', '1', '0', '0', 'ragnaros SAY_MAGMABURST');
INSERT INTO `script_texts` VALUES ('-1469000', 'None of your kind should be here! You\'ve doomed only yourselves!', null, null, null, null, null, null, null, null, '8286', '1', '0', '0', 'broodlord SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1469001', 'Clever Mortals but I am not so easily lured away from my sanctum!', null, null, null, null, null, null, null, null, '8287', '1', '0', '0', 'broodlord SAY_LEASH');
INSERT INTO `script_texts` VALUES ('-1469002', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1469003', '%s flinches as its skin shimmers.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'chromaggus EMOTE_SHIMMER');
INSERT INTO `script_texts` VALUES ('-1469004', 'In this world where time is your enemy, it is my greatest ally. This grand game of life that you think you play in fact plays you. To that I say...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'victor_nefarius SAY_GAMESBEGIN_1');
INSERT INTO `script_texts` VALUES ('-1469005', 'Let the games begin!', null, null, null, null, null, null, null, null, '8280', '1', '0', '0', 'victor_nefarius SAY_GAMESBEGIN_2');
INSERT INTO `script_texts` VALUES ('-1469006', 'Ah, the heroes. You are persistent, aren\'t you. Your allied attempted to match his power against mine, and had to pay the price. Now he shall serve me, by slaughtering you. Get up little red wyrm and destroy them!', null, null, null, null, null, null, null, null, '8279', '1', '0', '0', 'victor_nefarius SAY_VAEL_INTRO');
INSERT INTO `script_texts` VALUES ('-1469007', 'Well done, my minions. The mortals\' courage begins to wane! Now, let\'s see how they contend with the true Lord of Blackrock Spire!', null, null, null, null, null, null, null, null, '8288', '1', '0', '0', 'nefarian SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1469008', 'Enough! Now you vermin shall feel the force of my birthright, the fury of the earth itself.', null, null, null, null, null, null, null, null, '8289', '1', '0', '0', 'nefarian SAY_XHEALTH');
INSERT INTO `script_texts` VALUES ('-1469009', 'Burn, you wretches! Burn!', null, null, null, null, null, null, null, null, '8290', '1', '0', '0', 'nefarian SAY_SHADOWFLAME');
INSERT INTO `script_texts` VALUES ('-1469010', 'Impossible! Rise my minions! Serve your master once more!', null, null, null, null, null, null, null, null, '8291', '1', '0', '0', 'nefarian SAY_RAISE_SKELETONS');
INSERT INTO `script_texts` VALUES ('-1469011', 'Worthless $N! Your friends will join you soon enough!', null, null, null, null, null, null, null, null, '8293', '1', '0', '0', 'nefarian SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1469012', 'This cannot be! I am the Master here! You mortals are nothing to my kind! DO YOU HEAR? NOTHING!', null, null, null, null, null, null, null, null, '8292', '1', '0', '0', 'nefarian SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1469013', 'Mages too? You should be more careful when you play with magic...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_MAGE');
INSERT INTO `script_texts` VALUES ('-1469014', 'Warriors, I know you can hit harder than that! Let\'s see it!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_WARRIOR');
INSERT INTO `script_texts` VALUES ('-1469015', 'Druids and your silly shapeshifting. Let\'s see it in action!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_DRUID');
INSERT INTO `script_texts` VALUES ('-1469016', 'Priests! If you\'re going to keep healing like that, we might as well make it a little more interesting!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_PRIEST');
INSERT INTO `script_texts` VALUES ('-1469017', 'Paladins, I\'ve heard you have many lives. Show me.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_PALADIN');
INSERT INTO `script_texts` VALUES ('-1469018', 'Shamans, show me what your totems can do!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_SHAMAN');
INSERT INTO `script_texts` VALUES ('-1469019', 'Warlocks, you shouldn\'t be playing with magic you don\'t understand. See what happens?', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_WARLOCK');
INSERT INTO `script_texts` VALUES ('-1469020', 'Hunters and your annoying pea-shooters!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_HUNTER');
INSERT INTO `script_texts` VALUES ('-1469021', 'Rogues? Stop hiding and face me!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_ROGUE');
INSERT INTO `script_texts` VALUES ('-1469022', 'You\'ll pay for forcing me to do this.', null, null, null, null, null, null, null, null, '8275', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN1');
INSERT INTO `script_texts` VALUES ('-1469023', 'Fools! These eggs are more precious than you know.', null, null, null, null, null, null, null, null, '8276', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN2');
INSERT INTO `script_texts` VALUES ('-1469024', 'No! Not another one! I\'ll have your heads for this atrocity.', null, null, null, null, null, null, null, null, '8277', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN3');
INSERT INTO `script_texts` VALUES ('-1469025', 'If I fall into the abyss I\'ll take all of you mortals with me...', null, null, null, null, null, null, null, null, '8278', '1', '0', '0', 'razorgore SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1469026', 'Too late...friends. Nefarius\' corruption has taken hold. I cannot...control myself.', null, null, null, null, null, null, null, null, '8281', '1', '0', '0', 'vaelastrasz SAY_LINE1');
INSERT INTO `script_texts` VALUES ('-1469027', 'I beg you Mortals, flee! Flee before I lose all control. The Black Fire rages within my heart. I must release it!', null, null, null, null, null, null, null, null, '8282', '1', '0', '0', 'vaelastrasz SAY_LINE2');
INSERT INTO `script_texts` VALUES ('-1469028', 'FLAME! DEATH! DESTRUCTION! COWER MORTALS BEFORE THE WRATH OF LORD....NO! I MUST FIGHT THIS!', null, null, null, null, null, null, null, null, '8283', '1', '0', '0', 'vaelastrasz SAY_LINE3');
INSERT INTO `script_texts` VALUES ('-1469029', 'Nefarius\' hate has made me stronger than ever before. You should have fled, while you could, mortals! The fury of Blackrock courses through my veins!', null, null, null, null, null, null, null, null, '8285', '1', '0', '0', 'vaelastrasz SAY_HALFLIFE');
INSERT INTO `script_texts` VALUES ('-1469030', 'Forgive me $N, your death only adds to my failure.', null, null, null, null, null, null, null, null, '8284', '1', '0', '0', 'vaelastrasz SAY_KILLTARGET');
INSERT INTO `script_texts` VALUES ('-1469031', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1509000', '%s senses your fear.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'moam EMOTE_AGGRO');
INSERT INTO `script_texts` VALUES ('-1509001', '%s bristles with energy!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'moan EMOTE_MANA_FULL');
INSERT INTO `script_texts` VALUES ('-1509002', '%s sets eyes on $N!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'buru EMOTE_TARGET');
INSERT INTO `script_texts` VALUES ('-1509003', 'They come now. Try not to get yourself killed, young blood.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'andorov SAY_ANDOROV_INTRO');
INSERT INTO `script_texts` VALUES ('-1509004', 'Remember, Rajaxx, when I said I\'d kill you last? I lied...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'andorov SAY_ANDOROV_ATTACK');
INSERT INTO `script_texts` VALUES ('-1509005', 'The time of our retribution is at hand! Let darkness reign in the hearts of our enemies!', null, null, null, null, null, null, null, null, '8612', '1', '0', '0', 'rajaxx SAY_WAVE3');
INSERT INTO `script_texts` VALUES ('-1509006', 'No longer will we wait behind barred doors and walls of stone! No longer will our vengeance be denied! The dragons themselves will tremble before our wrath!', null, null, null, null, null, null, null, null, '8610', '1', '0', '0', 'rajaxx SAY_WAVE4');
INSERT INTO `script_texts` VALUES ('-1509007', 'Fear is for the enemy! Fear and death!', null, null, null, null, null, null, null, null, '8608', '1', '0', '0', 'rajaxx SAY_WAVE5');
INSERT INTO `script_texts` VALUES ('-1509008', 'Staghelm will whimper and beg for his life, just as his whelp of a son did! One thousand years of injustice will end this day!', null, null, null, null, null, null, null, null, '8611', '1', '0', '0', 'rajaxx SAY_WAVE6');
INSERT INTO `script_texts` VALUES ('-1509009', 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!', null, null, null, null, null, null, null, null, '8607', '1', '0', '0', 'rajaxx SAY_WAVE7');
INSERT INTO `script_texts` VALUES ('-1509010', 'Impudent fool! I will kill you myself!', null, null, null, null, null, null, null, null, '8609', '1', '0', '0', 'rajaxx SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1509011', 'Attack and make them pay dearly!', null, null, null, null, null, null, null, null, '8603', '1', '0', '0', 'rajaxx SAY_UNK1');
INSERT INTO `script_texts` VALUES ('-1509012', 'Crush them! Drive them out!', null, null, null, null, null, null, null, null, '8605', '1', '0', '0', 'rajaxx SAY_UNK2');
INSERT INTO `script_texts` VALUES ('-1509013', 'Do not hesitate! Destroy them!', null, null, null, null, null, null, null, null, '8606', '1', '0', '0', 'rajaxx SAY_UNK3');
INSERT INTO `script_texts` VALUES ('-1509014', 'Warriors! Captains! Continue the fight!', null, null, null, null, null, null, null, null, '8613', '1', '0', '0', 'rajaxx SAY_UNK4');
INSERT INTO `script_texts` VALUES ('-1509015', 'You are not worth my time $N!', null, null, null, null, null, null, null, null, '8614', '1', '0', '0', 'rajaxx SAY_DEAGGRO');
INSERT INTO `script_texts` VALUES ('-1509016', 'Breath your last!', null, null, null, null, null, null, null, null, '8604', '1', '0', '0', 'rajaxx SAY_KILLS_ANDOROV');
INSERT INTO `script_texts` VALUES ('-1509017', 'Soon you will know the price of your meddling, mortals... The master is nearly whole... And when he rises, your world will be cease!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'rajaxx SAY_COMPLETE_QUEST');
INSERT INTO `script_texts` VALUES ('-1509018', 'I am rejuvinated!', null, null, null, null, null, null, null, null, '8593', '1', '0', '0', 'ossirian SAY_SURPREME1');
INSERT INTO `script_texts` VALUES ('-1509019', 'My powers are renewed!', null, null, null, null, null, null, null, null, '8595', '1', '0', '0', 'ossirian SAY_SURPREME2');
INSERT INTO `script_texts` VALUES ('-1509020', 'My powers return!', null, null, null, null, null, null, null, null, '8596', '1', '0', '0', 'ossirian SAY_SURPREME3');
INSERT INTO `script_texts` VALUES ('-1509021', 'Protect the city at all costs!', null, null, null, null, null, null, null, null, '8597', '1', '0', '0', 'ossirian SAY_RAND_INTRO1');
INSERT INTO `script_texts` VALUES ('-1509022', 'The walls have been breached!', null, null, null, null, null, null, null, null, '8599', '1', '0', '0', 'ossirian SAY_RAND_INTRO2');
INSERT INTO `script_texts` VALUES ('-1509023', 'To your posts. Defend the city.', null, null, null, null, null, null, null, null, '8600', '1', '0', '0', 'ossirian SAY_RAND_INTRO3');
INSERT INTO `script_texts` VALUES ('-1509024', 'Tresspassers will be terminated.', null, null, null, null, null, null, null, null, '8601', '1', '0', '0', 'ossirian SAY_RAND_INTRO4');
INSERT INTO `script_texts` VALUES ('-1509025', 'Sands of the desert rise and block out the sun!', null, null, null, null, null, null, null, null, '8598', '1', '0', '0', 'ossirian SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1509026', 'You are terminated.', null, null, null, null, null, null, null, null, '8602', '1', '0', '0', 'ossirian SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1509027', 'I...have...failed.', null, null, null, null, null, null, null, null, '8594', '1', '0', '0', 'ossirian SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1531000', 'Are you so eager to die? I would be happy to accomodate you.', null, null, null, null, null, null, null, null, '8615', '1', '0', '0', 'skeram SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1531001', 'Cower mortals! The age of darkness is at hand.', null, null, null, null, null, null, null, null, '8616', '1', '0', '0', 'skeram SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1531002', 'Tremble! The end is upon you.', null, null, null, null, null, null, null, null, '8621', '1', '0', '0', 'skeram SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1531003', 'Let your death serve as an example!', null, null, null, null, null, null, null, null, '8617', '1', '0', '0', 'skeram SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1531004', 'Spineless wretches! You will drown in rivers of blood!', null, null, null, null, null, null, null, null, '8619', '1', '0', '0', 'skeram SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1531005', 'The screams of the dying will fill the air. A symphony of terror is about to begin!', null, null, null, null, null, null, null, null, '8620', '1', '0', '0', 'skeram SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1531006', 'Prepare for the return of the ancient ones!', null, null, null, null, null, null, null, null, '8618', '1', '0', '0', 'skeram SAY_SPLIT');
INSERT INTO `script_texts` VALUES ('-1531007', 'You only delay... the inevetable.', null, null, null, null, null, null, null, null, '8622', '1', '0', '0', 'skeram SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1531008', 'You will be judged for defiling these sacred grounds! The laws of the Ancients will not be challenged! Trespassers will be annihilated!', null, null, null, null, null, null, null, null, '8646', '1', '0', '0', 'sartura SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1531009', 'I sentence you to death!', null, null, null, null, null, null, null, null, '8647', '1', '0', '0', 'sartura SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1531010', 'I serve to the last!', null, null, null, null, null, null, null, null, '8648', '1', '0', '0', 'sartura SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1531011', '%s is weakened!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'cthun EMOTE_WEAKENED');
INSERT INTO `script_texts` VALUES ('-1532000', 'Well done Midnight!', null, null, null, null, null, null, null, null, '9173', '1', '0', '0', 'attumen SAY_MIDNIGHT_KILL');
INSERT INTO `script_texts` VALUES ('-1532001', 'Cowards! Wretches!', null, null, null, null, null, null, null, null, '9167', '1', '0', '0', 'attumen SAY_APPEAR1');
INSERT INTO `script_texts` VALUES ('-1532002', 'Who dares attack the steed of the Huntsman?', null, null, null, null, null, null, null, null, '9298', '1', '0', '0', 'attumen SAY_APPEAR2');
INSERT INTO `script_texts` VALUES ('-1532003', 'Perhaps you would rather test yourselves against a more formidable opponent?!', null, null, null, null, null, null, null, null, '9299', '1', '0', '0', 'attumen SAY_APPEAR3');
INSERT INTO `script_texts` VALUES ('-1532004', 'Come, Midnight, let\'s disperse this petty rabble!', null, null, null, null, null, null, null, null, '9168', '1', '0', '0', 'attumen SAY_MOUNT');
INSERT INTO `script_texts` VALUES ('-1532005', 'It was... inevitable.', null, null, null, null, null, null, null, null, '9169', '1', '0', '0', 'attumen SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1532006', 'Another trophy to add to my collection!', null, null, null, null, null, null, null, null, '9300', '1', '0', '0', 'attumen SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1532007', 'Weapons are merely a convenience for a warrior of my skill!', null, null, null, null, null, null, null, null, '9166', '1', '0', '0', 'attumen SAY_DISARMED');
INSERT INTO `script_texts` VALUES ('-1532008', 'I always knew... someday I would become... the hunted.', null, null, null, null, null, null, null, null, '9165', '1', '0', '0', 'attumen SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1532009', 'Such easy sport.', null, null, null, null, null, null, null, null, '9170', '1', '0', '0', 'attumen SAY_RANDOM1');
INSERT INTO `script_texts` VALUES ('-1532010', 'Amateurs! Do not think you can best me! I kill for a living.', null, null, null, null, null, null, null, null, '9304', '1', '0', '0', 'attumen SAY_RANDOM2');
INSERT INTO `script_texts` VALUES ('-1532011', 'Hmm, unannounced visitors? Preparations must be made.', null, null, null, null, null, null, null, null, '9211', '1', '0', '0', 'moroes SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532012', 'Now, where was I? Oh yes...', null, null, null, null, null, null, null, null, '9215', '1', '0', '0', 'moroes SAY_SPECIAL_1');
INSERT INTO `script_texts` VALUES ('-1532013', 'You rang?', null, null, null, null, null, null, null, null, '9316', '1', '0', '0', 'moroes SAY_SPECIAL_2');
INSERT INTO `script_texts` VALUES ('-1532014', 'One more for dinner this evening.', null, null, null, null, null, null, null, null, '9214', '1', '0', '0', 'moroes SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1532015', 'Time... Never enough time.', null, null, null, null, null, null, null, null, '9314', '1', '0', '0', 'moroes SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1532016', 'I\'ve gone and made a mess.', null, null, null, null, null, null, null, null, '9315', '1', '0', '0', 'moroes SAY_KILL_3');
INSERT INTO `script_texts` VALUES ('-1532017', 'How terribly clumsy of me...', null, null, null, null, null, null, null, null, '9213', '1', '0', '0', 'moroes SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1532018', 'Your behavior will not be tolerated!', null, null, null, null, null, null, null, null, '9204', '1', '0', '0', 'maiden SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532019', 'Ah ah ah...', null, null, null, null, null, null, null, null, '9207', '1', '0', '0', 'maiden SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1532020', 'This is for the best.', null, null, null, null, null, null, null, null, '9312', '1', '0', '0', 'maiden SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1532021', 'Impure thoughts lead to profane actions.', null, null, null, null, null, null, null, null, '9311', '1', '0', '0', 'maiden SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1532022', 'Cast out your corrupt thoughts.', null, null, null, null, null, null, null, null, '9313', '1', '0', '0', 'maiden SAY_REPENTANCE1');
INSERT INTO `script_texts` VALUES ('-1532023', 'Your impurity must be cleansed.', null, null, null, null, null, null, null, null, '9208', '1', '0', '0', 'maiden SAY_REPENTANCE2');
INSERT INTO `script_texts` VALUES ('-1532024', 'Death comes. Will your conscience be clear?', null, null, null, null, null, null, null, null, '9206', '1', '0', '0', 'maiden SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1532025', 'Oh at last, at last. I can go home.', null, null, null, null, null, null, null, null, '9190', '1', '0', '0', 'dorothee SAY_DOROTHEE_DEATH');
INSERT INTO `script_texts` VALUES ('-1532026', 'Don\'t let them hurt us, Tito! Oh, you won\'t, will you?', null, null, null, null, null, null, null, null, '9191', '1', '0', '0', 'dorothee SAY_DOROTHEE_SUMMON');
INSERT INTO `script_texts` VALUES ('-1532027', 'Tito, oh Tito, no!', null, null, null, null, null, null, null, null, '9192', '1', '0', '0', 'dorothee SAY_DOROTHEE_TITO_DEATH');
INSERT INTO `script_texts` VALUES ('-1532028', 'Oh dear, we simply must find a way home! The old wizard could be our only hope! Strawman, Roar, Tinhead, will you... wait! Oh golly, look! We have visitors!', null, null, null, null, null, null, null, null, '9195', '1', '0', '0', 'dorothee SAY_DOROTHEE_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532029', 'Wanna fight? Huh? Do ya? C\'mon, I\'ll fight you with both claws behind my back!', null, null, null, null, null, null, null, null, '9227', '1', '0', '0', 'roar SAY_ROAR_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532030', 'You didn\'t have to go and do that.', null, null, null, null, null, null, null, null, '9229', '1', '0', '0', 'roar SAY_ROAR_DEATH');
INSERT INTO `script_texts` VALUES ('-1532031', 'I think I\'m going to go take fourty winks.', null, null, null, null, null, null, null, null, '9230', '1', '0', '0', 'roar SAY_ROAR_SLAY');
INSERT INTO `script_texts` VALUES ('-1532032', 'Now what should I do with you? I simply can\'t make up my mind.', null, null, null, null, null, null, null, null, '9254', '1', '0', '0', 'strawman SAY_STRAWMAN_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532033', 'Don\'t let them make a mattress... out of me.', null, null, null, null, null, null, null, null, '9256', '1', '0', '0', 'strawman SAY_STRAWMAN_DEATH');
INSERT INTO `script_texts` VALUES ('-1532034', 'I guess I\'m not a failure after all.', null, null, null, null, null, null, null, null, '9257', '1', '0', '0', 'strawman SAY_STRAWMAN_SLAY');
INSERT INTO `script_texts` VALUES ('-1532035', 'I could really use a heart. Say, can I have yours?', null, null, null, null, null, null, null, null, '9268', '1', '0', '0', 'tinhead SAY_TINHEAD_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532036', 'Back to being an old rustbucket.', null, null, null, null, null, null, null, null, '9270', '1', '0', '0', 'tinhead SAY_TINHEAD_DEATH');
INSERT INTO `script_texts` VALUES ('-1532037', 'Guess I\'m not so rusty, after all.', null, null, null, null, null, null, null, null, '9271', '1', '0', '0', 'tinhead SAY_TINHEAD_SLAY');
INSERT INTO `script_texts` VALUES ('-1532038', '%s begins to rust.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'tinhead EMOTE_RUST');
INSERT INTO `script_texts` VALUES ('-1532039', 'Woe to each and every one of you my pretties! <cackles>', null, null, null, null, null, null, null, null, '9179', '1', '0', '0', 'crone SAY_CRONE_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532040', 'It will all be over soon! <cackles>', null, null, null, null, null, null, null, null, '9307', '1', '0', '0', 'crone SAY_CRONE_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1532041', 'How could you? What a cruel, cruel world!', null, null, null, null, null, null, null, null, '9178', '1', '0', '0', 'crone SAY_CRONE_DEATH');
INSERT INTO `script_texts` VALUES ('-1532042', 'Fixed you, didn\'t I? <cackles>', null, null, null, null, null, null, null, null, '9180', '1', '0', '0', 'crone SAY_CRONE_SLAY');
INSERT INTO `script_texts` VALUES ('-1532043', 'All the better to own you with!', null, null, null, null, null, null, null, null, '9276', '1', '0', '0', 'wolf SAY_WOLF_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532044', 'Mmmm... delicious.', null, null, null, null, null, null, null, null, '9277', '1', '0', '0', 'wolf SAY_WOLF_SLAY');
INSERT INTO `script_texts` VALUES ('-1532045', 'Run away little girl, run away!', null, null, null, null, null, null, null, null, '9278', '1', '0', '0', 'wolf SAY_WOLF_HOOD');
INSERT INTO `script_texts` VALUES ('-1532046', 'What devil art thou, that dost torment me thus?', null, null, null, null, null, null, null, null, '9196', '1', '0', '0', 'julianne SAY_JULIANNE_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532047', 'Where is my lord? Where is my Romulo?', null, null, null, null, null, null, null, null, '9199', '1', '0', '0', 'julianne SAY_JULIANNE_ENTER');
INSERT INTO `script_texts` VALUES ('-1532048', 'Romulo, I come! Oh... this do I drink to thee!', null, null, null, null, null, null, null, null, '9198', '1', '0', '0', 'julianne SAY_JULIANNE_DEATH01');
INSERT INTO `script_texts` VALUES ('-1532049', 'Where is my Lord? Where is my Romulo? Ohh, happy dagger! This is thy sheath! There rust, and let me die!', null, null, null, null, null, null, null, null, '9310', '1', '0', '0', 'julianne SAY_JULIANNE_DEATH02');
INSERT INTO `script_texts` VALUES ('-1532050', 'Come, gentle night; and give me back my Romulo!', null, null, null, null, null, null, null, null, '9200', '1', '0', '0', 'julianne SAY_JULIANNE_RESURRECT');
INSERT INTO `script_texts` VALUES ('-1532051', 'Parting is such sweet sorrow.', null, null, null, null, null, null, null, null, '9201', '1', '0', '0', 'julianne SAY_JULIANNE_SLAY');
INSERT INTO `script_texts` VALUES ('-1532052', 'Wilt thou provoke me? Then have at thee, boy!', null, null, null, null, null, null, null, null, '9233', '1', '0', '0', 'romulo SAY_ROMULO_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532053', 'Thou smilest... upon the stroke that... murders me.', null, null, null, null, null, null, null, null, '9235', '1', '0', '0', 'romulo SAY_ROMULO_DEATH');
INSERT INTO `script_texts` VALUES ('-1532054', 'This day\'s black fate on more days doth depend. This but begins the woe. Others must end.', null, null, null, null, null, null, null, null, '9236', '1', '0', '0', 'romulo SAY_ROMULO_ENTER');
INSERT INTO `script_texts` VALUES ('-1532055', 'Thou detestable maw, thou womb of death; I enforce thy rotten jaws to open!', null, null, null, null, null, null, null, null, '9237', '1', '0', '0', 'romulo SAY_ROMULO_RESURRECT');
INSERT INTO `script_texts` VALUES ('-1532056', 'How well my comfort is revived by this!', null, null, null, null, null, null, null, null, '9238', '1', '0', '0', 'romulo SAY_ROMULO_SLAY');
INSERT INTO `script_texts` VALUES ('-1532057', 'The Menagerie is for guests only.', null, null, null, null, null, null, null, null, '9183', '1', '0', '0', 'curator SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532058', 'Gallery rules will be strictly enforced.', null, null, null, null, null, null, null, null, '9188', '1', '0', '0', 'curator SAY_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1532059', 'This curator is equipped for gallery protection.', null, null, null, null, null, null, null, null, '9309', '1', '0', '0', 'curator SAY_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1532060', 'Your request cannot be processed.', null, null, null, null, null, null, null, null, '9186', '1', '0', '0', 'curator SAY_EVOCATE');
INSERT INTO `script_texts` VALUES ('-1532061', 'Failure to comply will result in offensive action.', null, null, null, null, null, null, null, null, '9185', '1', '0', '0', 'curator SAY_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1532062', 'Do not touch the displays.', null, null, null, null, null, null, null, null, '9187', '1', '0', '0', 'curator SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1532063', 'You are not a guest.', null, null, null, null, null, null, null, null, '9308', '1', '0', '0', 'curator SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1532064', 'This Curator is no longer op... er... ation... al.', null, null, null, null, null, null, null, null, '9184', '1', '0', '0', 'curator SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1532065', 'Your blood will anoint my circle.', null, null, null, null, null, null, null, null, '9264', '1', '0', '0', 'terestian SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1532066', 'The great one will be pleased.', null, null, null, null, null, null, null, null, '9329', '1', '0', '0', 'terestian SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1532067', 'My life, is yours. Oh great one.', null, null, null, null, null, null, null, null, '9262', '1', '0', '0', 'terestian SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1532068', 'Ah, you\'re just in time. The rituals are about to begin.', null, null, null, null, null, null, null, null, '9260', '1', '0', '0', 'terestian SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532069', 'Please, accept this humble offering, oh great one.', null, null, null, null, null, null, null, null, '9263', '1', '0', '0', 'terestian SAY_SACRIFICE1');
INSERT INTO `script_texts` VALUES ('-1532070', 'Let the sacrifice serve his testament to my fealty.', null, null, null, null, null, null, null, null, '9330', '1', '0', '0', 'terestian SAY_SACRIFICE2');
INSERT INTO `script_texts` VALUES ('-1532071', 'Come, you dwellers in the dark. Rally to my call!', null, null, null, null, null, null, null, null, '9265', '1', '0', '0', 'terestian SAY_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1532072', 'Gather, my pets. There is plenty for all.', null, null, null, null, null, null, null, null, '9331', '1', '0', '0', 'terestian SAY_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1532073', 'Please, no more. My son... he\'s gone mad!', null, null, null, null, null, null, null, null, '9241', '1', '0', '0', 'aran SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1532074', 'I\'ll not be tortured again!', null, null, null, null, null, null, null, null, '9323', '1', '0', '0', 'aran SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1532075', 'Who are you? What do you want? Stay away from me!', null, null, null, null, null, null, null, null, '9324', '1', '0', '0', 'aran SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1532076', 'I\'ll show you this beaten dog still has some teeth!', null, null, null, null, null, null, null, null, '9245', '1', '0', '0', 'aran SAY_FLAMEWREATH1');
INSERT INTO `script_texts` VALUES ('-1532077', 'Burn you hellish fiends!', null, null, null, null, null, null, null, null, '9326', '1', '0', '0', 'aran SAY_FLAMEWREATH2');
INSERT INTO `script_texts` VALUES ('-1532078', 'I\'ll freeze you all!', null, null, null, null, null, null, null, null, '9246', '1', '0', '0', 'aran SAY_BLIZZARD1');
INSERT INTO `script_texts` VALUES ('-1532079', 'Back to the cold dark with you!', null, null, null, null, null, null, null, null, '9327', '1', '0', '0', 'aran SAY_BLIZZARD2');
INSERT INTO `script_texts` VALUES ('-1532080', 'Yes, yes, my son is quite powerful... but I have powers of my own!', null, null, null, null, null, null, null, null, '9242', '1', '0', '0', 'aran SAY_EXPLOSION1');
INSERT INTO `script_texts` VALUES ('-1532081', 'I am not some simple jester! I am Nielas Aran!', null, null, null, null, null, null, null, null, '9325', '1', '0', '0', 'aran SAY_EXPLOSION2');
INSERT INTO `script_texts` VALUES ('-1532082', 'Surely you would not deny an old man a replenishing drink? No, no I thought not.', null, null, null, null, null, null, null, null, '9248', '1', '0', '0', 'aran SAY_DRINK');
INSERT INTO `script_texts` VALUES ('-1532083', 'I\'m not finished yet! No, I have a few more tricks up me sleeve.', null, null, null, null, null, null, null, null, '9251', '1', '0', '0', 'aran SAY_ELEMENTALS');
INSERT INTO `script_texts` VALUES ('-1532084', 'I want this nightmare to be over!', null, null, null, null, null, null, null, null, '9250', '1', '0', '0', 'aran SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1532085', 'Torment me no more!', null, null, null, null, null, null, null, null, '9328', '1', '0', '0', 'aran SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1532086', 'You\'ve wasted enough of my time. Let these games be finished!', null, null, null, null, null, null, null, null, '9247', '1', '0', '0', 'aran SAY_TIMEOVER');
INSERT INTO `script_texts` VALUES ('-1532087', 'At last... The nightmare is.. over...', null, null, null, null, null, null, null, null, '9244', '1', '0', '0', 'aran SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1532088', 'Where did you get that?! Did HE send you?!', null, null, null, null, null, null, null, null, '9249', '1', '0', '0', 'aran SAY_ATIESH');
INSERT INTO `script_texts` VALUES ('-1532089', '%s cries out in withdrawal, opening gates to the warp.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'netherspite EMOTE_PHASE_PORTAL');
INSERT INTO `script_texts` VALUES ('-1532090', '%s goes into a nether-fed rage!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'netherspite EMOTE_PHASE_BANISH');
INSERT INTO `script_texts` VALUES ('-1532091', 'Madness has brought you here to me. I shall be your undoing!', null, null, null, null, null, null, null, null, '9218', '1', '0', '0', 'malchezaar SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1532092', 'Simple fools! Time is the fire in which you\'ll burn!', null, null, null, null, null, null, null, null, '9220', '1', '0', '0', 'malchezaar SAY_AXE_TOSS1');
INSERT INTO `script_texts` VALUES ('-1532093', 'I see the subtlety of conception is beyond primitives such as you.', null, null, null, null, null, null, null, null, '9317', '1', '0', '0', 'malchezaar SAY_AXE_TOSS2');
INSERT INTO `script_texts` VALUES ('-1532094', 'Who knows what secrets hide in the dark.', null, null, null, null, null, null, null, null, '9223', '1', '0', '0', 'malchezaar SAY_SPECIAL1');
INSERT INTO `script_texts` VALUES ('-1532095', 'The cerestial forces are mine to manipulate.', null, null, null, null, null, null, null, null, '9320', '1', '0', '0', 'malchezaar SAY_SPECIAL2');
INSERT INTO `script_texts` VALUES ('-1532096', 'How can you hope to withstand against such overwhelming power?', null, null, null, null, null, null, null, null, '9321', '1', '0', '0', 'malchezaar SAY_SPECIAL3');
INSERT INTO `script_texts` VALUES ('-1532097', 'Surely you did not think you could win.', null, null, null, null, null, null, null, null, '9222', '1', '0', '0', 'malchezaar SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1532098', 'Your greed, your foolishness has brought you to this end.', null, null, null, null, null, null, null, null, '9318', '1', '0', '0', 'malchezaar SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1532099', 'You are, but a plaything, unfit even to amuse.', null, null, null, null, null, null, null, null, '9319', '1', '0', '0', 'malchezaar SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1532100', 'All realities, all dimensions are open to me!', null, null, null, null, null, null, null, null, '9224', '1', '0', '0', 'malchezaar SAY_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1532101', 'You face not Malchezaar alone, but the legions I command!', null, null, null, null, null, null, null, null, '9322', '1', '0', '0', 'malchezaar SAY_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1532102', 'I refuse to concede defeat. I am a prince of the Eredar! I am...', null, null, null, null, null, null, null, null, '9221', '1', '0', '0', 'malchezaar SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1532103', 'Welcome Ladies and Gentlemen, to this evening\'s presentation!', null, null, null, null, null, null, null, null, '9174', '1', '0', '0', 'barnes OZ1');
INSERT INTO `script_texts` VALUES ('-1532104', 'Tonight we plumb the depths of the human soul as we join a lost, lonely girl trying desperately -- with the help of her loyal companions -- to find her way home!', null, null, null, null, null, null, null, null, '9338', '1', '0', '0', 'barnes OZ2');
INSERT INTO `script_texts` VALUES ('-1532105', 'But she is pursued... by a wicked malevolent crone!', null, null, null, null, null, null, null, null, '9339', '1', '0', '0', 'barnes OZ3');
INSERT INTO `script_texts` VALUES ('-1532106', 'Will she survive? Will she prevail? Only time will tell. And now ... on with the show!', null, null, null, null, null, null, null, null, '9340', '1', '0', '0', 'barnes OZ4');
INSERT INTO `script_texts` VALUES ('-1532107', 'Good evening, Ladies and Gentlemen! Welcome to this evening\'s presentation!', null, null, null, null, null, null, null, null, '9175', '1', '0', '0', 'barnes HOOD1');
INSERT INTO `script_texts` VALUES ('-1532108', 'Tonight, things are not what they seem. For tonight, your eyes may not be trusted', null, null, null, null, null, null, null, null, '9335', '1', '0', '0', 'barnes HOOD2');
INSERT INTO `script_texts` VALUES ('-1532109', 'Take for instance, this quiet, elderly woman, waiting for a visit from her granddaughter. Surely there is nothing to fear from this sweet, grey-haired, old lady.', null, null, null, null, null, null, null, null, '9336', '1', '0', '0', 'barnes HOOD3');
INSERT INTO `script_texts` VALUES ('-1532110', 'But don\'t let me pull the wool over your eyes. See for yourself what lies beneath those covers! And now... on with the show!', null, null, null, null, null, null, null, null, '9337', '1', '0', '0', 'barnes HOOD4');
INSERT INTO `script_texts` VALUES ('-1532111', 'Welcome, Ladies and Gentlemen, to this evening\'s presentation!', null, null, null, null, null, null, null, null, '9176', '1', '0', '0', 'barnes RAJ1');
INSERT INTO `script_texts` VALUES ('-1532112', 'Tonight, we explore a tale of forbidden love!', null, null, null, null, null, null, null, null, '9341', '1', '0', '0', 'barnes RAJ2');
INSERT INTO `script_texts` VALUES ('-1532113', 'But beware, for not all love stories end happily, as you may find out. Sometimes, love pricks like a thorn.', null, null, null, null, null, null, null, null, '9342', '1', '0', '0', 'barnes RAJ3');
INSERT INTO `script_texts` VALUES ('-1532114', 'But don\'t take it from me, see for yourself what tragedy lies ahead when the paths of star-crossed lovers meet. And now...on with the show!', null, null, null, null, null, null, null, null, '9343', '1', '0', '0', 'barnes RAJ4');
INSERT INTO `script_texts` VALUES ('-1533000', 'Ahh... welcome to my parlor.', null, null, null, null, null, null, null, null, '8788', '1', '0', '0', 'anubrekhan SAY_GREET');
INSERT INTO `script_texts` VALUES ('-1533001', 'Just a little taste...', null, null, null, null, null, null, null, null, '8785', '1', '0', '0', 'anubrekhan SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533002', 'There is no way out.', null, null, null, null, null, null, null, null, '8786', '1', '0', '0', 'anubrekhan SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533003', 'Yes, Run! It makes the blood pump faster!', null, null, null, null, null, null, null, null, '8787', '1', '0', '0', 'anubrekhan SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533004', 'I hear little hearts beating. Yesss... beating faster now. Soon the beating will stop.', null, null, null, null, null, null, null, null, '8790', '1', '0', '0', 'anubrekhan SAY_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533005', 'Where to go? What to do? So many choices that all end in pain, end in death.', null, null, null, null, null, null, null, null, '8791', '1', '0', '0', 'anubrekhan SAY_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533006', 'Which one shall I eat first? So difficult to choose... the all smell so delicious.', null, null, null, null, null, null, null, null, '8792', '1', '0', '0', 'anubrekhan SAY_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533007', 'Closer now... tasty morsels. I\'ve been too long without food. Without blood to drink.', null, null, null, null, null, null, null, null, '8793', '1', '0', '0', 'anubrekhan SAY_TAUNT4');
INSERT INTO `script_texts` VALUES ('-1533008', 'Shh... it will all be over soon.', null, null, null, null, null, null, null, null, '8789', '1', '0', '0', 'anubrekhan SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1533009', 'Your old lives, your mortal desires, mean nothing. You are acolytes of the master now, and you will serve the cause without question! The greatest glory is to die in the master\'s service!', null, null, null, null, null, null, null, null, '8799', '1', '0', '0', 'faerlina SAY_GREET');
INSERT INTO `script_texts` VALUES ('-1533010', 'Slay them in the master\'s name!', null, null, null, null, null, null, null, null, '8794', '1', '0', '0', 'faerlina SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533011', 'You cannot hide from me!', null, null, null, null, null, null, null, null, '8795', '1', '0', '0', 'faerlina SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533012', 'Kneel before me, worm!', null, null, null, null, null, null, null, null, '8796', '1', '0', '0', 'faerlina SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533013', 'Run while you still can!', null, null, null, null, null, null, null, null, '8797', '1', '0', '0', 'faerlina SAY_AGGRO4');
INSERT INTO `script_texts` VALUES ('-1533014', 'You have failed!', null, null, null, null, null, null, null, null, '8800', '1', '0', '0', 'faerlina SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533015', 'Pathetic wretch!', null, null, null, null, null, null, null, null, '8801', '1', '0', '0', 'faerlina SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533016', 'The master... will avenge me!', null, null, null, null, null, null, null, null, '8798', '1', '0', '0', 'faerlina SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533017', 'Patchwerk want to play!', null, null, null, null, null, null, null, null, '8909', '1', '0', '0', 'patchwerk SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533018', 'Kel\'Thuzad make Patchwerk his Avatar of War!', null, null, null, null, null, null, null, null, '8910', '1', '0', '0', 'patchwerk SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533019', 'No more play?', null, null, null, null, null, null, null, null, '8912', '1', '0', '0', 'patchwerk SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1533020', 'What happened to... Patch...', null, null, null, null, null, null, null, null, '8911', '1', '0', '0', 'patchwerk SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533021', '%s goes into a berserker rage!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'patchwerk EMOTE_BERSERK');
INSERT INTO `script_texts` VALUES ('-1533022', '%s becomes enraged!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'patchwerk EMOTE_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1533023', 'Stalagg crush you!', null, null, null, null, null, null, null, null, '8864', '1', '0', '0', 'stalagg SAY_STAL_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533024', 'Stalagg kill!', null, null, null, null, null, null, null, null, '8866', '1', '0', '0', 'stalagg SAY_STAL_SLAY');
INSERT INTO `script_texts` VALUES ('-1533025', 'Master save me...', null, null, null, null, null, null, null, null, '8865', '1', '0', '0', 'stalagg SAY_STAL_DEATH');
INSERT INTO `script_texts` VALUES ('-1533026', 'Feed you to master!', null, null, null, null, null, null, null, null, '8802', '1', '0', '0', 'feugen SAY_FEUG_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533027', 'Feugen make master happy!', null, null, null, null, null, null, null, null, '8804', '1', '0', '0', 'feugen SAY_FEUG_SLAY');
INSERT INTO `script_texts` VALUES ('-1533028', 'No... more... Feugen...', null, null, null, null, null, null, null, null, '8803', '1', '0', '0', 'feugen SAY_FEUG_DEATH');
INSERT INTO `script_texts` VALUES ('-1533029', 'You are too late... I... must... OBEY!', null, null, null, null, null, null, null, null, '8872', '1', '0', '0', 'thaddius SAY_GREET');
INSERT INTO `script_texts` VALUES ('-1533030', 'KILL!', null, null, null, null, null, null, null, null, '8867', '1', '0', '0', 'thaddius SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533031', 'EAT YOUR BONES!', null, null, null, null, null, null, null, null, '8868', '1', '0', '0', 'thaddius SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533032', 'BREAK YOU!', null, null, null, null, null, null, null, null, '8869', '1', '0', '0', 'thaddius SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533033', 'You die now!', null, null, null, null, null, null, null, null, '8877', '1', '0', '0', 'thaddius SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1533034', 'Now YOU feel pain!', null, null, null, null, null, null, null, null, '8871', '1', '0', '0', 'thaddius SAY_ELECT');
INSERT INTO `script_texts` VALUES ('-1533035', 'Thank... you...', null, null, null, null, null, null, null, null, '8870', '1', '0', '0', 'thaddius SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533036', 'Pleeease!', null, null, null, null, null, null, null, null, '8873', '1', '0', '0', 'thaddius SAY_SCREAM1');
INSERT INTO `script_texts` VALUES ('-1533037', 'Stop, make it stop!', null, null, null, null, null, null, null, null, '8874', '1', '0', '0', 'thaddius SAY_SCREAM2');
INSERT INTO `script_texts` VALUES ('-1533038', 'Help me! Save me!', null, null, null, null, null, null, null, null, '8875', '1', '0', '0', 'thaddius SAY_SCREAM3');
INSERT INTO `script_texts` VALUES ('-1533039', 'Please, nooo!', null, null, null, null, null, null, null, null, '8876', '1', '0', '0', 'thaddius SAY_SCREAM4');
INSERT INTO `script_texts` VALUES ('-1533040', 'Foolishly you have sought your own demise. Brazenly you have disregarded powers beyond your understanding. You have fought hard to invade the realm of the harvester. Now there is only one way out - to walk the lonely path of the damned.', null, null, null, null, null, null, null, null, '8807', '1', '0', '0', 'gothik SAY_SPEECH');
INSERT INTO `script_texts` VALUES ('-1533041', 'Death is the only escape.', null, null, null, null, null, null, null, null, '8806', '1', '0', '0', 'gothik SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1533042', 'I... am... undone!', null, null, null, null, null, null, null, null, '8805', '1', '0', '0', 'gothik SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533043', 'I have waited long enough! Now, you face the harvester of souls!', null, null, null, null, null, null, null, null, '8808', '1', '0', '0', 'gothik SAY_TELEPORT');
INSERT INTO `script_texts` VALUES ('-1533044', 'Defend youself!', null, null, null, null, null, null, null, null, '8892', '1', '0', '0', 'blaumeux SAY_BLAU_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533045', 'Come, Zeliek, do not drive them out. Not before we\'ve had our fun.', null, null, null, null, null, null, null, null, '8896', '1', '0', '0', 'blaumeux SAY_BLAU_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533046', 'I do hope they stay alive long enough for me to... introduce myself.', null, null, null, null, null, null, null, null, '8897', '1', '0', '0', 'blaumeux SAY_BLAU_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533047', 'The first kill goes to me! Anyone care to wager?', null, null, null, null, null, null, null, null, '8898', '1', '0', '0', 'blaumeux SAY_BLAU_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533048', 'Your life is mine!', null, null, null, null, null, null, null, null, '8895', '1', '0', '0', 'blaumeux SAY_BLAU_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1533049', 'Who\'s next?', null, null, null, null, null, null, null, null, '8894', '1', '0', '0', 'blaumeux SAY_BLAU_SLAY');
INSERT INTO `script_texts` VALUES ('-1533050', 'Tou... che!', null, null, null, null, null, null, null, null, '8893', '1', '0', '0', 'blaumeux SAY_BLAU_DEATH');
INSERT INTO `script_texts` VALUES ('-1533051', 'Come out and fight, ye wee ninny!', null, null, null, null, null, null, null, null, '8899', '1', '0', '0', 'korthazz SAY_KORT_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533052', 'To arms, ye roustabouts! We\'ve got company!', null, null, null, null, null, null, null, null, '8903', '1', '0', '0', 'korthazz SAY_KORT_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533053', 'I heard about enough of yer sniveling. Shut yer fly trap \'afore I shut it for ye!', null, null, null, null, null, null, null, null, '8904', '1', '0', '0', 'korthazz SAY_KORT_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533054', 'I\'m gonna enjoy killin\' these slack-jawed daffodils!', null, null, null, null, null, null, null, null, '8905', '1', '0', '0', 'korthazz SAY_KORT_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533055', 'I like my meat extra crispy!', null, null, null, null, null, null, null, null, '8902', '1', '0', '0', 'korthazz SAY_KORT_SPECIAl');
INSERT INTO `script_texts` VALUES ('-1533056', 'Next time, bring more friends!', null, null, null, null, null, null, null, null, '8901', '1', '0', '0', 'korthazz SAY_KORT_SLAY');
INSERT INTO `script_texts` VALUES ('-1533057', 'What a bloody waste this is!', null, null, null, null, null, null, null, null, '8900', '1', '0', '0', 'korthazz SAY_KORT_DEATH');
INSERT INTO `script_texts` VALUES ('-1533058', 'Flee, before it\'s too late!', null, null, null, null, null, null, null, null, '8913', '1', '0', '0', 'zeliek SAY_ZELI_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533059', 'Invaders, cease this foolish venture at once! Turn away while you still can!', null, null, null, null, null, null, null, null, '8917', '1', '0', '0', 'zeliek SAY_ZELI_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533060', 'Perhaps they will come to their senses, and run away as fast as they can!', null, null, null, null, null, null, null, null, '8918', '1', '0', '0', 'zeliek SAY_ZELI_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533061', 'Do not continue! Turn back while there\'s still time!', null, null, null, null, null, null, null, null, '8919', '1', '0', '0', 'zeliek SAY_ZELI_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533062', 'I- I have no choice but to obey!', null, null, null, null, null, null, null, null, '8916', '1', '0', '0', 'zeliek SAY_ZELI_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1533063', 'Forgive me!', null, null, null, null, null, null, null, null, '8915', '1', '0', '0', 'zeliek SAY_ZELI_SLAY');
INSERT INTO `script_texts` VALUES ('-1533064', 'It is... as it should be.', null, null, null, null, null, null, null, null, '8914', '1', '0', '0', 'zeliek SAY_ZELI_DEATH');
INSERT INTO `script_texts` VALUES ('-1533065', 'You seek death?', null, null, null, null, null, null, null, null, '14571', '1', '0', '0', 'rivendare_naxx SAY_RIVE_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533066', 'None shall pass!', null, null, null, null, null, null, null, null, '14572', '1', '0', '0', 'rivendare_naxx SAY_RIVE_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533067', 'Be still!', null, null, null, null, null, null, null, null, '14573', '1', '0', '0', 'rivendare_naxx SAY_RIVE_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533068', 'You will find no peace in death.', null, null, null, null, null, null, null, null, '14574', '1', '0', '0', 'rivendare_naxx SAY_RIVE_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533069', 'The master\'s will is done.', null, null, null, null, null, null, null, null, '14575', '1', '0', '0', 'rivendare_naxx SAY_RIVE_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533070', 'Bow to the might of the scourge!', null, null, null, null, null, null, null, null, '14576', '1', '0', '0', 'rivendare_naxx SAY_RIVE_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1533071', 'Enough prattling. Let them come! We shall grind their bones to dust.', null, null, null, null, null, null, null, null, '14577', '1', '0', '0', 'rivendare_naxx SAY_RIVE_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533072', 'Conserve your anger! Harness your rage! You will all have outlets for your frustration soon enough.', null, null, null, null, null, null, null, null, '14578', '1', '0', '0', 'rivendare_naxx SAY_RIVE_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533073', 'Life is meaningless. It is in death that we are truly tested.', null, null, null, null, null, null, null, null, '14579', '1', '0', '0', 'rivendare_naxx SAY_RIVE_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533074', 'Death... will not stop me...', null, null, null, null, null, null, null, null, '14580', '1', '0', '0', 'rivendare_naxx SAY_RIVE_DEATH');
INSERT INTO `script_texts` VALUES ('-1533075', 'Glory to the master!', null, null, null, null, null, null, null, null, '8845', '1', '0', '0', 'noth SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533076', 'Your life is forfeit!', null, null, null, null, null, null, null, null, '8846', '1', '0', '0', 'noth SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533077', 'Die, trespasser!', null, null, null, null, null, null, null, null, '8847', '1', '0', '0', 'noth SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533078', 'Rise, my soldiers! Rise and fight once more!', null, null, null, null, null, null, null, null, '8851', '1', '0', '0', 'noth SAY_SUMMON');
INSERT INTO `script_texts` VALUES ('-1533079', 'My task is done!', null, null, null, null, null, null, null, null, '8849', '1', '0', '0', 'noth SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533080', 'Breathe no more!', null, null, null, null, null, null, null, null, '8850', '1', '0', '0', 'noth SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533081', 'I will serve the master... in... death!', null, null, null, null, null, null, null, null, '8848', '1', '0', '0', 'noth SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533082', '%s takes in a deep breath...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'sapphiron EMOTE_BREATH');
INSERT INTO `script_texts` VALUES ('-1533083', '%s enrages!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'sapphiron EMOTE_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1533084', 'Our preparations continue as planned, master.', null, null, null, null, null, null, null, null, '14467', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG1');
INSERT INTO `script_texts` VALUES ('-1533085', 'It is good that you serve me so faithfully. Soon, all will serve the Lich King and in the end, you shall be rewarded...so long as you do not falter.', null, null, null, null, null, null, null, null, '8881', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG2_LICH');
INSERT INTO `script_texts` VALUES ('-1533086', 'I see no complications... Wait... What is this?', null, null, null, null, null, null, null, null, '14468', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG3');
INSERT INTO `script_texts` VALUES ('-1533087', 'Your security measures have failed! See to this interruption immediately!', null, null, null, null, null, null, null, null, '8882', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG4_LICH');
INSERT INTO `script_texts` VALUES ('-1533088', 'Yes, master!', null, null, null, null, null, null, null, null, '14469', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG5');
INSERT INTO `script_texts` VALUES ('-1533089', 'No!!! A curse upon you, interlopers! The armies of the Lich King will hunt you down. You will not escape your fate...', null, null, null, null, null, null, null, null, '14484', '1', '0', '0', 'kelthuzad SAY_CAT_DIED');
INSERT INTO `script_texts` VALUES ('-1533090', 'Who dares violate the sanctity of my domain? Be warned, all who trespass here are doomed.', null, null, null, null, null, null, null, null, '14463', '1', '0', '0', 'kelthuzad SAY_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533091', 'Fools, you think yourselves triumphant? You have only taken one step closer to the abyss! ', null, null, null, null, null, null, null, null, '14464', '1', '0', '0', 'kelthuzad SAY_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533092', 'I grow tired of these games. Proceed, and I will banish your souls to oblivion!', null, null, null, null, null, null, null, null, '14465', '1', '0', '0', 'kelthuzad SAY_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533093', 'You have no idea what horrors lie ahead. You have seen nothing! The frozen heart of Naxxramas awaits you!', null, null, null, null, null, null, null, null, '14466', '1', '0', '0', 'kelthuzad SAY_TAUNT4');
INSERT INTO `script_texts` VALUES ('-1533094', 'Pray for mercy!', null, null, null, null, null, null, null, null, '14475', '1', '0', '0', 'kelthuzad SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533095', 'Scream your dying breath!', null, null, null, null, null, null, null, null, '14476', '1', '0', '0', 'kelthuzad SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533096', 'The end is upon you!', null, null, null, null, null, null, null, null, '14477', '1', '0', '0', 'kelthuzad SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533097', 'The dark void awaits you!', null, null, null, null, null, null, null, null, '14478', '1', '0', '0', 'kelthuzad SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533098', '<Kel\'Thuzad cackles maniacally!>', null, null, null, null, null, null, null, null, '14479', '1', '0', '0', 'kelthuzad SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533099', 'AAAAGHHH!... Do not rejoice... your victory is a hollow one... for I shall return with powers beyond your imagining!', null, null, null, null, null, null, null, null, '14480', '1', '0', '0', 'kelthuzad SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533100', 'Your soul, is bound to me now!', null, null, null, null, null, null, null, null, '14472', '1', '0', '0', 'kelthuzad SAY_CHAIN1');
INSERT INTO `script_texts` VALUES ('-1533101', 'There will be no escape!', null, null, null, null, null, null, null, null, '14473', '1', '0', '0', 'kelthuzad SAY_CHAIN2');
INSERT INTO `script_texts` VALUES ('-1533102', 'I will freeze the blood in your veins!', null, null, null, null, null, null, null, null, '14474', '1', '0', '0', 'kelthuzad SAY_FROST_BLAST');
INSERT INTO `script_texts` VALUES ('-1533103', 'Master! I require aid! ', null, null, null, null, null, null, null, null, '14470', '1', '0', '0', 'kelthuzad SAY_REQUEST_AID');
INSERT INTO `script_texts` VALUES ('-1533104', 'Very well... warriors of the frozen wastes, rise up! I command you to fight, kill, and die for your master. Let none survive...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'kelthuzad SAY_ANSWER_REQUEST');
INSERT INTO `script_texts` VALUES ('-1533105', 'Minions, servants, soldiers of the cold dark, obey the call of Kel\'Thuzad!', null, null, null, null, null, null, null, null, '14471', '1', '0', '0', 'kelthuzad SAY_SUMMON_MINIONS');
INSERT INTO `script_texts` VALUES ('-1533106', 'Your petty magics are no challenge to the might of the Scourge! ', null, null, null, null, null, null, null, null, '14481', '1', '0', '0', 'kelthuzad SAY_SPECIAL1_MANA_DET');
INSERT INTO `script_texts` VALUES ('-1533107', 'Enough! I grow tired of these distractions! ', null, null, null, null, null, null, null, null, '14483', '1', '0', '0', 'kelthuzad SAY_SPECIAL3_MANA_DET');
INSERT INTO `script_texts` VALUES ('-1533108', 'Fools, you have spread your powers too thin. Be free, my minions!', null, null, null, null, null, null, null, null, '14482', '1', '0', '0', 'kelthuzad SAY_SPECIAL2_DISPELL');
INSERT INTO `script_texts` VALUES ('-1533109', 'You are mine now!', null, null, null, null, null, null, null, null, '8825', '1', '0', '0', 'heigan SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533110', 'I see you!', null, null, null, null, null, null, null, null, '8826', '1', '0', '0', 'heigan SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533111', 'You...are next!', null, null, null, null, null, null, null, null, '8827', '1', '0', '0', 'heigan SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533112', 'Close your eyes... sleep!', null, null, null, null, null, null, null, null, '8829', '1', '0', '0', 'heigan SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1533113', 'The races of the world will perish. It is only a matter of time.', null, null, null, null, null, null, null, null, '8830', '1', '0', '0', 'heigan SAY_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533114', 'I see endless suffering, I see torment, I see rage. I see... everything!', null, null, null, null, null, null, null, null, '8831', '1', '0', '0', 'heigan SAY_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533115', 'Soon... the world will tremble!', null, null, null, null, null, null, null, null, '8832', '1', '0', '0', 'heigan SAY_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533116', 'The end is upon you.', null, null, null, null, null, null, null, null, '8833', '1', '0', '0', 'heigan SAY_TAUNT4');
INSERT INTO `script_texts` VALUES ('-1533117', 'Hungry worms will feast on your rotten flesh!', null, null, null, null, null, null, null, null, '8834', '1', '0', '0', 'heigan SAY_TAUNT5');
INSERT INTO `script_texts` VALUES ('-1533118', 'Noo... o...', null, null, null, null, null, null, null, null, '8828', '1', '0', '0', 'heigan SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533119', '%s spots a nearby Zombie to devour!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'gluth EMOTE_ZOMBIE');
INSERT INTO `script_texts` VALUES ('-1533120', 'Hah hah, I\'m just getting warmed up!', null, null, null, null, null, null, null, null, '8852', '1', '0', '0', 'razuvious SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533121', 'Stand and fight!', null, null, null, null, null, null, null, null, '8853', '1', '0', '0', 'razuvious SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533122', 'Show me what you\'ve got!', null, null, null, null, null, null, null, null, '8854', '1', '0', '0', 'razuvious SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533123', 'Sweep the leg! Do you have a problem with that?', null, null, null, null, null, null, null, null, '8861', '1', '0', '0', 'razuvious SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533124', 'You should have stayed home!', null, null, null, null, null, null, null, null, '8862', '1', '0', '0', 'razuvious SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533125', 'Do as I taught you!', null, null, null, null, null, null, null, null, '8855', '1', '0', '0', 'razuvious SAY_COMMAND1');
INSERT INTO `script_texts` VALUES ('-1533126', 'Show them no mercy!', null, null, null, null, null, null, null, null, '8856', '1', '0', '0', 'razuvious SAY_COMMAND2');
INSERT INTO `script_texts` VALUES ('-1533127', 'You disappoint me, students!', null, null, null, null, null, null, null, null, '8858', '1', '0', '0', 'razuvious SAY_COMMAND3');
INSERT INTO `script_texts` VALUES ('-1533128', 'The time for practice is over! Show me what you\'ve learned!', null, null, null, null, null, null, null, null, '8859', '1', '0', '0', 'razuvious SAY_COMMAND4');
INSERT INTO `script_texts` VALUES ('-1533129', 'An honorable... death...', null, null, null, null, null, null, null, null, '8860', '1', '0', '0', 'razuvious SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1534000', 'I\'m in jeopardy, help me if you can!', null, null, null, null, null, null, null, null, '11007', '1', '0', '0', 'jaina hyjal ATTACKED 1');
INSERT INTO `script_texts` VALUES ('-1534001', 'They\'ve broken through!', null, null, null, null, null, null, null, null, '11049', '1', '0', '0', 'jaina hyjal ATTACKED 2');
INSERT INTO `script_texts` VALUES ('-1534002', 'Stay alert! Another wave approaches.', null, null, null, null, null, null, null, null, '11008', '1', '0', '0', 'jaina hyjal INCOMING');
INSERT INTO `script_texts` VALUES ('-1534003', 'Don\'t give up! We must prevail!', null, null, null, null, null, null, null, null, '11006', '1', '0', '0', 'jaina hyjal BEGIN');
INSERT INTO `script_texts` VALUES ('-1534004', 'Hold them back as long as possible.', null, null, null, null, null, null, null, null, '11050', '1', '0', '0', 'jaina hyjal RALLY 1');
INSERT INTO `script_texts` VALUES ('-1534005', 'We must hold strong!', null, null, null, null, null, null, null, null, '11051', '1', '0', '0', 'jaina hyjal RALLY 2');
INSERT INTO `script_texts` VALUES ('-1534006', 'We are lost. Fall back!', null, null, null, null, null, null, null, null, '11009', '1', '0', '0', 'jaina hyjal FAILURE');
INSERT INTO `script_texts` VALUES ('-1534007', 'We have won valuable time. Now we must pull back!', null, null, null, null, null, null, null, null, '11011', '1', '0', '0', 'jaina hyjal SUCCESS');
INSERT INTO `script_texts` VALUES ('-1534008', 'I did... my best.', null, null, null, null, null, null, null, null, '11010', '1', '0', '0', 'jaina hyjal DEATH');
INSERT INTO `script_texts` VALUES ('-1534009', 'I will lie down for no one!', null, null, null, null, null, null, null, null, '11031', '1', '0', '0', 'thrall hyjal ATTACKED 1');
INSERT INTO `script_texts` VALUES ('-1534010', 'Bring the fight to me and pay with your lives!', null, null, null, null, null, null, null, null, '11061', '1', '0', '0', 'thrall hyjal ATTACKED 2');
INSERT INTO `script_texts` VALUES ('-1534011', 'Make ready for another wave! LOK-TAR OGAR!', null, null, null, null, null, null, null, null, '11032', '1', '0', '0', 'thrall hyjal INCOMING');
INSERT INTO `script_texts` VALUES ('-1534012', 'Hold them back! Do not falter!', null, null, null, null, null, null, null, null, '11030', '1', '0', '0', 'thrall hyjal BEGIN');
INSERT INTO `script_texts` VALUES ('-1534013', 'Victory or death!', null, null, null, null, null, null, null, null, '11059', '1', '0', '0', 'thrall hyjal RALLY 1');
INSERT INTO `script_texts` VALUES ('-1534014', 'Do not give an inch of ground!', null, null, null, null, null, null, null, null, '11060', '1', '0', '0', 'thrall hyjal RALLY 2');
INSERT INTO `script_texts` VALUES ('-1534015', 'It is over. Withdraw! We have failed.', null, null, null, null, null, null, null, null, '11033', '1', '0', '0', 'thrall hyjal FAILURE');
INSERT INTO `script_texts` VALUES ('-1534016', 'We have played our part and done well. It is up to the others now.', null, null, null, null, null, null, null, null, '11035', '1', '0', '0', 'thrall hyjal SUCCESS');
INSERT INTO `script_texts` VALUES ('-1534017', 'Uraaa...', null, null, null, null, null, null, null, null, '11034', '1', '0', '0', 'thrall hyjal DEATH');
INSERT INTO `script_texts` VALUES ('-1534018', 'All of your efforts have been in vain, for the draining of the World Tree has already begun. Soon the heart of your world will beat no more.', null, null, null, null, null, null, null, null, '10986', '1', '0', '0', 'archimonde SAY_PRE_EVENTS_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1534019', 'Your resistance is insignificant.', null, null, null, null, null, null, null, null, '10987', '1', '0', '0', 'archimonde SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1534020', 'This world will burn!', null, null, null, null, null, null, null, null, '10990', '1', '0', '0', 'archimonde SAY_DOOMFIRE1');
INSERT INTO `script_texts` VALUES ('-1534021', 'Manach sheek-thrish!', null, null, null, null, null, null, null, null, '11041', '1', '0', '0', 'archimonde SAY_DOOMFIRE2');
INSERT INTO `script_texts` VALUES ('-1534022', 'A-kreesh!', null, null, null, null, null, null, null, null, '10989', '1', '0', '0', 'archimonde SAY_AIR_BURST1');
INSERT INTO `script_texts` VALUES ('-1534023', 'Away vermin!', null, null, null, null, null, null, null, null, '11043', '1', '0', '0', 'archimonde SAY_AIR_BURST2');
INSERT INTO `script_texts` VALUES ('-1534024', 'All creation will be devoured!', null, null, null, null, null, null, null, null, '11044', '1', '0', '0', 'archimonde SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1534025', 'Your soul will languish for eternity.', null, null, null, null, null, null, null, null, '10991', '1', '0', '0', 'archimonde SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1534026', 'I am the coming of the end!', null, null, null, null, null, null, null, null, '11045', '1', '0', '0', 'archimonde SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1534027', 'At last it is here. Mourn and lament the passing of all you have ever known and all that would have been! Akmin-kurai!', null, null, null, null, null, null, null, null, '10993', '1', '0', '0', 'archimonde SAY_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1534028', 'No, it cannot be! Nooo!', null, null, null, null, null, null, null, null, '10992', '1', '0', '0', 'archimonde SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1534029', 'You are mine now.', null, null, null, null, null, null, null, null, '10988', '1', '0', '0', 'archimonde SAY_SOUL_CHARGE1');
INSERT INTO `script_texts` VALUES ('-1534030', 'Bow to my will.', null, null, null, null, null, null, null, null, '11042', '1', '0', '0', 'archimonde SAY_SOUL_CHARGE2');
INSERT INTO `script_texts` VALUES ('-1540000', 'You wish to fight us all at once? This should be amusing!', null, null, null, null, null, null, null, null, '10262', '1', '0', '0', 'nethekurse SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1540001', 'You can have that one. I no longer need him.', null, null, null, null, null, null, null, null, '10263', '1', '0', '0', 'nethekurse PEON_ATTACK_1');
INSERT INTO `script_texts` VALUES ('-1540002', 'Yes, beat him mercilessly. His skull is a thick as an ogres.', null, null, null, null, null, null, null, null, '10264', '1', '0', '0', 'nethekurse PEON_ATTACK_2');
INSERT INTO `script_texts` VALUES ('-1540003', 'Don\'t waste your time on that one. He\'s weak!', null, null, null, null, null, null, null, null, '10265', '1', '0', '0', 'nethekurse PEON_ATTACK_3');
INSERT INTO `script_texts` VALUES ('-1540004', 'You want him? Very well, take him!', null, null, null, null, null, null, null, null, '10266', '1', '0', '0', 'nethekurse PEON_ATTACK_4');
INSERT INTO `script_texts` VALUES ('-1540005', 'One pitiful wretch down. Go on, take another one.', null, null, null, null, null, null, null, null, '10267', '1', '0', '0', 'nethekurse PEON_DIE_1');
INSERT INTO `script_texts` VALUES ('-1540006', 'Ahh, what a waste... Next!', null, null, null, null, null, null, null, null, '10268', '1', '0', '0', 'nethekurse PEON_DIE_2');
INSERT INTO `script_texts` VALUES ('-1540007', 'I was going to kill him anyway!', null, null, null, null, null, null, null, null, '10269', '1', '0', '0', 'nethekurse PEON_DIE_3');
INSERT INTO `script_texts` VALUES ('-1540008', 'Thank you for saving me the trouble! Now it\'s my turn to have some fun...', null, null, null, null, null, null, null, null, '10270', '1', '0', '0', 'nethekurse PEON_DIE_4');
INSERT INTO `script_texts` VALUES ('-1540009', 'Beg for your pittyfull life!', null, null, null, null, null, null, null, null, '10259', '1', '0', '0', 'nethekurse SAY_TAUNT_1');
INSERT INTO `script_texts` VALUES ('-1540010', 'Run covad, ruun!', null, null, null, null, null, null, null, null, '10260', '1', '0', '0', 'nethekurse SAY_TAUNT_2');
INSERT INTO `script_texts` VALUES ('-1540011', 'Your pain amuses me.', null, null, null, null, null, null, null, null, '10261', '1', '0', '0', 'nethekurse SAY_TAUNT_3');
INSERT INTO `script_texts` VALUES ('-1540012', 'I\'m already bored.', null, null, null, null, null, null, null, null, '10271', '1', '0', '0', 'nethekurse SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1540013', 'Come on! ... Show me a real fight.', null, null, null, null, null, null, null, null, '10272', '1', '0', '0', 'nethekurse SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1540014', 'I had more fun torturing the peons.', null, null, null, null, null, null, null, null, '10273', '1', '0', '0', 'nethekurse SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1540015', 'You Loose.', null, null, null, null, null, null, null, null, '10274', '1', '0', '0', 'nethekurse SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1540016', 'Ohh! Just die.', null, null, null, null, null, null, null, null, '10275', '1', '0', '0', 'nethekurse SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1540017', 'What a ... a shame.', null, null, null, null, null, null, null, null, '10276', '1', '0', '0', 'nethekurse SAY_DIE');
INSERT INTO `script_texts` VALUES ('-1540018', 'Smash!', null, null, null, null, null, null, null, null, '10306', '1', '0', '0', 'omrogg GoCombat_1');
INSERT INTO `script_texts` VALUES ('-1540019', 'If you nice me let you live.', null, null, null, null, null, null, null, null, '10308', '1', '0', '0', 'omrogg GoCombat_2');
INSERT INTO `script_texts` VALUES ('-1540020', 'Me hungry!', null, null, null, null, null, null, null, null, '10309', '1', '0', '0', 'omrogg GoCombat_3');
INSERT INTO `script_texts` VALUES ('-1540021', 'Why don\'t you let me do the talking?', null, null, null, null, null, null, null, null, '10317', '1', '0', '0', 'omrogg GoCombatDelay_1');
INSERT INTO `script_texts` VALUES ('-1540022', 'No, we will NOT let you live!', null, null, null, null, null, null, null, null, '10318', '1', '0', '0', 'omrogg GoCombatDelay_2');
INSERT INTO `script_texts` VALUES ('-1540023', 'You always hungry. That why we so fat!', null, null, null, null, null, null, null, null, '10319', '1', '0', '0', 'omrogg GoCombatDelay_3');
INSERT INTO `script_texts` VALUES ('-1540024', 'You stay here. Me go kill someone else!', null, null, null, null, null, null, null, null, '10303', '1', '0', '0', 'omrogg Threat_1');
INSERT INTO `script_texts` VALUES ('-1540025', 'What are you doing!', null, null, null, null, null, null, null, null, '10315', '1', '0', '0', 'omrogg Threat_2');
