INSERT INTO `script_texts` VALUES ('-1543000', 'Do you smell that? Fresh meat has somehow breached our citadel. Be wary of any intruders.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'gargolmar SAY_TAUNT');
