INSERT INTO `script_texts` VALUES ('-1548013', 'Perish, mortal.', null, null, null, null, null, null, null, null, '11307', '1', '0', '0', 'leotheras SAY_DEMON_SLAY2');
INSERT INTO `script_texts` VALUES ('-1548014', 'Yes, YES! Ahahah!', null, null, null, null, null, null, null, null, '11308', '1', '0', '0', 'leotheras SAY_DEMON_SLAY3');
INSERT INTO `script_texts` VALUES ('-1548015', 'Kill! KILL!', null, null, null, null, null, null, null, null, '11314', '1', '0', '0', 'leotheras SAY_NIGHTELF_SLAY1');
INSERT INTO `script_texts` VALUES ('-1548016', 'That\'s right! Yes!', null, null, null, null, null, null, null, null, '11315', '1', '0', '0', 'leotheras SAY_NIGHTELF_SLAY2');
INSERT INTO `script_texts` VALUES ('-1548017', 'Who\'s the master now?', null, null, null, null, null, null, null, null, '11316', '1', '0', '0', 'leotheras SAY_NIGHTELF_SLAY3');
INSERT INTO `script_texts` VALUES ('-1548018', 'No... no! What have you done? I am the master! Do you hear me? I am... aaggh! Can\'t... contain him...', null, null, null, null, null, null, null, null, '11313', '1', '0', '0', 'leotheras SAY_FINAL_FORM');
INSERT INTO `script_texts` VALUES ('-1548019', 'At last I am liberated. It has been too long since I have tasted true freedom!', null, null, null, null, null, null, null, null, '11309', '1', '0', '0', 'leotheras SAY_FREE');
INSERT INTO `script_texts` VALUES ('-1548020', 'You cannot kill me! Fools, I\'ll be back! I\'ll... aarghh...', null, null, null, null, null, null, null, null, '11317', '1', '0', '0', 'leotheras SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1548021', 'Guards, attention! We have visitors...', null, null, null, null, null, null, null, null, '11277', '1', '0', '0', 'karathress SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1548022', 'Your overconfidence will be your undoing! Guards, lend me your strength!', null, null, null, null, null, null, null, null, '11278', '1', '0', '0', 'karathress SAY_GAIN_BLESSING');
INSERT INTO `script_texts` VALUES ('-1548023', 'Go on, kill them! I\'ll be the better for it!', null, null, null, null, null, null, null, null, '11279', '1', '0', '0', 'karathress SAY_GAIN_ABILITY1');
INSERT INTO `script_texts` VALUES ('-1548024', 'I am more powerful than ever!', null, null, null, null, null, null, null, null, '11280', '1', '0', '0', 'karathress SAY_GAIN_ABILITY2');
INSERT INTO `script_texts` VALUES ('-1548025', 'More knowledge, more power!', null, null, null, null, null, null, null, null, '11281', '1', '0', '0', 'karathress SAY_GAIN_ABILITY3');
INSERT INTO `script_texts` VALUES ('-1548026', 'Land-dwelling scum!', null, null, null, null, null, null, null, null, '11282', '1', '0', '0', 'karathress SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1548027', 'Alana be\'lendor!', null, null, null, null, null, null, null, null, '11283', '1', '0', '0', 'karathress SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1548028', 'I am rid of you.', null, null, null, null, null, null, null, null, '11284', '1', '0', '0', 'karathress SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1548029', 'Her ... excellency ... awaits!', null, null, null, null, null, null, null, null, '11285', '1', '0', '0', 'karathress SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1548030', 'Flood of the deep, take you!', null, null, null, null, null, null, null, null, '11321', '1', '0', '0', 'morogrim SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1548031', 'By the Tides, kill them at once!', null, null, null, null, null, null, null, null, '11322', '1', '0', '0', 'morogrim SAY_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1548032', 'Destroy them my subjects!', null, null, null, null, null, null, null, null, '11323', '1', '0', '0', 'morogrim SAY_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1548033', 'There is nowhere to hide!', null, null, null, null, null, null, null, null, '11324', '1', '0', '0', 'morogrim SAY_SUMMON_BUBL1');
INSERT INTO `script_texts` VALUES ('-1548034', 'Soon it will be finished!', null, null, null, null, null, null, null, null, '11325', '1', '0', '0', 'morogrim SAY_SUMMON_BUBL2');
INSERT INTO `script_texts` VALUES ('-1548035', 'It is done!', null, null, null, null, null, null, null, null, '11326', '1', '0', '0', 'morogrim SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1548036', 'Strugging only makes it worse.', null, null, null, null, null, null, null, null, '11327', '1', '0', '0', 'morogrim SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1548037', 'Only the strong survive.', null, null, null, null, null, null, null, null, '11328', '1', '0', '0', 'morogrim SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1548038', 'Great... currents of... Ageon.', null, null, null, null, null, null, null, null, '11329', '1', '0', '0', 'morogrim SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1548039', '%s sends his enemies to their watery graves!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'morogrim EMOTE_WATERY_GRAVE');
INSERT INTO `script_texts` VALUES ('-1548040', 'The violent earthquake has alerted nearby murlocs!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'morogrim EMOTE_EARTHQUAKE');
INSERT INTO `script_texts` VALUES ('-1548041', '%s summons Watery Globules!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'morogrim EMOTE_WATERY_GLOBULES');
INSERT INTO `script_texts` VALUES ('-1548042', 'Water is life. It has become a rare commodity here in Outland. A commodity that we alone shall control. We are the Highborne, and the time has come at last for us to retake our rightful place in the world!', null, null, null, null, null, null, null, null, '11531', '1', '0', '0', 'vashj SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1548043', 'I\'ll split you from stem to stern!', null, null, null, null, null, null, null, null, '11532', '1', '0', '0', 'vashj SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1548044', 'Victory to Lord Illidan!', null, null, null, null, null, null, null, null, '11533', '1', '0', '0', 'vashj SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1548045', 'I spit on you, surface filth!', null, null, null, null, null, null, null, null, '11534', '1', '0', '0', 'vashj SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1548046', 'Death to the outsiders!', null, null, null, null, null, null, null, null, '11535', '1', '0', '0', 'vashj SAY_AGGRO4');
INSERT INTO `script_texts` VALUES ('-1548047', 'I did not wish to lower myself by engaging your kind, but you leave me little choice!', null, null, null, null, null, null, null, null, '11538', '1', '0', '0', 'vashj SAY_PHASE1');
INSERT INTO `script_texts` VALUES ('-1548048', 'The time is now! Leave none standing!', null, null, null, null, null, null, null, null, '11539', '1', '0', '0', 'vashj SAY_PHASE2');
INSERT INTO `script_texts` VALUES ('-1548049', 'You may want to take cover.', null, null, null, null, null, null, null, null, '11540', '1', '0', '0', 'vashj SAY_PHASE3');
INSERT INTO `script_texts` VALUES ('-1548050', 'Straight to the heart!', null, null, null, null, null, null, null, null, '11536', '1', '0', '0', 'vashj SAY_BOWSHOT1');
INSERT INTO `script_texts` VALUES ('-1548051', 'Seek your mark!', null, null, null, null, null, null, null, null, '11537', '1', '0', '0', 'vashj SAY_BOWSHOT2');
INSERT INTO `script_texts` VALUES ('-1548052', 'Your time ends now!', null, null, null, null, null, null, null, null, '11541', '1', '0', '0', 'vashj SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1548053', 'You have failed!', null, null, null, null, null, null, null, null, '11542', '1', '0', '0', 'vashj SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1548054', 'Be\'lamere an\'delay', null, null, null, null, null, null, null, null, '11543', '1', '0', '0', 'vashj SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1548055', 'Lord Illidan, I... I am... sorry.', null, null, null, null, null, null, null, null, '11544', '1', '0', '0', 'vashj SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1550000', 'Alert, you are marked for extermination!', null, null, null, null, null, null, null, null, '11213', '1', '0', '0', 'voidreaver SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1550001', 'Extermination, successful.', null, null, null, null, null, null, null, null, '11215', '1', '0', '0', 'voidreaver SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1550002', 'Imbecile life form, no longer functional.', null, null, null, null, null, null, null, null, '11216', '1', '0', '0', 'voidreaver SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1550003', 'Threat neutralized.', null, null, null, null, null, null, null, null, '11217', '1', '0', '0', 'voidreaver SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1550004', 'Systems... shutting... down...', null, null, null, null, null, null, null, null, '11214', '1', '0', '0', 'voidreaver SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1550005', 'Alternative measure commencing...', null, null, null, null, null, null, null, null, '11218', '1', '0', '0', 'voidreaver SAY_POUNDING1');
INSERT INTO `script_texts` VALUES ('-1550006', 'Calculating force parameters...', null, null, null, null, null, null, null, null, '11219', '1', '0', '0', 'voidreaver SAY_POUNDING2');
INSERT INTO `script_texts` VALUES ('-1550007', 'Tal anu\'men no Sin\'dorei!', null, null, null, null, null, null, null, null, '11134', '1', '0', '0', 'solarian SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1550008', 'Ha ha ha! You are hopelessly outmatched!', null, null, null, null, null, null, null, null, '11139', '1', '0', '0', 'solarian SAY_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1550009', 'I will crush your delusions of grandeur!', null, null, null, null, null, null, null, null, '11140', '1', '0', '0', 'solarian SAY_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1550010', 'Your soul belongs to the Abyss!', null, null, null, null, null, null, null, null, '11136', '1', '0', '0', 'solarian SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1550011', 'By the blood of the Highborne!', null, null, null, null, null, null, null, null, '11137', '1', '0', '0', 'solarian SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1550012', 'For the Sunwell!', null, null, null, null, null, null, null, null, '11138', '1', '0', '0', 'solarian SAY_KILL3');
INSERT INTO `script_texts` VALUES ('-1550013', 'The warmth of the sun... awaits.', null, null, null, null, null, null, null, null, '11135', '1', '0', '0', 'solarian SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1550014', 'Enough of this! Now I call upon the fury of the cosmos itself.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'solarian SAY_VOIDA');
INSERT INTO `script_texts` VALUES ('-1550015', 'I become ONE... with the VOID!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'solarian SAY_VOIDB');
INSERT INTO `script_texts` VALUES ('-1550016', 'Energy. Power. My people are addicted to it... a dependence made manifest after the Sunwell was destroyed. Welcome... to the future. A pity you are too late to stop it. No one can stop me now! Selama ashal\'anore!', null, null, null, null, null, null, null, null, '11256', '1', '0', '0', 'kaelthas SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1550017', 'Capernian will see to it that your stay here is a short one.', null, null, null, null, null, null, null, null, '11257', '1', '0', '0', 'kaelthas SAY_INTRO_CAPERNIAN');
INSERT INTO `script_texts` VALUES ('-1550018', 'Well done, you have proven worthy to test your skills against my master engineer, Telonicus.', null, null, null, null, null, null, null, null, '11258', '1', '0', '0', 'kaelthas SAY_INTRO_TELONICUS');
INSERT INTO `script_texts` VALUES ('-1550019', 'Let us see how your nerves hold up against the Darkener, Thaladred.', null, null, null, null, null, null, null, null, '11259', '1', '0', '0', 'kaelthas SAY_INTRO_THALADRED');
INSERT INTO `script_texts` VALUES ('-1550020', 'You have persevered against some of my best advisors... but none can withstand the might of the Blood Hammer. Behold, Lord Sanguinar!', null, null, null, null, null, null, null, null, '11260', '1', '0', '0', 'kaelthas SAY_INTRO_SANGUINAR');
INSERT INTO `script_texts` VALUES ('-1550021', 'As you see, I have many weapons in my arsenal...', null, null, null, null, null, null, null, null, '11261', '1', '0', '0', 'kaelthas SAY_PHASE2_WEAPON');
INSERT INTO `script_texts` VALUES ('-1550022', 'Perhaps I underestimated you. It would be unfair to make you fight all four advisors at once, but... fair treatment was never shown to my people. I\'m just returning the favor.', null, null, null, null, null, null, null, null, '11262', '1', '0', '0', 'kaelthas SAY_PHASE3_ADVANCE');
INSERT INTO `script_texts` VALUES ('-1550023', 'Alas, sometimes one must take matters into one\'s own hands. Balamore shanal!', null, null, null, null, null, null, null, null, '11263', '1', '0', '0', 'kaelthas SAY_PHASE4_INTRO2');
INSERT INTO `script_texts` VALUES ('-1550024', 'I have not come this far to be stopped! The future I have planned will not be jeopardized! Now you will taste true power!!', null, null, null, null, null, null, null, null, '11273', '1', '0', '0', 'kaelthas SAY_PHASE5_NUTS');
INSERT INTO `script_texts` VALUES ('-1550025', 'You will not prevail.', null, null, null, null, null, null, null, null, '11270', '1', '0', '0', 'kaelthas SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1550026', 'You gambled...and lost.', null, null, null, null, null, null, null, null, '11271', '1', '0', '0', 'kaelthas SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1550027', 'This was Child\'s play.', null, null, null, null, null, null, null, null, '11272', '1', '0', '0', 'kaelthas SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1550028', 'Obey me.', null, null, null, null, null, null, null, null, '11268', '1', '0', '0', 'kaelthas SAY_MINDCONTROL1');
INSERT INTO `script_texts` VALUES ('-1550029', 'Bow to my will.', null, null, null, null, null, null, null, null, '11269', '1', '0', '0', 'kaelthas SAY_MINDCONTROL2');
INSERT INTO `script_texts` VALUES ('-1550030', 'Let us see how you fare when your world is turned upside down.', null, null, null, null, null, null, null, null, '11264', '1', '0', '0', 'kaelthas SAY_GRAVITYLAPSE1');
INSERT INTO `script_texts` VALUES ('-1550031', 'Having trouble staying grounded?', null, null, null, null, null, null, null, null, '11265', '1', '0', '0', 'kaelthas SAY_GRAVITYLAPSE2');
INSERT INTO `script_texts` VALUES ('-1550032', 'Anara\'nel belore!', null, null, null, null, null, null, null, null, '11267', '1', '0', '0', 'kaelthas SAY_SUMMON_PHOENIX1');
INSERT INTO `script_texts` VALUES ('-1550033', 'By the power of the sun!', null, null, null, null, null, null, null, null, '11266', '1', '0', '0', 'kaelthas SAY_SUMMON_PHOENIX2');
INSERT INTO `script_texts` VALUES ('-1550034', 'For...Quel...thalas!', null, null, null, null, null, null, null, null, '11274', '1', '0', '0', 'kaelthas SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1550035', 'Prepare yourselves!', null, null, null, null, null, null, null, null, '11203', '1', '0', '0', 'thaladred SAY_THALADRED_AGGRO');
INSERT INTO `script_texts` VALUES ('-1550036', 'Forgive me, my prince! I have... failed.', null, null, null, null, null, null, null, null, '11204', '1', '0', '0', 'thaladred SAY_THALADRED_DEATH');
INSERT INTO `script_texts` VALUES ('-1550037', '%s sets his gaze on $N!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'thaladred EMOTE_THALADRED_GAZE');
INSERT INTO `script_texts` VALUES ('-1550038', 'Blood for blood!', null, null, null, null, null, null, null, null, '11152', '1', '0', '0', 'sanguinar SAY_SANGUINAR_AGGRO');
INSERT INTO `script_texts` VALUES ('-1550039', 'NO! I ...will... not...', null, null, null, null, null, null, null, null, '11153', '1', '0', '0', 'sanguinar SAY_SANGUINAR_DEATH');
INSERT INTO `script_texts` VALUES ('-1550040', 'The sin\'dore reign supreme!', null, null, null, null, null, null, null, null, '11117', '1', '0', '0', 'capernian SAY_CAPERNIAN_AGGRO');
INSERT INTO `script_texts` VALUES ('-1550041', 'This is not over!', null, null, null, null, null, null, null, null, '11118', '1', '0', '0', 'capernian SAY_CAPERNIAN_DEATH');
INSERT INTO `script_texts` VALUES ('-1550042', 'Anar\'alah belore!', null, null, null, null, null, null, null, null, '11157', '1', '0', '0', 'telonicus SAY_TELONICUS_AGGRO');
INSERT INTO `script_texts` VALUES ('-1550043', 'More perils... await', null, null, null, null, null, null, null, null, '11158', '1', '0', '0', 'telonicus SAY_TELONICUS_DEATH');
INSERT INTO `script_texts` VALUES ('-1552000', 'It is a small matter to control the mind of the weak... for I bear allegiance to powers untouched by time, unmoved by fate. No force on this world or beyond harbors the strength to bend our knee... not even the mighty Legion!', null, null, null, null, null, null, null, null, '11122', '1', '0', '0', 'skyriss SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1552001', 'Bear witness to the agent of your demise!', null, null, null, null, null, null, null, null, '11123', '1', '0', '0', 'skyriss SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1552002', 'Your fate is written!', null, null, null, null, null, null, null, null, '11124', '1', '0', '0', 'skyriss SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1552003', 'The chaos I have sown here is but a taste...', null, null, null, null, null, null, null, null, '11125', '1', '0', '0', 'skyriss SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1552004', 'You will do my bidding, weakling.', null, null, null, null, null, null, null, null, '11127', '1', '0', '0', 'skyriss SAY_MIND_1');
INSERT INTO `script_texts` VALUES ('-1552005', 'Your will is no longer your own.', null, null, null, null, null, null, null, null, '11128', '1', '0', '0', 'skyriss SAY_MIND_2');
INSERT INTO `script_texts` VALUES ('-1552006', 'Flee in terror!', null, null, null, null, null, null, null, null, '11129', '1', '0', '0', 'skyriss SAY_FEAR_1');
INSERT INTO `script_texts` VALUES ('-1552007', 'I will show you horrors undreamed of!', null, null, null, null, null, null, null, null, '11130', '1', '0', '0', 'skyriss SAY_FEAR_2');
INSERT INTO `script_texts` VALUES ('-1552008', 'We span the universe, as countless as the stars!', null, null, null, null, null, null, null, null, '11131', '1', '0', '0', 'skyriss SAY_IMAGE');
INSERT INTO `script_texts` VALUES ('-1552009', 'I am merely one of... infinite multitudes.', null, null, null, null, null, null, null, null, '11126', '1', '0', '0', 'skyriss SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1552010', 'Where in Bonzo\'s brass buttons am I? And who are-- yaaghh, that\'s one mother of a headache!', null, null, null, null, null, null, null, null, '11171', '1', '0', '0', 'millhouse SAY_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1552011', '\"Lowly\"? I don\'t care who you are friend, no one refers to the mighty Millhouse Manastorm as \"Lowly\"! I have no idea what goes on here, but I will gladly join your fight against this impudent imbecile! Prepare to defend yourself, cretin!', null, null, null, null, null, null, null, null, '11172', '1', '0', '0', 'millhouse SAY_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1552012', 'I just need to get some things ready first. You guys go ahead and get started. I need to summon up some water...', null, null, null, null, null, null, null, null, '11173', '1', '0', '0', 'millhouse SAY_WATER');
INSERT INTO `script_texts` VALUES ('-1552013', 'Fantastic! Next, some protective spells. Yes! Now we\'re cookin\'', null, null, null, null, null, null, null, null, '11174', '1', '0', '0', 'millhouse SAY_BUFFS');
INSERT INTO `script_texts` VALUES ('-1552014', 'And of course i\'ll need some mana. You guys are gonna love this, just wait.', null, null, null, null, null, null, null, null, '11175', '1', '0', '0', 'millhouse SAY_DRINK');
INSERT INTO `script_texts` VALUES ('-1552015', 'Aaalllriiiight!! Who ordered up an extra large can of whoop-ass?', null, null, null, null, null, null, null, null, '11176', '1', '0', '0', 'millhouse SAY_READY');
INSERT INTO `script_texts` VALUES ('-1552016', 'I didn\'t even break a sweat on that one.', null, null, null, null, null, null, null, null, '11177', '1', '0', '0', 'millhouse SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1552017', 'You guys, feel free to jump in anytime.', null, null, null, null, null, null, null, null, '11178', '1', '0', '0', 'millhouse SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1552018', 'I\'m gonna light you up, sweet cheeks!', null, null, null, null, null, null, null, null, '11179', '1', '0', '0', 'millhouse SAY_PYRO');
INSERT INTO `script_texts` VALUES ('-1552019', 'Ice, ice, baby!', null, null, null, null, null, null, null, null, '11180', '1', '0', '0', 'millhouse SAY_ICEBLOCK');
INSERT INTO `script_texts` VALUES ('-1552020', 'Heal me! Oh, for the love of all that is holy, HEAL me! I\'m dying!', null, null, null, null, null, null, null, null, '11181', '1', '0', '0', 'millhouse SAY_LOWHP');
INSERT INTO `script_texts` VALUES ('-1552021', 'You\'ll be hearing from my lawyer...', null, null, null, null, null, null, null, null, '11182', '1', '0', '0', 'millhouse SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1552022', 'Who\'s bad? Who\'s bad? That\'s right: we bad!', null, null, null, null, null, null, null, null, '11183', '1', '0', '0', 'millhouse SAY_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1552023', 'I knew the prince would be angry but, I... I have not been myself. I had to let them out! The great one speaks to me, you see. Wait--outsiders. Kael\'thas did not send you! Good... I\'ll just tell the prince you released the prisoners!', null, null, null, null, null, null, null, null, '11222', '1', '0', '0', 'mellichar YELL_INTRO1');
INSERT INTO `script_texts` VALUES ('-1552024', 'The naaru kept some of the most dangerous beings in existence here in these cells. Let me introduce you to another...', null, null, null, null, null, null, null, null, '11223', '1', '0', '0', 'mellichar YELL_INTRO2');
INSERT INTO `script_texts` VALUES ('-1552025', 'Yes, yes... another! Your will is mine!', null, null, null, null, null, null, null, null, '11224', '1', '0', '0', 'mellichar YELL_RELEASE1');
INSERT INTO `script_texts` VALUES ('-1552026', 'Behold another terrifying creature of incomprehensible power!', null, null, null, null, null, null, null, null, '11225', '1', '0', '0', 'mellichar YELL_RELEASE2A');
INSERT INTO `script_texts` VALUES ('-1552027', 'What is this? A lowly gnome? I will do better, O\'great one.', null, null, null, null, null, null, null, null, '11226', '1', '0', '0', 'mellichar YELL_RELEASE2B');
INSERT INTO `script_texts` VALUES ('-1552028', 'Anarchy! Bedlam! Oh, you are so wise! Yes, I see it now, of course!', null, null, null, null, null, null, null, null, '11227', '1', '0', '0', 'mellichar YELL_RELEASE3');
INSERT INTO `script_texts` VALUES ('-1552029', 'One final cell remains. Yes, O\'great one, right away!', null, null, null, null, null, null, null, null, '11228', '1', '0', '0', 'mellichar YELL_RELEASE4');
INSERT INTO `script_texts` VALUES ('-1552030', 'Welcome, O\'great one. I am your humble servant.', null, null, null, null, null, null, null, null, '11229', '1', '0', '0', 'mellichar YELL_WELCOME');
INSERT INTO `script_texts` VALUES ('-1553000', 'What are you doing? These specimens are very delicate!', null, null, null, null, null, null, null, null, '11144', '1', '0', '0', 'freywinn SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1553001', 'Your life cycle is now concluded!', null, null, null, null, null, null, null, null, '11145', '1', '0', '0', 'freywinn SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1553002', 'You will feed the worms.', null, null, null, null, null, null, null, null, '11146', '1', '0', '0', 'freywinn SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1553003', 'Endorel aluminor!', null, null, null, null, null, null, null, null, '11147', '1', '0', '0', 'freywinn SAY_TREE_1');
INSERT INTO `script_texts` VALUES ('-1553004', 'Nature bends to my will!', null, null, null, null, null, null, null, null, '11148', '1', '0', '0', 'freywinn SAY_TREE_2');
INSERT INTO `script_texts` VALUES ('-1553005', 'The specimens...must be preserved.', null, null, null, null, null, null, null, null, '11149', '1', '0', '0', 'freywinn SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1553006', '%s emits a strange noise.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'laj EMOTE_SUMMON');
INSERT INTO `script_texts` VALUES ('-1553007', 'Who disturbs this sanctuary?', null, null, null, null, null, null, null, null, '11230', '1', '0', '0', 'warp SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1553008', 'You must die! But wait: this does not--No, no... you must die!', null, null, null, null, null, null, null, null, '11231', '1', '0', '0', 'warp SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1553009', 'What am I doing? Why do I...', null, null, null, null, null, null, null, null, '11232', '1', '0', '0', 'warp SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1553010', 'Children, come to me!', null, null, null, null, null, null, null, null, '11233', '1', '0', '0', 'warp SAY_SUMMON_1');
INSERT INTO `script_texts` VALUES ('-1553011', 'Maybe this is not--No, we fight! Come to my aid.', null, null, null, null, null, null, null, null, '11234', '1', '0', '0', 'warp SAY_SUMMON_2');
INSERT INTO `script_texts` VALUES ('-1553012', 'So... confused. Do not... belong here!', null, null, null, null, null, null, null, null, '11235', '1', '0', '0', 'warp SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1554000', 'I predict a painful death.', null, null, null, null, null, null, null, null, '11101', '1', '0', '0', 'gyro SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1554001', 'Measure twice; cut once!', null, null, null, null, null, null, null, null, '11104', '1', '0', '0', 'gyro SAY_SAW_ATTACK1');
INSERT INTO `script_texts` VALUES ('-1554002', 'If my division is correct, you should be quite dead.', null, null, null, null, null, null, null, null, '11105', '1', '0', '0', 'gyro SAY_SAW_ATTACK2');
INSERT INTO `script_texts` VALUES ('-1554003', 'Your strategy was flawed!', null, null, null, null, null, null, null, null, '11102', '1', '0', '0', 'gyro SAY_SLAY1');
