INSERT INTO `script_texts` VALUES ('-1554019', 'Anu... bala belore...alon.', null, null, null, null, null, null, null, null, '11192', '1', '0', '0', 'sepethrea SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1554020', 'We are on a strict timetable. You will not interfere!', null, null, null, null, null, null, null, null, '11193', '1', '0', '0', 'pathaleon SAY_AGGRO');
