INSERT INTO `script_texts` VALUES ('-1554026', 'Looks like you lose.', null, null, null, null, null, null, null, null, '11195', '1', '0', '0', 'pathaleon SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1554027', 'The project will... continue.', null, null, null, null, null, null, null, null, '11200', '1', '0', '0', 'pathaleon SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1555000', 'Infidels have invaded the sanctuary! Sniveling pests...You have yet to learn the true meaning of agony!', null, null, null, null, null, null, null, null, '10473', '1', '0', '0', 'hellmaw SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1555001', 'Pathetic mortals! You will pay dearly!', null, null, null, null, null, null, null, null, '10475', '1', '0', '0', 'hellmaw SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1555002', 'I will break you!', null, null, null, null, null, null, null, null, '10476', '1', '0', '0', 'hellmaw SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1555003', 'Finally! Something to relieve the tedium!', null, null, null, null, null, null, null, null, '10477', '1', '0', '0', 'hellmaw SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1555004', 'Aid me, you fools, before it\'s too late!', null, null, null, null, null, null, null, null, '10474', '1', '0', '0', 'hellmaw SAY_HELP');
