INSERT INTO `script_texts` VALUES ('-1555015', 'Nice try!', null, null, null, null, null, null, null, null, '10490', '1', '0', '0', 'blackhearth SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1555016', 'Help us, hurry!', null, null, null, null, null, null, null, null, '10485', '1', '0', '0', 'blackhearth SAY_HELP');
