INSERT INTO `script_texts` VALUES ('-1564097', 'Akama... your duplicity is hardly surprising. I should have slaughtered you and your malformed brethren long ago.', null, null, null, null, null, null, null, null, '11463', '1', '0', '0', 'illidan SAY_CONVO_1');
INSERT INTO `script_texts` VALUES ('-1564098', 'We\'ve come to end your reign, Illidan. My people and all of Outland shall be free!', null, null, null, null, null, null, null, null, '11389', '1', '0', '25', 'illidan SAY_CONVO_2');
