INSERT INTO `script_texts` VALUES ('-1564102', 'Is this it, mortals? Is this all the fury you can muster?', null, null, null, null, null, null, null, null, '11476', '1', '0', '0', 'illidan SAY_CONVO_6');
INSERT INTO `script_texts` VALUES ('-1564103', 'Their fury pales before mine, Illidan. We have some unsettled business between us.', null, null, null, null, null, null, null, null, '11491', '1', '0', '5', 'illidan SAY_CONVO_7');
INSERT INTO `script_texts` VALUES ('-1564104', 'Maiev... How is this even possible?', null, null, null, null, null, null, null, null, '11477', '1', '0', '1', 'illidan SAY_CONVO_8');
