INSERT INTO `script_texts` VALUES ('-1564106', 'Feel the hatred of ten thousand years!', null, null, null, null, null, null, null, null, '11470', '1', '0', '0', 'illidan SAY_CONVO_10');
INSERT INTO `script_texts` VALUES ('-1564107', 'Ahh... It is finished. You are beaten.', null, null, null, null, null, null, null, null, '11496', '1', '0', '0', 'illidan SAY_CONVO_11');
INSERT INTO `script_texts` VALUES ('-1564108', 'You have won... Maiev...but the huntress... is nothing...without the hunt... you... are nothing... without me..', null, null, null, null, null, null, null, null, '11478', '1', '0', '65', 'illidan SAY_CONVO_12');
