INSERT INTO `script_texts` VALUES ('-1568013', 'I be losin\' me patience! Go on: make dem wish dey was never born!', null, null, null, null, null, null, null, null, '12069', '1', '0', '0', 'nalorakk SAY_WAVE4_PLATFORM');
