INSERT INTO `script_texts` VALUES ('-1568024', 'Da eagles gonna bear your spirits to me. Your sacrifice is not gonna be in vein!', null, null, null, null, null, null, null, null, '12122', '1', '0', '0', 'akilzon SAY_EVENT1');
INSERT INTO `script_texts` VALUES ('-1568025', 'Your death gonna be quick, strangers. You shoulda never have come to this place...', null, null, null, null, null, null, null, null, '12123', '1', '0', '0', 'akilzon SAY_EVENT2');
INSERT INTO `script_texts` VALUES ('-1568026', 'I be da predator! You da prey...', null, null, null, null, null, null, null, null, '12013', '1', '0', '0', 'akilzon SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1568027', 'Feed, me bruddahs!', null, null, null, null, null, null, null, null, '12014', '1', '0', '0', 'akilzon SAY_SUMMON');
INSERT INTO `script_texts` VALUES ('-1568028', 'Come, and join me bruddahs!', null, null, null, null, null, null, null, null, '12015', '1', '0', '0', 'akilzon SAY_SUMMON_ALT');
INSERT INTO `script_texts` VALUES ('-1568029', 'All you be doing is wasting my time!', null, null, null, null, null, null, null, null, '12016', '1', '0', '0', 'akilzon SAY_ENRAGE');
