INSERT INTO `script_texts` VALUES ('-1568035', 'I fight wit\' untamed spirit....', null, null, null, null, null, null, null, null, '12021', '1', '0', '0', 'halazzi SAY_SPLIT');
INSERT INTO `script_texts` VALUES ('-1568036', 'Spirit, come back to me!', null, null, null, null, null, null, null, null, '12022', '1', '0', '0', 'halazzi SAY_MERGE');
INSERT INTO `script_texts` VALUES ('-1568037', 'Me gonna carve ya now!', null, null, null, null, null, null, null, null, '12023', '1', '0', '0', 'halazzi SAY_SABERLASH1');
INSERT INTO `script_texts` VALUES ('-1568038', 'You gonna leave in pieces!', null, null, null, null, null, null, null, null, '12024', '1', '0', '0', 'halazzi SAY_SABERLASH2');
INSERT INTO `script_texts` VALUES ('-1568039', 'Whatch you be doing? Pissin\' yourselves...', null, null, null, null, null, null, null, null, '12025', '1', '0', '0', 'halazzi SAY_BERSERK');
INSERT INTO `script_texts` VALUES ('-1568040', 'You cant fight the power!', null, null, null, null, null, null, null, null, '12026', '1', '0', '0', 'halazzi SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1568041', 'You gonna fail!', null, null, null, null, null, null, null, null, '12027', '1', '0', '0', 'halazzi SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1568042', 'Chaga... choka\'jinn.', null, null, null, null, null, null, null, null, '12028', '1', '0', '0', 'halazzi SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1568043', 'Come, fools. Fill ma empty cages...', null, null, null, null, null, null, null, null, '12029', '1', '0', '0', 'halazzi SAY_EVENT1');
INSERT INTO `script_texts` VALUES ('-1568044', 'I be waitin, strangers. Your deaths gonna make me stronger!', null, null, null, null, null, null, null, null, '12030', '1', '0', '0', 'halazzi SAY_EVENT2');
INSERT INTO `script_texts` VALUES ('-1568045', 'Da shadow gonna fall on you...', null, null, null, null, null, null, null, null, '12041', '1', '0', '0', 'malacrass SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1568046', 'Ya don\'t kill me yet, ya don\'t get another chance!', null, null, null, null, null, null, null, null, '12042', '1', '0', '0', 'malacrass SAY_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1568047', 'Dis a nightmare ya don\' wake up from!', null, null, null, null, null, null, null, null, '12043', '1', '0', '0', 'malacrass SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1568048', 'Azzaga choogo zinn!', null, null, null, null, null, null, null, null, '12044', '1', '0', '0', 'malacrass SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1568049', 'Your will belong ta me now!', null, null, null, null, null, null, null, null, '12045', '1', '0', '0', 'malacrass SAY_SOUL_SIPHON');
INSERT INTO `script_texts` VALUES ('-1568050', 'Darkness comin\' for you...', null, null, null, null, null, null, null, null, '12046', '1', '0', '0', 'malacrass SAY_DRAIN_POWER');
INSERT INTO `script_texts` VALUES ('-1568051', 'Your soul gonna bleed!', null, null, null, null, null, null, null, null, '12047', '1', '0', '0', 'malacrass SAY_SPIRIT_BOLTS');
INSERT INTO `script_texts` VALUES ('-1568052', 'It not gonna make no difference.', null, null, null, null, null, null, null, null, '12048', '1', '0', '0', 'malacrass SAY_ADD_DIED1');
INSERT INTO `script_texts` VALUES ('-1568053', 'You gonna die worse dan him.', null, null, null, null, null, null, null, null, '12049', '1', '0', '0', 'malacrass SAY_ADD_DIED2');
INSERT INTO `script_texts` VALUES ('-1568054', 'Dat no bodda me.', null, null, null, null, null, null, null, null, '12050', '1', '0', '0', 'malacrass SAY_ADD_DIED3');
INSERT INTO `script_texts` VALUES ('-1568055', 'Dis not... da end of me...', null, null, null, null, null, null, null, null, '12051', '1', '0', '0', 'malacrass SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1568056', 'Everybody always wanna take from us. Now we gonna start takin\' back. Anybody who get in our way...gonna drown in dey own blood! Da Amani empire be back now...seekin\' vengeance. And we gonna start wit\' you.', null, null, null, null, null, null, null, null, '12090', '1', '0', '0', 'zuljin SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1568057', 'Nobody badduh dan me!', null, null, null, null, null, null, null, null, '12091', '1', '0', '0', 'zuljin SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1568058', 'Got me some new tricks... like me brudda bear....', null, null, null, null, null, null, null, null, '12092', '1', '0', '0', 'zuljin SAY_BEAR_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1568059', 'Dere be no hidin\' from da eagle!', null, null, null, null, null, null, null, null, '12093', '1', '0', '0', 'zuljin SAY_EAGLE_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1568060', 'Let me introduce you to me new bruddas: fang and claw!', null, null, null, null, null, null, null, null, '12094', '1', '0', '0', 'zuljin SAY_LYNX_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1568061', 'Ya don\' have to look to da sky to see da dragonhawk!', null, null, null, null, null, null, null, null, '12095', '1', '0', '0', 'zuljin SAY_DRAGONHAWK_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1568062', 'Fire kill you just as quick!', null, null, null, null, null, null, null, null, '12096', '1', '0', '0', 'zuljin SAY_FIRE_BREATH');
INSERT INTO `script_texts` VALUES ('-1568063', 'You too slow! Me too strong!', null, null, null, null, null, null, null, null, '12097', '1', '0', '0', 'zuljin SAY_BERSERK');
INSERT INTO `script_texts` VALUES ('-1568064', 'Da Amani de chuka!', null, null, null, null, null, null, null, null, '12098', '1', '0', '0', 'zuljin SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1568065', 'Lot more gonna fall like you!', null, null, null, null, null, null, null, null, '12099', '1', '0', '0', 'zuljin SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1568066', 'Mebbe me fall...but da Amani empire...never gonna die...', null, null, null, null, null, null, null, null, '12100', '1', '0', '0', 'zuljin SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1568067', 'Zul\'jin got a surprise for ya...', null, null, null, null, null, null, null, null, '12052', '1', '0', '0', 'zulaman SAY_INST_RELEASE');
INSERT INTO `script_texts` VALUES ('-1568068', 'Da spirits gonna feast today! Begin da ceremonies, sacrifice da prisoners... make room for our new guests!', null, null, null, null, null, null, null, null, '12053', '1', '0', '0', 'zulaman SAY_INST_BEGIN');
INSERT INTO `script_texts` VALUES ('-1568069', 'Take your pick, trespassers! Any of ma priests be happy to accommodate ya.', null, null, null, null, null, null, null, null, '12054', '1', '0', '0', 'zulaman SAY_INST_PROGRESS_1');
INSERT INTO `script_texts` VALUES ('-1568070', 'Don\'t be shy. Thousands have come before you. Ya not be alone in your service.', null, null, null, null, null, null, null, null, '12055', '1', '0', '0', 'zulaman SAY_INST_PROGRESS_2');
INSERT INTO `script_texts` VALUES ('-1568071', 'Ya gonna fail, strangers. Many try before you, but dey only make us stronger!', null, null, null, null, null, null, null, null, '12056', '1', '0', '0', 'zulaman SAY_INST_PROGRESS_3');
INSERT INTO `script_texts` VALUES ('-1568072', 'Your efforts was in vain, trespassers. The rituals nearly be complete.', null, null, null, null, null, null, null, null, '12057', '1', '0', '0', 'zulaman SAY_INST_WARN_1');
INSERT INTO `script_texts` VALUES ('-1568073', 'Soon da cages gonna be empty, da sacrifices be complete, and you gonna take dere places.', null, null, null, null, null, null, null, null, '12058', '1', '0', '0', 'zulaman SAY_INST_WARN_2');
INSERT INTO `script_texts` VALUES ('-1568074', 'Time be running low, strangers. Soon you gonna join da souls of dem ya failed to save.', null, null, null, null, null, null, null, null, '12059', '1', '0', '0', 'zulaman SAY_INST_WARN_3');
INSERT INTO `script_texts` VALUES ('-1568075', 'Make haste, ma priests! Da rituals must not be interrupted!', null, null, null, null, null, null, null, null, '12060', '1', '0', '0', 'zulaman SAY_INST_WARN_4');
INSERT INTO `script_texts` VALUES ('-1568076', 'Ya make a good try... but now you gonna join da ones who already fall.', null, null, null, null, null, null, null, null, '12061', '1', '0', '0', 'zulaman SAY_INST_SACRIF1');
INSERT INTO `script_texts` VALUES ('-1568077', 'Ya not do too bad. Ya efforts [...] for a small time. Come to me now. Ya prove yourself worthy offerings.', null, null, null, null, null, null, null, null, '12062', '1', '0', '0', 'zulaman SAY_INST_SACRIF2');
INSERT INTO `script_texts` VALUES ('-1568078', 'Watch now. Every offering gonna strengthen our ties to da spirit world. Soon, we gonna be unstoppable!', null, null, null, null, null, null, null, null, '12065', '1', '0', '0', 'zulaman SAY_INST_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1568079', 'Suit yourself. At least five of you must assist me if we\'re to get inside. Follow me.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'harrison SAY_START');
INSERT INTO `script_texts` VALUES ('-1568080', 'According to my calculations, if enough of us bang the gong at once the seal on these doors will break and we can enter.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'harrison SAY_AT_GONG');
INSERT INTO `script_texts` VALUES ('-1568081', 'I\'ve researched this site extensively and I won\'t allow any dim-witted treasure hunters to swoop in and steal what belongs to in a museum. I\'ll lead this charge.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'harrison SAY_OPEN_ENTRANCE');
INSERT INTO `script_texts` VALUES ('-1574000', 'Your blood is mine!', null, null, null, null, null, null, null, null, '13221', '1', '0', '0', 'keleseth SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1574001', 'Not so fast.', null, null, null, null, null, null, null, null, '13222', '1', '0', '0', 'keleseth SAY_FROSTTOMB');
INSERT INTO `script_texts` VALUES ('-1574002', 'Aranal, lidel! Their fate shall be yours!', null, null, null, null, null, null, null, null, '13224', '1', '0', '0', 'keleseth SAY_SKELETONS');
INSERT INTO `script_texts` VALUES ('-1574003', 'Darkness waits!', null, null, null, null, null, null, null, null, '13223', '1', '0', '0', 'keleseth SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1574004', 'I join... the night.', null, null, null, null, null, null, null, null, '13225', '1', '0', '0', 'keleseth SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1574005', 'I\'ll paint my face with your blood!', null, null, null, null, null, null, null, null, '13207', '1', '0', '0', 'ingvar SAY_AGGRO_FIRST');
INSERT INTO `script_texts` VALUES ('-1574006', 'I return! A second chance to carve out your skull!', null, null, null, null, null, null, null, null, '13209', '1', '0', '0', 'ingvar SAY_AGGRO_SECOND');
INSERT INTO `script_texts` VALUES ('-1574007', 'My life for the... death god!', null, null, null, null, null, null, null, null, '13213', '1', '0', '0', 'ingvar SAY_DEATH_FIRST');
INSERT INTO `script_texts` VALUES ('-1574008', 'No! I can do... better! I can...', null, null, null, null, null, null, null, null, '13211', '1', '0', '0', 'ingvar SAY_DEATH_SECOND');
INSERT INTO `script_texts` VALUES ('-1574009', 'Mjul orm agn gjor!', null, null, null, null, null, null, null, null, '13212', '1', '0', '0', 'ingvar SAY_KILL_FIRST');
INSERT INTO `script_texts` VALUES ('-1574010', 'I am a warrior born!', null, null, null, null, null, null, null, null, '13214', '1', '0', '0', 'ingvar SAY_KILL_SECOND');
INSERT INTO `script_texts` VALUES ('-1574011', 'Dalronn! See if you can muster the nerve to join my attack!', null, null, null, null, null, null, null, null, '13229', '1', '0', '0', 'skarvald SAY_SKA_AGGRO');
INSERT INTO `script_texts` VALUES ('-1574012', 'Not... over... yet.', null, null, null, null, null, null, null, null, '13230', '1', '0', '0', 'skarvald SAY_SKA_DEATH');
INSERT INTO `script_texts` VALUES ('-1574013', 'A warrior\'s death.', null, null, null, null, null, null, null, null, '13231', '1', '0', '0', 'skarvald SAY_SKA_DEATH_REAL');
INSERT INTO `script_texts` VALUES ('-1574014', '???', null, null, null, null, null, null, null, null, '13232', '1', '0', '0', 'skarvald SAY_SKA_KILL');
INSERT INTO `script_texts` VALUES ('-1574015', 'Pagh! What sort of necromancer lets death stop him? I knew you were worthless!', null, null, null, null, null, null, null, null, '13233', '1', '0', '0', 'skarvald SAY_SKA_DAL_DIES_REPLY');
INSERT INTO `script_texts` VALUES ('-1574016', 'By all means, don\'t assess the situation, you halfwit! Just jump into the fray!', null, null, null, null, null, null, null, null, '13199', '1', '0', '0', 'dalronn SAY_DAL_AGGRO_REPLY');
INSERT INTO `script_texts` VALUES ('-1574017', 'See... you... soon.', null, null, null, null, null, null, null, null, '13200', '1', '0', '0', 'dalronn SAY_DAL_DEATH');
INSERT INTO `script_texts` VALUES ('-1574018', 'There\'s no... greater... glory.', null, null, null, null, null, null, null, null, '13201', '1', '0', '0', 'dalronn SAY_DAL_DEATH_REAL');
INSERT INTO `script_texts` VALUES ('-1574019', 'You may serve me yet.', null, null, null, null, null, null, null, null, '13202', '1', '0', '0', 'dalronn SAY_DAL_KILL');
INSERT INTO `script_texts` VALUES ('-1574020', 'Skarvald, you incompetent slug! Return and make yourself useful!', null, null, null, null, null, null, null, null, '13203', '1', '0', '0', 'dalronn SAY_DAL_SKA_DIES_REPLY');
INSERT INTO `script_texts` VALUES ('-1574021', '%s casts Frost Tomb on $N', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'keleseth EMOTE_FROST_TOMB');
INSERT INTO `script_texts` VALUES ('-1574022', '%s roars!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'ingvar EMOTE_ROAR');
INSERT INTO `script_texts` VALUES ('-1575000', 'My liege! I have done as you asked, and now beseech you for your blessing!', null, null, null, null, null, null, null, null, '13856', '1', '0', '0', 'svala SAY_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1575001', 'Your sacrifice is a testament to your obedience. Indeed you are worthy of this charge. Arise, and forever be known as Svala Sorrowgrave!', null, null, null, null, null, null, null, null, '14732', '1', '0', '0', 'svala SAY_INTRO_2_ARTHAS');
INSERT INTO `script_texts` VALUES ('-1575002', 'The sensation is... beyond my imagining. I am yours to command, my king.', null, null, null, null, null, null, null, null, '13857', '1', '0', '0', 'svala SAY_INTRO_3');
INSERT INTO `script_texts` VALUES ('-1575003', 'Your first test awaits you. Destroy our uninvited guests.', null, null, null, null, null, null, null, null, '14733', '1', '0', '0', 'svala SAY_INTRO_4_ARTHAS');
INSERT INTO `script_texts` VALUES ('-1575004', 'I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King!', null, null, null, null, null, null, null, null, '13858', '1', '0', '0', 'svala SAY_INTRO_5');
INSERT INTO `script_texts` VALUES ('-1575005', 'I will vanquish your soul!', null, null, null, null, null, null, null, null, '13842', '1', '0', '0', 'svala SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1575006', 'You were a fool to challenge the power of the Lich King!', null, null, null, null, null, null, null, null, '13845', '1', '0', '0', 'svala SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1575007', 'Your will is done, my king.', null, null, null, null, null, null, null, null, '13847', '1', '0', '0', 'svala SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1575008', 'Another soul for my master.', null, null, null, null, null, null, null, null, '13848', '1', '0', '0', 'svala SAY_SLAY_3');
INSERT INTO `script_texts` VALUES ('-1575009', 'Your death approaches.', null, null, null, null, null, null, null, null, '13850', '1', '0', '0', 'svala SAY_SACRIFICE_1');
INSERT INTO `script_texts` VALUES ('-1575010', 'Go now to my master.', null, null, null, null, null, null, null, null, '13851', '1', '0', '0', 'svala SAY_SACRIFICE_2');
INSERT INTO `script_texts` VALUES ('-1575011', 'Your end is inevitable.', null, null, null, null, null, null, null, null, '13852', '1', '0', '0', 'svala SAY_SACRIFICE_3');
INSERT INTO `script_texts` VALUES ('-1575012', 'Yor-guul mak!', null, null, null, null, null, null, null, null, '13853', '1', '0', '0', 'svala SAY_SACRIFICE_4');
INSERT INTO `script_texts` VALUES ('-1575013', 'Any last words?', null, null, null, null, null, null, null, null, '13854', '1', '0', '0', 'svala SAY_SACRIFICE_5');
INSERT INTO `script_texts` VALUES ('-1575014', 'Nooo! I did not come this far... to...', null, null, null, null, null, null, null, null, '13855', '1', '0', '0', 'svala SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1575015', 'What this place? I will destroy you!', null, null, null, null, null, null, null, null, '13464', '1', '0', '0', 'gortok SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1575016', 'You die! That what master wants!', null, null, null, null, null, null, null, null, '13465', '1', '0', '0', 'gortok SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1575017', 'An easy task!', null, null, null, null, null, null, null, null, '13466', '1', '0', '0', 'gortok SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1575018', ' ', null, null, null, null, null, null, null, null, '13467', '1', '0', '0', 'gortok SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1575019', 'What mongrels dare intrude here? Look alive, my brothers! A feast for the one that brings me their heads!', null, null, null, null, null, null, null, null, '13497', '1', '0', '0', 'skadi SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1575020', 'Sear them to the bone!', null, null, null, null, null, null, null, null, '13498', '1', '0', '0', 'skadi SAY_DRAKEBREATH_1');
INSERT INTO `script_texts` VALUES ('-1575021', 'Go now! Leave nothing but ash in your wake!', null, null, null, null, null, null, null, null, '13499', '1', '0', '0', 'skadi SAY_DRAKEBREATH_2');
INSERT INTO `script_texts` VALUES ('-1575022', 'Cleanse our sacred halls with flame!', null, null, null, null, null, null, null, null, '13500', '1', '0', '0', 'skadi SAY_DRAKEBREATH_3');
INSERT INTO `script_texts` VALUES ('-1575023', 'I ask for ... to kill them, yet all I get is feeble whelps! By Ye.. SLAUGHTER THEM!', null, null, null, null, null, null, null, null, '13501', '1', '0', '0', 'skadi SAY_DRAKE_HARPOON_1');
INSERT INTO `script_texts` VALUES ('-1575024', 'If one more harpoon touches my drake I\'ll flae my miserable heins.', null, null, null, null, null, null, null, null, '13502', '1', '0', '0', 'skadi SAY_DRAKE_HARPOON_2');
INSERT INTO `script_texts` VALUES ('-1575025', 'Mjor Na Ul Kaval!', null, null, null, null, null, null, null, null, '13503', '1', '0', '0', 'skadi SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1575026', 'Not so brash now, are you?', null, null, null, null, null, null, null, null, '13504', '1', '0', '0', 'skadi SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1575027', 'I\'ll mount your skull from the highest tower!', null, null, null, null, null, null, null, null, '13505', '1', '0', '0', 'skadi SAY_KILL_3');
INSERT INTO `script_texts` VALUES ('-1575028', 'ARGH! You call that... an attack? I\'ll... show... aghhhh...', null, null, null, null, null, null, null, null, '13506', '1', '0', '0', 'skadi SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1575029', 'You motherless knaves! Your corpses will make fine morsels for my new drake!', null, null, null, null, null, null, null, null, '13507', '1', '0', '0', 'skadi SAY_DRAKE_DEATH');
INSERT INTO `script_texts` VALUES ('-1575030', '%s is within range of the harpoon launchers!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'skadi EMOTE_HARPOON_RANGE');
INSERT INTO `script_texts` VALUES ('-1575031', 'You invade my home and then dare to challenge me? I will tear the hearts from your chests and offer them as gifts to the death god! Rualg nja gaborr!', null, null, null, null, null, null, null, null, '13609', '1', '0', '0', 'ymiron SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1575032', 'Bjorn of the Black Storm! Honor me now with your presence!', null, null, null, null, null, null, null, null, '13610', '1', '0', '0', 'ymiron SAY_SUMMON_BJORN');
INSERT INTO `script_texts` VALUES ('-1575033', 'Haldor of the rocky cliffs, grant me your strength!', null, null, null, null, null, null, null, null, '13611', '1', '0', '0', 'ymiron SAY_SUMMON_HALDOR');
INSERT INTO `script_texts` VALUES ('-1575034', 'Ranulf of the screaming abyss, snuff these maggots with darkest night!', null, null, null, null, null, null, null, null, '13612', '1', '0', '0', 'ymiron SAY_SUMMON_RANULF');
INSERT INTO `script_texts` VALUES ('-1575035', 'Tor of the Brutal Siege! Bestow your might upon me!', null, null, null, null, null, null, null, null, '13613', '1', '0', '0', 'ymiron SAY_SUMMON_TORGYN');
INSERT INTO `script_texts` VALUES ('-1575036', 'Your death is only the beginning!', null, null, null, null, null, null, null, null, '13614', '1', '0', '0', 'ymiron SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1575037', 'You have failed your people!', null, null, null, null, null, null, null, null, '13615', '1', '0', '0', 'ymiron SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1575038', 'There is a reason I am king!', null, null, null, null, null, null, null, null, '13616', '1', '0', '0', 'ymiron SAY_SLAY_3');
INSERT INTO `script_texts` VALUES ('-1575039', 'Bleed no more!', null, null, null, null, null, null, null, null, '13617', '1', '0', '0', 'ymiron SAY_SLAY_4');
INSERT INTO `script_texts` VALUES ('-1575040', 'What... awaits me... now?', null, null, null, null, null, null, null, null, '13618', '1', '0', '0', 'ymiron SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1576000', 'You know what they say about curiosity.', null, null, null, null, null, null, null, null, '13319', '1', '0', '0', 'telestra SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1576001', 'I\'ll give you more than you can handle.', null, null, null, null, null, null, null, null, '13321', '1', '0', '0', 'telestra SAY_SPLIT_1');
INSERT INTO `script_texts` VALUES ('-1576002', 'There\'s plenty of me to go around.', null, null, null, null, null, null, null, null, '13322', '1', '0', '0', 'telestra SAY_SPLIT_2');
INSERT INTO `script_texts` VALUES ('-1576003', 'Now to finish the job!', null, null, null, null, null, null, null, null, '13323', '1', '0', '0', 'telestra SAY_MERGE');
INSERT INTO `script_texts` VALUES ('-1576004', 'Death becomes you!', null, null, null, null, null, null, null, null, '13324', '1', '0', '0', 'telestra SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1576005', 'Damn the... luck.', null, null, null, null, null, null, null, null, '13320', '1', '0', '0', 'telestra SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1576006', 'Chaos beckons.', null, null, null, null, null, null, null, null, '13186', '1', '0', '0', 'anomalus SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1576007', 'Reality... unwoven.', null, null, null, null, null, null, null, null, '13188', '1', '0', '0', 'anomalus SAY_RIFT');
INSERT INTO `script_texts` VALUES ('-1576008', 'Indestructible.', null, null, null, null, null, null, null, null, '13189', '1', '0', '0', 'anomalus SAY_SHIELD');
INSERT INTO `script_texts` VALUES ('-1576009', 'Expiration... is necesarry.', null, null, null, null, null, null, null, null, '13274', '1', '0', '0', 'anomalus SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1576010', 'Of course.', null, null, null, null, null, null, null, null, '13187', '1', '0', '0', 'anomalus SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1576011', 'Noo!', null, null, null, null, null, null, null, null, '13328', '1', '0', '0', 'ormorok SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1576012', '???', null, null, null, null, null, null, null, null, '13329', '1', '0', '0', 'ormorok SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1576013', 'Baaack!', null, null, null, null, null, null, null, null, '13331', '1', '0', '0', 'ormorok SAY_REFLECT');
INSERT INTO `script_texts` VALUES ('-1576014', 'Bleeeed!', null, null, null, null, null, null, null, null, '13332', '1', '0', '0', 'ormorok SAY_ICESPIKE');
INSERT INTO `script_texts` VALUES ('-1576015', 'Aaggh!', null, null, null, null, null, null, null, null, '13330', '1', '0', '0', 'ormorok SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1576016', 'Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now!', null, null, null, null, null, null, null, null, '13450', '1', '0', '0', 'keristrasza SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1576017', 'Stay. Enjoy your final moments.', null, null, null, null, null, null, null, null, '13451', '1', '0', '0', 'keristrasza SAY_CRYSTAL_NOVA');
INSERT INTO `script_texts` VALUES ('-1576018', 'Finish it! Finish it! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again!', null, null, null, null, null, null, null, null, '13452', '1', '0', '0', 'keristrasza SAY_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1576019', 'Now we\'ve come to the truth!', null, null, null, null, null, null, null, null, '13453', '1', '0', '0', 'keristrasza SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1576020', 'Dragonqueen... Life-Binder... preserve... me.', null, null, null, null, null, null, null, null, '13454', '1', '0', '0', 'keristrasza SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1576021', '%s opens a Chaotic Rift!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'anomalus EMOTE_OPEN_RIFT');
INSERT INTO `script_texts` VALUES ('-1576022', '%s shields himself and divert his power to the rifts!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'anomalus EMOTE_SHIELD');
INSERT INTO `script_texts` VALUES ('-1580000', 'Aggh! No longer will I be a slave to Malygos! Challenge me and you will be destroyed!', null, null, null, null, null, null, null, null, '12422', '1', '0', '0', 'kalecgos SAY_EVIL_AGGRO');
INSERT INTO `script_texts` VALUES ('-1580001', 'I will purge you!', null, null, null, null, null, null, null, null, '12423', '1', '0', '0', 'kalecgos SAY_EVIL_SPELL1');
INSERT INTO `script_texts` VALUES ('-1580002', 'Your pain has only begun!', null, null, null, null, null, null, null, null, '12424', '1', '0', '0', 'kalecgos SAY_EVIL_SPELL2');
INSERT INTO `script_texts` VALUES ('-1580003', 'In the name of Kil\'jaeden!', null, null, null, null, null, null, null, null, '12425', '1', '0', '0', 'kalecgos SAY_EVIL_SLAY1');
INSERT INTO `script_texts` VALUES ('-1580004', 'You were warned!', null, null, null, null, null, null, null, null, '12426', '1', '0', '0', 'kalecgos SAY_EVIL_SLAY2');
INSERT INTO `script_texts` VALUES ('-1580005', 'My awakening is complete! You shall all perish!', null, null, null, null, null, null, null, null, '12427', '1', '0', '0', 'kalecgos SAY_EVIL_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1580006', 'I need... your help... Cannot... resist him... much longer...', null, null, null, null, null, null, null, null, '12428', '1', '0', '0', 'kalecgos humanoid SAY_GOOD_AGGRO');
INSERT INTO `script_texts` VALUES ('-1580007', 'Aaahhh! Help me, before I lose my mind!', null, null, null, null, null, null, null, null, '12429', '1', '0', '0', 'kalecgos humanoid SAY_GOOD_NEAR_DEATH');
INSERT INTO `script_texts` VALUES ('-1580008', 'Hurry! There is not much of me left!', null, null, null, null, null, null, null, null, '12430', '1', '0', '0', 'kalecgos humanoid SAY_GOOD_NEAR_DEATH2');
INSERT INTO `script_texts` VALUES ('-1580009', 'I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well.', null, null, null, null, null, null, null, null, '12431', '1', '0', '0', 'kalecgos humanoid SAY_GOOD_PLRWIN');
INSERT INTO `script_texts` VALUES ('-1580010', 'There will be no reprieve. My work here is nearly finished.', null, null, null, null, null, null, null, null, '12451', '1', '0', '0', 'sathrovarr SAY_SATH_AGGRO');
INSERT INTO `script_texts` VALUES ('-1580011', 'I\'m... never on... the losing... side...', null, null, null, null, null, null, null, null, '12452', '1', '0', '0', 'sathrovarr SAY_SATH_DEATH');
INSERT INTO `script_texts` VALUES ('-1580012', 'Your misery is my delight!', null, null, null, null, null, null, null, null, '12453', '1', '0', '0', 'sathrovarr SAY_SATH_SPELL1');
INSERT INTO `script_texts` VALUES ('-1580013', 'I will watch you bleed!', null, null, null, null, null, null, null, null, '12454', '1', '0', '0', 'sathrovarr SAY_SATH_SPELL2');
INSERT INTO `script_texts` VALUES ('-1580014', 'Pitious mortal!', null, null, null, null, null, null, null, null, '12455', '1', '0', '0', 'sathrovarr SAY_SATH_SLAY1');
INSERT INTO `script_texts` VALUES ('-1580015', 'Haven\'t you heard? I always win!', null, null, null, null, null, null, null, null, '12456', '1', '0', '0', 'sathrovarr SAY_SATH_SLAY2');
INSERT INTO `script_texts` VALUES ('-1580016', 'I have toyed with you long enough!', null, null, null, null, null, null, null, null, '12457', '1', '0', '0', 'sathrovarr SAY_SATH_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1580017', 'Puny lizard! Death is the only answer you\'ll find here!', null, null, null, null, null, null, null, null, '12458', '1', '0', '0', 'brutallus YELL_INTRO');
INSERT INTO `script_texts` VALUES ('-1580018', 'Grah! Your magic is weak!', null, null, null, null, null, null, null, null, '12459', '1', '0', '0', 'brutallus YELL_INTRO_BREAK_ICE');
INSERT INTO `script_texts` VALUES ('-1580019', 'I will crush you!', null, null, null, null, null, null, null, null, '12460', '1', '0', '0', 'brutallus YELL_INTRO_CHARGE');
INSERT INTO `script_texts` VALUES ('-1580020', 'That was fun.', null, null, null, null, null, null, null, null, '12461', '1', '0', '0', 'brutallus YELL_INTRO_KILL_MADRIGOSA');
INSERT INTO `script_texts` VALUES ('-1580021', 'Come, try your luck!', null, null, null, null, null, null, null, null, '12462', '1', '0', '0', 'brutallus YELL_INTRO_TAUNT');
INSERT INTO `script_texts` VALUES ('-1580022', 'Ahh! More lambs to the slaughter!', null, null, null, null, null, null, null, null, '12463', '1', '0', '0', 'brutallus YELL_AGGRO');
INSERT INTO `script_texts` VALUES ('-1580023', 'Perish, insect!', null, null, null, null, null, null, null, null, '12464', '1', '0', '0', 'brutallus YELL_KILL1');
INSERT INTO `script_texts` VALUES ('-1580024', 'You are meat!', null, null, null, null, null, null, null, null, '12465', '1', '0', '0', 'brutallus YELL_KILL2');
INSERT INTO `script_texts` VALUES ('-1580025', 'Too easy!', null, null, null, null, null, null, null, null, '12466', '1', '0', '0', 'brutallus YELL_KILL3');
INSERT INTO `script_texts` VALUES ('-1580026', 'Bring the fight to me!', null, null, null, null, null, null, null, null, '12467', '1', '0', '0', 'brutallus YELL_LOVE1');
INSERT INTO `script_texts` VALUES ('-1580027', 'Another day, another glorious battle!', null, null, null, null, null, null, null, null, '12468', '1', '0', '0', 'brutallus YELL_LOVE2');
INSERT INTO `script_texts` VALUES ('-1580028', 'I live for this!', null, null, null, null, null, null, null, null, '12469', '1', '0', '0', 'brutallus YELL_LOVE3');
INSERT INTO `script_texts` VALUES ('-1580029', 'So much for a real challenge... Die!', null, null, null, null, null, null, null, null, '12470', '1', '0', '0', 'brutallus YELL_BERSERK');
INSERT INTO `script_texts` VALUES ('-1580030', 'Gah! Well done... Now... this gets... interesting...', null, null, null, null, null, null, null, null, '12471', '1', '0', '0', 'brutallus YELL_DEATH');
INSERT INTO `script_texts` VALUES ('-1580031', 'Hold, friends! There is information to be had before this devil meets his fate!', null, null, null, null, null, null, null, null, '12472', '1', '0', '0', 'madrigosa YELL_MADR_ICE_BARRIER');
INSERT INTO `script_texts` VALUES ('-1580032', 'Where is Anveena, demon? What has become of Kalec?', null, null, null, null, null, null, null, null, '12473', '1', '0', '0', 'madrigosa YELL_MADR_INTRO');
INSERT INTO `script_texts` VALUES ('-1580033', 'You will tell me where they are!', null, null, null, null, null, null, null, null, '12474', '1', '0', '0', 'madrigosa YELL_MADR_ICE_BLOCK');
INSERT INTO `script_texts` VALUES ('-1580034', 'Speak, I grow weary of asking!', null, null, null, null, null, null, null, null, '12475', '1', '0', '0', 'madrigosa YELL_MADR_TRAP');
INSERT INTO `script_texts` VALUES ('-1580035', 'Malygos, my lord! I did my best!', null, null, null, null, null, null, null, null, '12476', '1', '0', '0', 'madrigosa YELL_MADR_DEATH');
INSERT INTO `script_texts` VALUES ('-1585000', 'You only waste my time!', null, null, null, null, null, null, null, null, '12378', '1', '0', '0', 'selin SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1585001', 'My hunger knows no bounds!', null, null, null, null, null, null, null, null, '12381', '1', '0', '0', 'selin SAY_ENERGY');
INSERT INTO `script_texts` VALUES ('-1585002', 'Yes! I am a god!', null, null, null, null, null, null, null, null, '12382', '1', '0', '0', 'selin SAY_EMPOWERED');
INSERT INTO `script_texts` VALUES ('-1585003', 'Enough distractions!', null, null, null, null, null, null, null, null, '12388', '1', '0', '0', 'selin SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1585004', 'I am invincible!', null, null, null, null, null, null, null, null, '12385', '1', '0', '0', 'selin SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1585005', 'No! More... I must have more!', null, null, null, null, null, null, null, null, '12383', '1', '0', '0', 'selin SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1585006', '%s begins to channel from the nearby Fel Crystal...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'selin EMOTE_CRYSTAL');
INSERT INTO `script_texts` VALUES ('-1585007', 'Drain...life!', null, null, null, null, null, null, null, null, '12389', '1', '0', '0', 'vexallus SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1585008', 'Un...con...tainable.', null, null, null, null, null, null, null, null, '12392', '1', '0', '0', 'vexallus SAY_ENERGY');
INSERT INTO `script_texts` VALUES ('-1585009', 'Un...leash...', null, null, null, null, null, null, null, null, '12390', '1', '0', '0', 'vexallus SAY_OVERLOAD');
INSERT INTO `script_texts` VALUES ('-1585010', 'Con...sume.', null, null, null, null, null, null, null, null, '12393', '1', '0', '0', 'vexallus SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1585011', '%s discharges pure energy!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'vexallus EMOTE_DISCHARGE_ENERGY');
INSERT INTO `script_texts` VALUES ('-1585012', 'Annihilate them!', null, null, null, null, null, null, null, null, '12395', '1', '0', '0', 'delrissa SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1585013', 'Oh, the horror.', null, null, null, null, null, null, null, null, '12398', '1', '0', '0', 'delrissa LackeyDeath1');
INSERT INTO `script_texts` VALUES ('-1585014', 'Well, aren\'t you lucky?', null, null, null, null, null, null, null, null, '12400', '1', '0', '0', 'delrissa LackeyDeath2');
INSERT INTO `script_texts` VALUES ('-1585015', 'Now I\'m getting annoyed.', null, null, null, null, null, null, null, null, '12401', '1', '0', '0', 'delrissa LackeyDeath3');
INSERT INTO `script_texts` VALUES ('-1585016', 'Lackies be damned! I\'ll finish you myself!', null, null, null, null, null, null, null, null, '12403', '1', '0', '0', 'delrissa LackeyDeath4');
INSERT INTO `script_texts` VALUES ('-1585017', 'I call that a good start.', null, null, null, null, null, null, null, null, '12405', '1', '0', '0', 'delrissa PlayerDeath1');
INSERT INTO `script_texts` VALUES ('-1585018', 'I could have sworn there were more of you.', null, null, null, null, null, null, null, null, '12407', '1', '0', '0', 'delrissa PlayerDeath2');
INSERT INTO `script_texts` VALUES ('-1585019', 'Not really much of a group, anymore, is it?', null, null, null, null, null, null, null, null, '12409', '1', '0', '0', 'delrissa PlayerDeath3');
INSERT INTO `script_texts` VALUES ('-1585020', 'One is such a lonely number.', null, null, null, null, null, null, null, null, '12410', '1', '0', '0', 'delrissa PlayerDeath4');
INSERT INTO `script_texts` VALUES ('-1585021', 'It\'s been a kick, really.', null, null, null, null, null, null, null, null, '12411', '1', '0', '0', 'delrissa PlayerDeath5');
INSERT INTO `script_texts` VALUES ('-1585022', 'Not what I had... planned...', null, null, null, null, null, null, null, null, '12397', '1', '0', '0', 'delrissa SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1585023', 'Don\'t look so smug! I know what you\'re thinking, but Tempest Keep was merely a set back. Did you honestly believe I would trust the future to some blind, half-night elf mongrel? Oh no, he was merely an instrument, a stepping stone to a much larger plan! It has all led to this, and this time, you will not interfere!', null, null, null, null, null, null, null, null, '12413', '1', '0', '0', 'kaelthas MT SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1585024', 'Vengeance burns!', null, null, null, null, null, null, null, null, '12415', '1', '0', '0', 'kaelthas MT SAY_PHOENIX');
INSERT INTO `script_texts` VALUES ('-1585025', 'Felomin ashal!', null, null, null, null, null, null, null, null, '12417', '1', '0', '0', 'kaelthas MT SAY_FLAMESTRIKE');
INSERT INTO `script_texts` VALUES ('-1585026', 'I\'ll turn your world... upside... down...', null, null, null, null, null, null, null, null, '12418', '1', '0', '0', 'kaelthas MT SAY_GRAVITY_LAPSE');
INSERT INTO `script_texts` VALUES ('-1585027', 'Master... grant me strength.', null, null, null, null, null, null, null, null, '12419', '1', '0', '0', 'kaelthas MT SAY_TIRED');
INSERT INTO `script_texts` VALUES ('-1585028', 'Do not... get too comfortable.', null, null, null, null, null, null, null, null, '12420', '1', '0', '0', 'kaelthas MT SAY_RECAST_GRAVITY');
INSERT INTO `script_texts` VALUES ('-1585029', 'My demise accomplishes nothing! The Master will have you! You will drown in your own blood! This world shall burn! Aaaghh!', null, null, null, null, null, null, null, null, '12421', '1', '0', '0', 'kaelthas MT SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1599000', 'Soft, vulnerable shells. Brief, fragile lives. You can not escape the curse of flesh!', null, null, null, null, null, null, null, null, '14180', '1', '0', '0', 'sjonnir SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1599001', '???', null, null, null, null, null, null, null, null, '14181', '1', '0', '0', 'sjonnir SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1599002', 'Flesh is no match for iron!', null, null, null, null, null, null, null, null, '14182', '1', '0', '0', 'sjonnir SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1599003', 'Armies of iron will smother the world!', null, null, null, null, null, null, null, null, '14183', '1', '0', '0', 'sjonnir SAY_SLAY_3');
INSERT INTO `script_texts` VALUES ('-1599004', 'Loken will not rest, until the forge is retaken. You changed nothing!', null, null, null, null, null, null, null, null, '14184', '1', '0', '0', 'sjonnir SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1599005', 'You shouldn\'t have come...now you will die!', null, null, null, null, null, null, null, null, '13487', '1', '0', '0', 'maiden SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1599006', 'Why must it be this way?', null, null, null, null, null, null, null, null, '13488', '1', '0', '0', 'maiden SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1599007', 'You had it coming!', null, null, null, null, null, null, null, null, '13489', '1', '0', '0', 'maiden SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1599008', 'My burden grows heavier.', null, null, null, null, null, null, null, null, '13490', '1', '0', '0', 'maiden SAY_SLAY_3');
INSERT INTO `script_texts` VALUES ('-1599009', 'This is your own fault!', null, null, null, null, null, null, null, null, '13491', '1', '0', '0', 'maiden SAY_SLAY_4');
INSERT INTO `script_texts` VALUES ('-1599010', 'So much lost time... that you\'ll never get back!', null, null, null, null, null, null, null, null, '13492', '1', '0', '0', 'maiden SAY_STUN');
INSERT INTO `script_texts` VALUES ('-1599011', 'I hope you all rot! I never...wanted...this.', null, null, null, null, null, null, null, null, '13493', '1', '0', '0', 'maiden SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1599012', 'Now that\'s owning your supper!', null, null, null, null, null, null, null, null, '14244', '1', '0', '0', 'brann SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1599013', 'Press on, that\'s the way!', null, null, null, null, null, null, null, null, '14245', '1', '0', '0', 'brann SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1599014', 'Keep it up now. Plenty of death-dealing for everyone!', null, null, null, null, null, null, null, null, '14246', '1', '0', '0', 'brann SAY_KILL_3');
INSERT INTO `script_texts` VALUES ('-1599015', 'I\'m all kinds of busted up. Might not... make it...', null, null, null, null, null, null, null, null, '14257', '1', '0', '0', 'brann SAY_LOW_HEALTH');
INSERT INTO `script_texts` VALUES ('-1599016', 'Not yet, not... yet-', null, null, null, null, null, null, null, null, '14258', '1', '0', '0', 'brann SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1599017', 'I\'m doing everything I can!', null, null, null, null, null, null, null, null, '14260', '1', '0', '0', 'brann SAY_PLAYER_DEATH_1');
INSERT INTO `script_texts` VALUES ('-1599018', 'Light preserve you!', null, null, null, null, null, null, null, null, '14261', '1', '0', '0', 'brann SAY_PLAYER_DEATH_2');
INSERT INTO `script_texts` VALUES ('-1599019', 'I hope this is all worth it!', null, null, null, null, null, null, null, null, '14262', '1', '0', '0', 'brann SAY_PLAYER_DEATH_3');
INSERT INTO `script_texts` VALUES ('-1599020', 'Time to get some answers! Let\'s get this show on the road!', null, null, null, null, null, null, null, null, '14259', '1', '0', '0', 'brann SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES ('-1599021', 'Don\'t worry. Old Brann has got your back. Keep that metal monstrosity busy and I\'ll see if I can sweet talk this machine into helping you.', null, null, null, null, null, null, null, null, '14274', '1', '0', '0', 'brann SAY_SPAWN_DWARF');
INSERT INTO `script_texts` VALUES ('-1599022', 'This is a wee bit trickier that before... Oh, bloody--incomin\'!', null, null, null, null, null, null, null, null, '14275', '1', '0', '0', 'brann SAY_SPAWN_TROGG');
INSERT INTO `script_texts` VALUES ('-1599023', 'What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it...', null, null, null, null, null, null, null, null, '14276', '1', '0', '0', 'brann SAY_SPAWN_OOZE');
INSERT INTO `script_texts` VALUES ('-1599024', 'Ha, that did it. Help\'s a-coming. Take this you glow-eying brute!', null, null, null, null, null, null, null, null, '14277', '1', '0', '0', 'brann SAY_SPAWN_EARTHEN');
INSERT INTO `script_texts` VALUES ('-1599025', 'Take a moment and relish this with me! Soon all will be revealed! Okay then, let', null, null, null, null, null, null, null, null, '14247', '1', '0', '0', 'brann SAY_EVENT_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1599026', 'Now keep an eye out! I\'ll have this licked in two shakes of a--', null, null, null, null, null, null, null, null, '14248', '1', '0', '0', 'brann SAY_EVENT_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1599027', 'Warning! Life form pattern not recognized. Archival processing terminated. Continued interference will result in targeted response.', null, null, null, null, null, null, null, null, '13765', '1', '0', '0', 'brann SAY_EVENT_INTRO_3_ABED');
INSERT INTO `script_texts` VALUES ('-1599028', 'Oh, that doesn\'t sound good. We might have a complication or two...', null, null, null, null, null, null, null, null, '14249', '1', '0', '0', 'brann SAY_EVENT_A_1');
INSERT INTO `script_texts` VALUES ('-1599029', 'Security breach in progress. Analysis of historical archives transferred to lower priority queue. Countermeasures engaged.', null, null, null, null, null, null, null, null, '13756', '1', '0', '0', 'brann SAY_EVENT_A_2_KADD');
INSERT INTO `script_texts` VALUES ('-1599030', 'Ah, you want to play hardball, eh? That\'s just my game!', null, null, null, null, null, null, null, null, '14250', '1', '0', '0', 'brann SAY_EVENT_A_3');
INSERT INTO `script_texts` VALUES ('-1599031', 'Couple more minutes and I\'ll--', null, null, null, null, null, null, null, null, '14251', '1', '0', '0', 'brann SAY_EVENT_B_1');
INSERT INTO `script_texts` VALUES ('-1599032', 'Threat index threshold exceeded. Celestial archive aborted. Security level heightened.', null, null, null, null, null, null, null, null, '13761', '1', '0', '0', 'brann SAY_EVENT_B_2_MARN');
INSERT INTO `script_texts` VALUES ('-1599033', 'Heightened? What\'s the good news?', null, null, null, null, null, null, null, null, '14252', '1', '0', '0', 'brann SAY_EVENT_B_3');
INSERT INTO `script_texts` VALUES ('-1599034', 'So that was the problem? Now I\'m makin\' progress...', null, null, null, null, null, null, null, null, '14253', '1', '0', '0', 'brann SAY_EVENT_C_1');
INSERT INTO `script_texts` VALUES ('-1599035', 'Critical threat index. Void analysis diverted. Initiating sanitization protocol.', null, null, null, null, null, null, null, null, '13767', '1', '0', '0', 'brann SAY_EVENT_C_2_ABED');
INSERT INTO `script_texts` VALUES ('-1599036', 'Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it!', null, null, null, null, null, null, null, null, '14254', '1', '0', '0', 'brann SAY_EVENT_C_3');
INSERT INTO `script_texts` VALUES ('-1599037', 'Ha! The old magic fingers finally won through! Now let\'s get down to-', null, null, null, null, null, null, null, null, '14255', '1', '0', '0', 'brann SAY_EVENT_D_1');
INSERT INTO `script_texts` VALUES ('-1599038', 'Alert! Security fail safes deactivated. Beginning memory purge...', null, null, null, null, null, null, null, null, '13768', '1', '0', '0', 'brann SAY_EVENT_D_2_ABED');
INSERT INTO `script_texts` VALUES ('-1599039', 'Purge? No no no no no! Where did I-- Aha, this should do the trick...', null, null, null, null, null, null, null, null, '14256', '1', '0', '0', 'brann SAY_EVENT_D_3');
INSERT INTO `script_texts` VALUES ('-1599040', 'System online. Life form pattern recognized. Welcome Branbronzan. Query?', null, null, null, null, null, null, null, null, '13769', '1', '0', '0', 'brann SAY_EVENT_D_4_ABED');
INSERT INTO `script_texts` VALUES ('-1599041', 'Query? What do you think I', null, null, null, null, null, null, null, null, '14263', '1', '0', '0', 'brann SAY_EVENT_END_01');
INSERT INTO `script_texts` VALUES ('-1599042', 'Tell me how that dwarfs came to be! And start at the beginning!', null, null, null, null, null, null, null, null, '14264', '1', '0', '0', 'brann SAY_EVENT_END_02');
INSERT INTO `script_texts` VALUES ('-1599043', 'Accessing prehistoric data. Retrieved. In the beginning Earthen were created to-', null, null, null, null, null, null, null, null, '13770', '1', '0', '0', 'brann SAY_EVENT_END_03_ABED');
INSERT INTO `script_texts` VALUES ('-1599044', 'Right, right! I know that the Earthen were made of stone to shape the deep reaches of the world but what about the anomalies? Matrix non-stabilizing and whatnot.', null, null, null, null, null, null, null, null, '14265', '1', '0', '0', 'brann SAY_EVENT_END_04');
INSERT INTO `script_texts` VALUES ('-1599045', 'Accessing. In the early stages of its development cycle Azeroth suffered infection by parasitic, necrophotic symbiotes.', null, null, null, null, null, null, null, null, '13771', '1', '0', '0', 'brann SAY_EVENT_END_05_ABED');
INSERT INTO `script_texts` VALUES ('-1599046', 'Necro-what? Speak bloody common will ya?', null, null, null, null, null, null, null, null, '14266', '1', '0', '0', 'brann SAY_EVENT_END_06');
INSERT INTO `script_texts` VALUES ('-1599047', 'Designation: Old Gods. Old Gods rendered all systems, including Earthen defenseless in order to facilitate assimilation. This matrix destabilization has been termed the Curse of Flesh. Effects of destabilization increased over time.', null, null, null, null, null, null, null, null, '13772', '1', '0', '0', 'brann SAY_EVENT_END_07_ABED');
INSERT INTO `script_texts` VALUES ('-1599048', 'Old Gods eh? So they zapped the Earthen with this Curse of Flesh. And then what?', null, null, null, null, null, null, null, null, '14267', '1', '0', '0', 'brann SAY_EVENT_END_08');
INSERT INTO `script_texts` VALUES ('-1599049', 'Accessing. Creators arrived to extirpate symbiotic infection. Assessment revealed that Old God infestation had grown malignant. Excising parasites would result in loss of host.', null, null, null, null, null, null, null, null, '13757', '1', '0', '0', 'brann SAY_EVENT_END_09_KADD');
INSERT INTO `script_texts` VALUES ('-1599050', 'If they killed the Old Gods Azeroth would have been destroyed.', null, null, null, null, null, null, null, null, '14268', '1', '0', '0', 'brann SAY_EVENT_END_10');
INSERT INTO `script_texts` VALUES ('-1599051', 'Correct. Creators neutralized parasitic threat and contained it within the host. Forge of Wills and other systems were instituted to create new Earthen. Safeguards were implemented and protectors were appointed.', null, null, null, null, null, null, null, null, '13758', '1', '0', '0', 'brann SAY_EVENT_END_11_KADD');
INSERT INTO `script_texts` VALUES ('-1599052', 'What protectors?', null, null, null, null, null, null, null, null, '14269', '1', '0', '0', 'brann SAY_EVENT_END_12');
INSERT INTO `script_texts` VALUES ('-1599053', 'Designations: Aesir and Vanir or in common nomenclator Storm and Earth Giants. Sentinel Loken designated supreme. Dragon Aspects appointed to monitor evolution of Azeroth.', null, null, null, null, null, null, null, null, '13759', '1', '0', '0', 'brann SAY_EVENT_END_13_KADD');
INSERT INTO `script_texts` VALUES ('-1599054', 'Aesir and Vanir. Okay. So the Forge of Wills started to make new Earthen. But what happened to the old ones?', null, null, null, null, null, null, null, null, '14270', '1', '0', '0', 'brann SAY_EVENT_END_14');
INSERT INTO `script_texts` VALUES ('-1599055', 'Additional background is relevant to your query. Following global combat between-', null, null, null, null, null, null, null, null, '13762', '1', '0', '0', 'brann SAY_EVENT_END_15_MARN');
INSERT INTO `script_texts` VALUES ('-1599056', 'Hold everything! The Aesir and Vanir went to war? Why?', null, null, null, null, null, null, null, null, '14271', '1', '0', '0', 'brann SAY_EVENT_END_16');
INSERT INTO `script_texts` VALUES ('-1599057', 'Unknown. Data suggests that impetus for global combat originated with prime designate Loken who neutralized all remaining Aesir and Vanir affecting termination of conflict. Prime designate Loken then initiated stasis of several seed races including Earthen, Giant and Vrykul at designated holding facilities.', null, null, null, null, null, null, null, null, '13763', '1', '0', '0', 'brann SAY_EVENT_END_17_MARN');
INSERT INTO `script_texts` VALUES ('-1599058', 'This Loken sounds like a nasty character. Glad we don', null, null, null, null, null, null, null, null, '14272', '1', '0', '0', 'brann SAY_EVENT_END_18');
INSERT INTO `script_texts` VALUES ('-1599059', 'Essentially that is correct.', null, null, null, null, null, null, null, null, '13764', '1', '0', '0', 'brann SAY_EVENT_END_19_MARN');
INSERT INTO `script_texts` VALUES ('-1599060', 'Well now. That', null, null, null, null, null, null, null, null, '14273', '1', '0', '0', 'brann SAY_EVENT_END_20');
INSERT INTO `script_texts` VALUES ('-1599061', 'Acknowledged Branbronzan. Session terminated.', null, null, null, null, null, null, null, null, '13773', '1', '0', '0', 'brann SAY_EVENT_END_21_ABED');
INSERT INTO `script_texts` VALUES ('-1599062', 'Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul!', null, null, null, null, null, null, null, null, '14278', '1', '0', '0', 'brann SAY_VICTORY_SJONNIR_1');
INSERT INTO `script_texts` VALUES ('-1599063', 'I\'ll use the forge to make badtches o\' earthen to stand guard... But our greatest challenge still remains: find and stop Loken!', null, null, null, null, null, null, null, null, '14279', '1', '0', '0', 'brann SAY_VICTORY_SJONNIR_2');
INSERT INTO `script_texts` VALUES ('-1599064', 'I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'brann SAY_ENTRANCE_MEET');
INSERT INTO `script_texts` VALUES ('-1600000', 'More grunts, more glands, more FOOD!', null, null, null, null, null, null, null, null, '13181', '1', '0', '0', 'trollgore SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1600001', 'So hungry! Must feed!', null, null, null, null, null, null, null, null, '13182', '1', '0', '0', 'trollgore SAY_CONSUME');
INSERT INTO `script_texts` VALUES ('-1600002', 'Aaaargh...', null, null, null, null, null, null, null, null, '13183', '1', '0', '0', 'trollgore SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1600003', 'Corpse go boom!', null, null, null, null, null, null, null, null, '13184', '1', '0', '0', 'trollgore SAY_EXPLODE');
INSERT INTO `script_texts` VALUES ('-1600004', 'You have gone, me gonna eat you!', null, null, null, null, null, null, null, null, '13185', '1', '0', '0', 'trollgore SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1600005', 'The chill that you feel is the herald of your doom!', null, null, null, null, null, null, null, null, '13173', '1', '0', '0', 'novos SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1600006', 'Your efforts... are in vain.', null, null, null, null, null, null, null, null, '13174', '1', '0', '0', 'novos SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1600007', 'Such is the fate of all who oppose the Lich King.', null, null, null, null, null, null, null, null, '13175', '1', '0', '0', 'novos SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1600008', 'Bolster my defenses! Hurry, curse you!', null, null, null, null, null, null, null, null, '13176', '1', '0', '0', 'novos SAY_ADDS');
INSERT INTO `script_texts` VALUES ('-1600009', 'Surely you can see the futility of it all!', null, null, null, null, null, null, null, null, '13177', '1', '0', '0', 'novos SAY_BUBBLE_1');
INSERT INTO `script_texts` VALUES ('-1600010', 'Just give up and die already!', null, null, null, null, null, null, null, null, '13178', '1', '0', '0', 'novos SAY_BUBBLE_2');
INSERT INTO `script_texts` VALUES ('-1600011', '%s calls for assistance.', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'novos EMOTE_ASSISTANCE');
INSERT INTO `script_texts` VALUES ('-1600012', 'Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty!', null, null, null, null, null, null, null, null, '13862', '1', '0', '0', 'tharonja SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1600013', 'As Tharon\'ja predicted.', null, null, null, null, null, null, null, null, '13863', '1', '0', '0', 'tharonja SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1600014', 'As it was written.', null, null, null, null, null, null, null, null, '13864', '1', '0', '0', 'tharonja SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1600015', 'Your flesh serves Tharon\'ja now!', null, null, null, null, null, null, null, null, '13865', '1', '0', '0', 'tharonja SAY_FLESH_1');
INSERT INTO `script_texts` VALUES ('-1600016', 'Tharon\'ja has a use for your mortal shell!', null, null, null, null, null, null, null, null, '13866', '1', '0', '0', 'tharonja SAY_FLESH_2');
INSERT INTO `script_texts` VALUES ('-1600017', 'No! A taste... all too brief!', null, null, null, null, null, null, null, null, '13867', '1', '0', '0', 'tharonja SAY_SKELETON_1');
INSERT INTO `script_texts` VALUES ('-1600018', 'Tharon\'ja will have more!', null, null, null, null, null, null, null, null, '13868', '1', '0', '0', 'tharonja SAY_SKELETON_2');
INSERT INTO `script_texts` VALUES ('-1600019', 'Im... impossible! Tharon\'ja is eternal! Tharon\'ja... is...', null, null, null, null, null, null, null, null, '13869', '1', '0', '0', 'tharonja SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1601000', 'This kingdom belongs to the Scourge! Only the dead may enter.', null, null, null, null, null, null, null, null, '14075', '1', '0', '0', 'krikthir SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1601001', '???', null, null, null, null, null, null, null, null, '14076', '1', '0', '0', 'krikthir SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1601002', 'You were foolish to come.', null, null, null, null, null, null, null, null, '14077', '1', '0', '0', 'krikthir SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1601003', 'As Anub\'Arak commands!', null, null, null, null, null, null, null, null, '14078', '1', '0', '0', 'krikthir SAY_KILL_3');
INSERT INTO `script_texts` VALUES ('-1601004', 'We are besieged. Strike out and bring back their corpses!', null, null, null, null, null, null, null, null, '14079', '1', '0', '0', 'krikthir SAY_SEND_GROUP_1');
INSERT INTO `script_texts` VALUES ('-1601005', 'We must hold the gate. Attack! Tear them limb from limb!', null, null, null, null, null, null, null, null, '14080', '1', '0', '0', 'krikthir SAY_SEND_GROUP_2');
INSERT INTO `script_texts` VALUES ('-1601006', 'The gate must be protected at all costs. Rip them to shreds!', null, null, null, null, null, null, null, null, '14081', '1', '0', '0', 'krikthir SAY_SEND_GROUP_3');
INSERT INTO `script_texts` VALUES ('-1601007', 'Keep an eye on the tunnel. We must not let anyone through!', null, null, null, null, null, null, null, null, '14082', '1', '0', '0', 'krikthir SAY_PREFIGHT_1');
INSERT INTO `script_texts` VALUES ('-1601008', 'I hear footsteps. Be on your guard.', null, null, null, null, null, null, null, null, '14083', '1', '0', '0', 'krikthir SAY_PREFIGHT_2');
INSERT INTO `script_texts` VALUES ('-1601009', 'I sense the living. Be ready.', null, null, null, null, null, null, null, null, '14084', '1', '0', '0', 'krikthir SAY_PREFIGHT_3');
INSERT INTO `script_texts` VALUES ('-1601010', 'They hunger.', null, null, null, null, null, null, null, null, '14085', '1', '0', '0', 'krikthir SAY_SWARM_1');
INSERT INTO `script_texts` VALUES ('-1601011', 'Dinner time, my pets.', null, null, null, null, null, null, null, null, '14086', '1', '0', '0', 'krikthir SAY_SWARM_2');
INSERT INTO `script_texts` VALUES ('-1601012', 'I should be grateful. But I long ago lost the capacity.', null, null, null, null, null, null, null, null, '14087', '1', '0', '0', 'krikthir SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1601013', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1601014', 'I was king of this empire once, long ago. In life I stood as champion. In death I returned as conqueror. Now I protect the kingdom once more. Ironic, yes?', null, null, null, null, null, null, null, null, '14053', '1', '0', '0', 'anubarak SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1601015', 'Eternal agony awaits you!', null, null, null, null, null, null, null, null, '14054', '1', '0', '0', 'anubarak SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1601016', 'You shall experience my torment, first-hand!', null, null, null, null, null, null, null, null, '14055', '1', '0', '0', 'anubarak SAY_KILL_1');
INSERT INTO `script_texts` VALUES ('-1601017', 'You have made your choice.', null, null, null, null, null, null, null, null, '14056', '1', '0', '0', 'anubarak SAY_KILL_2');
INSERT INTO `script_texts` VALUES ('-1601018', 'Soon, the Master\'s voice will call to you.', null, null, null, null, null, null, null, null, '14057', '1', '0', '0', 'anubarak SAY_KILL_3');
INSERT INTO `script_texts` VALUES ('-1601019', 'Come forth, my brethren. Feast on their flesh!', null, null, null, null, null, null, null, null, '14058', '1', '0', '0', 'anubarak SAY_SUBMERGE_1');
INSERT INTO `script_texts` VALUES ('-1601020', 'Auum na-l ak-k-k-k, isshhh.', null, null, null, null, null, null, null, null, '14059', '1', '0', '0', 'anubarak SAY_SUBMERGE_2');
INSERT INTO `script_texts` VALUES ('-1601021', 'Your armor is useless against my locusts!', null, null, null, null, null, null, null, null, '14060', '1', '0', '0', 'anubarak SAY_LOCUST_1');
INSERT INTO `script_texts` VALUES ('-1601022', 'The pestilence upon you!', null, null, null, null, null, null, null, null, '14067', '1', '0', '0', 'anubarak SAY_LOCUST_2');
INSERT INTO `script_texts` VALUES ('-1601023', 'Uunak-hissss tik-k-k-k-k!', null, null, null, null, null, null, null, null, '14068', '1', '0', '0', 'anubarak SAY_LOCUST_3');
INSERT INTO `script_texts` VALUES ('-1601024', 'Ahhh... RAAAAAGH! Never thought... I would be free of him...', null, null, null, null, null, null, null, null, '14069', '1', '0', '0', 'anubarak SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1602000', 'I am the greatest of my father\'s sons! Your end has come!', null, null, null, null, null, null, null, null, '14149', '1', '0', '0', 'bjarngrim SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1602001', 'So ends your curse!', null, null, null, null, null, null, null, null, '14153', '1', '0', '0', 'bjarngrim SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1602002', 'Flesh... is... weak!', null, null, null, null, null, null, null, null, '14154', '1', '0', '0', 'bjarngrim SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1602003', '...', null, null, null, null, null, null, null, null, '14155', '1', '0', '0', 'bjarngrim SAY_SLAY_3');
INSERT INTO `script_texts` VALUES ('-1602004', 'How can it be...? Flesh is not... stronger!', null, null, null, null, null, null, null, null, '14156', '1', '0', '0', 'bjarngrim SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1602005', 'Defend yourself, for all the good it will do!', null, null, null, null, null, null, null, null, '14151', '1', '0', '0', 'bjarngrim SAY_BATTLE_STANCE');
INSERT INTO `script_texts` VALUES ('-1602006', '%s switches to Battle Stance!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'bjarngrim EMOTE_BATTLE_STANCE');
INSERT INTO `script_texts` VALUES ('-1602007', 'GRAAAAAH! Behold the fury of iron and steel!', null, null, null, null, null, null, null, null, '14152', '1', '0', '0', 'bjarngrim SAY_BERSEKER_STANCE');
INSERT INTO `script_texts` VALUES ('-1602008', '%s switches to Berserker Stance!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'bjarngrim EMOTE_BERSEKER_STANCE');
INSERT INTO `script_texts` VALUES ('-1602009', 'Give me your worst!', null, null, null, null, null, null, null, null, '14150', '1', '0', '0', 'bjarngrim SAY_DEFENSIVE_STANCE');
