INSERT INTO `script_texts` VALUES ('-1619009', 'Your heartbeat is music to my ears.', null, null, null, null, null, null, null, null, '14361', '1', '0', '0', 'taldaram SAY_VANISH_1');
INSERT INTO `script_texts` VALUES ('-1619010', 'I am nowhere. I am everywhere. I am the watcher, unseen.', null, null, null, null, null, null, null, null, '14362', '1', '0', '0', 'taldaram SAY_VANISH_2');
INSERT INTO `script_texts` VALUES ('-1619011', 'So appetizing.', null, null, null, null, null, null, null, null, '14363', '1', '0', '0', 'taldaram SAY_FEED_1');
INSERT INTO `script_texts` VALUES ('-1619012', 'Fresh, warm blood. It has been too long.', null, null, null, null, null, null, null, null, '14364', '1', '0', '0', 'taldaram SAY_FEED_2');
INSERT INTO `script_texts` VALUES ('-1619013', 'Bin-dor\'el', null, null, null, null, null, null, null, null, '14365', '1', '0', '0', 'taldaram SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1619014', 'I will drink no blood before it\'s time.', null, null, null, null, null, null, null, null, '14366', '1', '0', '0', 'taldaram SAY_SLAY_2');
