INSERT INTO `script_texts` VALUES ('-1619017', 'These are sacred halls! Your intrusion will be met with death.', null, null, null, null, null, null, null, null, '14343', '1', '0', '0', 'jedoga SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1619018', 'Who among you is devoted?', null, null, null, null, null, null, null, null, '14344', '1', '0', '0', 'jedoga SAY_CALL_SACRIFICE_1');
INSERT INTO `script_texts` VALUES ('-1619019', 'You there! Step forward!', null, null, null, null, null, null, null, null, '14345', '1', '0', '0', 'jedoga SAY_CALL_SACRIFICE_2');
INSERT INTO `script_texts` VALUES ('-1619020', 'Yogg-Saron, grant me your power!', null, null, null, null, null, null, null, null, '14346', '1', '0', '0', 'jedoga SAY_SACRIFICE_1');
INSERT INTO `script_texts` VALUES ('-1619021', 'Master, a gift for you!', null, null, null, null, null, null, null, null, '14347', '1', '0', '0', 'jedoga SAY_SACRIFICE_2');
INSERT INTO `script_texts` VALUES ('-1619022', 'Glory to Yogg-Saron!', null, null, null, null, null, null, null, null, '14348', '1', '0', '0', 'jedoga SAY_SLAY_1');
INSERT INTO `script_texts` VALUES ('-1619023', 'You are unworthy!', null, null, null, null, null, null, null, null, '14349', '1', '0', '0', 'jedoga SAY_SLAY_2');
INSERT INTO `script_texts` VALUES ('-1619024', 'Get up! You haven\'t suffered enough.', null, null, null, null, null, null, null, null, '14350', '1', '0', '0', 'jedoga SAY_SLAY_3');
INSERT INTO `script_texts` VALUES ('-1619025', 'Do not expect your sacrilege... to go unpunished.', null, null, null, null, null, null, null, null, '14351', '1', '0', '0', 'jedoga SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1619026', 'The elements themselves will rise up against the civilized world! Only the faithful will be spared!', null, null, null, null, null, null, null, null, '14352', '1', '0', '0', 'jedoga SAY_PREACHING_1');
INSERT INTO `script_texts` VALUES ('-1619027', 'Immortality can be yours. But only if you pledge yourself fully to Yogg-Saron!', null, null, null, null, null, null, null, null, '14353', '1', '0', '0', 'jedoga SAY_PREACHING_2');
INSERT INTO `script_texts` VALUES ('-1619028', 'Here on the very borders of his domain. You will experience powers you would never have imagined! ', null, null, null, null, null, null, null, null, '14354', '1', '0', '0', 'jedoga SAY_PREACHING_3');
INSERT INTO `script_texts` VALUES ('-1619029', 'You have traveled long and risked much to be here. Your devotion shall be rewarded.', null, null, null, null, null, null, null, null, '14355', '1', '0', '0', 'jedoga SAY_PREACHING_4');
INSERT INTO `script_texts` VALUES ('-1619030', 'The faithful shall be exalted! But there is more work to be done. We will press on until all of Azeroth lies beneath his shadow!', null, null, null, null, null, null, null, null, '14356', '1', '0', '0', 'jedoga SAY_PREACHING_5');
